INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(1, 'Quo necessitatibus ut qui voluptate minus et et necessitatibus.', 'suscipit-aliquam-dolor-doloribus-ipsum-eius-aliquid', 'Reprehenderit blanditiis consequuntur delectus magni sed rerum.', 'Saepe aliquid mollitia rerum ipsam recusandae nesciunt soluta. Rerum explicabo est et corrupti rerum non. Qui explicabo assumenda in illo qui. Voluptas sit exercitationem possimus id quas.', 11, 13, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:29'),
(2, 'Et ut illo provident sit odio inventore.', 'totam-sit-deserunt-enim', 'Ut voluptatem voluptatem doloribus enim.', 'Provident atque delectus animi et non non. Dolorum quos voluptatibus ducimus repellat officiis ut quo ea. Repudiandae sit voluptas vitae vel id aperiam enim qui. Atque est est doloribus minima voluptatem non et.', 11, 14, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:34'),
(3, 'Enim sunt tenetur sequi enim non consectetur ad.', 'cupiditate-vel-quis-est-pariatur-omnis-quibusdam-nulla', 'Ut repellat commodi doloribus quia in culpa quae.', 'Ipsa suscipit sunt sunt adipisci maiores autem magni. Repellendus non iusto qui ea vel. Dolores modi molestiae deleniti ut qui dolorem. Quibusdam beatae dolorem laborum et vel incidunt.', 11, 15, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:29'),
(4, 'Inventore ea dolorem nostrum maxime ullam.', 'molestiae-officiis-voluptas-quam-architecto', 'Eos nesciunt facere aut rem suscipit voluptatibus.', 'Doloremque reiciendis expedita quia sit rerum odit. Dolores et architecto quibusdam et omnis aut. Blanditiis reiciendis impedit corrupti incidunt placeat.', 11, 16, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:33'),
(5, 'Sit autem autem tenetur aliquid.', 'maiores-modi-sunt-quibusdam-odit-laudantium-voluptatum', 'Quia quod est enim sit est.', 'Quia odio voluptatem aperiam repudiandae dolor. Provident laborum modi fugiat sed. Aut suscipit tempore eaque harum quia qui repudiandae.', 11, 17, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:22'),
(6, 'Iste ut molestias assumenda sunt delectus vel ducimus.', 'libero-est-quod-aut-et-quo-voluptatem-sunt', 'Et blanditiis provident optio quo ducimus ut.', 'Et vel magnam veniam repudiandae. Libero non saepe quod molestiae. Non iste eveniet beatae et quidem qui.', 11, 18, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:27'),
(7, 'Animi tempora consequuntur ex quae eos non perspiciatis.', 'libero-iste-quia-nam', 'Velit corrupti veniam omnis harum omnis assumenda quidem consectetur.', 'Placeat iure molestiae quibusdam ratione quod nesciunt odio. Earum asperiores provident aut sapiente qui. Et aut eligendi ducimus quae sit dolores est eligendi. Eaque voluptates placeat voluptates vero quo ut. Inventore aut nam et nemo.', 11, 19, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:34'),
(8, 'Labore accusamus nemo quam ab ab provident voluptatem.', 'ut-vitae-voluptatum-expedita', 'Labore et aliquam nisi ullam commodi illum.', 'Occaecati corporis laboriosam aut fugiat. Voluptas facilis totam nemo quisquam est illo et. Et soluta itaque sint consequatur dignissimos.', 11, 20, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:40'),
(9, 'Saepe quo voluptas minima iste perferendis debitis occaecati.', 'at-voluptatem-voluptas-vero-ipsa-non-sed', 'Dignissimos ipsa vel cumque aperiam cupiditate illo rerum.', 'Atque porro at eveniet ut perferendis quam totam quia. Dolorem modi esse dolor earum maxime. Sed adipisci praesentium cupiditate. Facere aut error excepturi nisi ratione.', 11, 21, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:33'),
(10, 'Enim pariatur nesciunt sequi delectus minus molestias.', 'dolore-assumenda-delectus-qui-velit', 'Quasi libero nisi ipsa saepe reprehenderit voluptas nam.', 'Ducimus quis delectus accusantium quidem voluptas cum. Facere est rerum aspernatur cumque dolore sequi. In et voluptatem tempora quos. Distinctio illum ipsum consectetur voluptas rerum quidem voluptatem. At perferendis quisquam aperiam id.', 11, 22, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:33'),
(11, 'Eos blanditiis dignissimos at est.', 'dolorem-non-molestiae-sed-est-dolor-et-harum-aut', 'Nobis sunt ut modi ea.', 'Dolorem sint cum voluptas quae cupiditate quae aliquam. Excepturi minus provident ut. Minus explicabo voluptatem deleniti rerum aspernatur voluptatibus.', 11, 23, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:36'),
(12, 'Sed eius incidunt quis vitae quisquam sit.', 'quia-perspiciatis-est-nihil', 'Molestiae maiores vel totam dignissimos ut quia quaerat.', 'Iusto enim quia et quo et enim ducimus. Quaerat voluptatum eum dolor consectetur. Rerum reiciendis vero dicta est vel perferendis facilis quisquam.', 11, 24, 0, 'published', '2022-03-13 07:42:42', '2022-03-13 07:44:19');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(13, '25 Best Things To Do In Budapest (Hungary)', '25-Best-Things-To-Do-In-Budapest', 'Hungary’s capital city Budapest is actually made up of 3 unified cities, with Buda and Óbuda on the west bank of the Danube and Pest on the east bank.', '<p><span style=\"color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Hungary&rsquo;s capital city Budapest is actually made up of 3 unified cities, with Buda and &Oacute;buda on the west bank of the Danube and Pest on the east bank. Much of the city has been granted UNESCO World Heritage Site status, and many visitors consider the city to be amongst the most beautiful cities in Europe.</span></p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The city successfully mixes its fascinating history with a brilliant, laid-back contemporary artistic style. There are a wide range of different things for visitors to see and do, from museums to thermal spas, so everyone should be able to find something that tickles their fancy.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Lets explore the&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">best things to do in Budapest</span>:</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">1. Parliament Building</h2>\r\n<figure id=\"attachment_6658\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6658\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6658 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-Parliament-Building-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-Parliament-Building-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-Parliament-Building-Budapest-300x200.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-Parliament-Building-Budapest-768x512.jpg 768w\" alt=\"Hungarian Parliament Building\" width=\"720\" height=\"480\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/theodevil/7159125589/in/photolist-bUCoBP-7R4RKF-NHCWx-6YNcRq-79cqif-6wHKBh-AqvEB6-t1UTyJ-s66cx3-92i3AZ-m53Bs-282isz-bPH91-GKbNF-9jB9pJ-qKP6HP-ApTsQ8-nefwGG-ApTsSc-7BZ2yS-w9Y7u-4FHYJ4-5v8dWG-B3RE6K-dY9Vm1-7DbX29-ApTxMU-Q3Q7E-dwDb5A-6mKG47-2ZpSvU-92i3Ek-64DMiB-7LPT21-iUbZAr-B1yczA-ApTwuJ-B2ECz3-dKJAKe-ApwvRi-8fs4jy-7hEcd-8yH6ZU-bkGhk9-4mAXpR-cwyxDG-cTw9q9-dngPU-A6XM3B-6YJd9Z\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6658\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6658\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Hungarian Parliament Building</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Hungarian Parliament Building, which was designed and built in the Gothic Revival style, is one of the largest buildings in Hungary, and is home to hundreds of parliamentary offices. Although the impressive building looks fantastic from every angle, to see the whole building in its full glory, it is worth viewing it from the other side of the Danube.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Tours of certain areas of the building are available daily, and run in different languages. You will need identification to get in, and your bag may be searched on entry. There is a&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">top selling guided tour</span>&nbsp;which you can book&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.getyourguide.com/budapest-l29/budapest-parliament-tour-t71462/?partner_id=AS1RATG&amp;cmp=budapestparliament&amp;deeplink_id=1289ca3e-b3f6-5c9d-9aea-b67cd143358a\" target=\"_blank\" rel=\"nofollow noopener noreferrer\" data-gyg-scraped=\"1647160451924\">here</a>.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">2. Gell&eacute;rt Baths</h2>\r\n<figure id=\"attachment_6656\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 674px;\" aria-describedby=\"caption-attachment-6656\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6656 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Gell%C3%A9rt-Baths-Budapest.jpg\" sizes=\"(max-width: 684px) 100vw, 684px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Gell%C3%A9rt-Baths-Budapest.jpg 684w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Gell%C3%A9rt-Baths-Budapest-200x300.jpg 200w\" alt=\"Gell&eacute;rt Baths, Budapest\" width=\"684\" height=\"1024\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/braubach/2128734784/in/photolist-4f7jFS-4qHgBH-4qHgzt-4jz6QZ-9CwG4j-nGv3XW-9sVkkx-6iTW1A-9sYo59-2jQAF6-EySd7-9sVmz2-9sYotd-iwoULM-u9sU4t-6iPL28-digbej-2Jf9SK-9YVugP-9sYqpm-ozs5XJ-dAx34i-49GNc7-7zs8rH-9sYmhm-9sVpEP-61aHY2-A6XLGg-9sVr9t-GFxVt-ozooyv-9sYpfA-8wH2eP-9sVoov-9CwFQj-bk8CbQ-9YVumz-9sYncq-dAx2DR-9sVyiR-dCGTjK-pwhQEm-2PE93K-dAx2S8-9sVrut-Er79w-9CtM1M-9sYr9m-8gruRK-9sVmb4\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6656\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6656\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Gell&eacute;rt Baths, Budapest</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">One of the grandest spas in the city is the Gellert Bath and Spa centre, which includes an open-air pool (which turns into a wave pool), an effervescent swimming pool, a Finnish sauna, and a range of other saunas and plunge pools.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Massages and other spa treatments are also available at an extra fee. The complex was originally built between 1912 and 1918 in an Art Nouveau style, but it sustained serious damage during World War II. The whole spa was extensively renovated in 2008 to bring the baths back to their former glory. The baths are open all week for mixed bathing.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">3. Heroes&rsquo; Square</h2>\r\n<figure id=\"attachment_6659\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6659\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6659 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Heroes%E2%80%99-Square-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Heroes%E2%80%99-Square-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Heroes%E2%80%99-Square-Budapest-300x200.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Heroes%E2%80%99-Square-Budapest-768x512.jpg 768w\" alt=\"Heroes&rsquo; Square\" width=\"720\" height=\"480\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/touncertaintyandbeyond/4964640624/in/photolist-8yH5G9-ipCHGX-xAsVj-uDTTqf-zqV3M-Co8yU-bmp6g8-82N4kw-nRQW2j-HFBNB-47YEAy-72AfjC-4EKoBd-ekf2tz-iTdYqA-5Mf5H6-nZDtfu-9UMyhX-9UMybX-aZV2Z4-mDEkPp-4wfLtT-PoRbK-9vXuNX-nAko4z-5toTU9-7D8crX-349Mff-4pUg4K-47YEew-4xwxA8-nZLgox-e8xtXz-aXasNR-bowb5C-4VMZMz-4SsdFC-bBr5Kx-4pU39p-7Dc1pE-5MWqb7-349NF5-afPnvT-nTv8QD-2roVvP-YS1mC-i9gsnQ-nDnkc-4EF8zF-4pU43Z\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6659\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6659\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Heroes&rsquo; Square</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Heroes&rsquo; Square (Hos&ouml;k tere), which marks the end of Andr&aacute;ssy Avenue is home to an iconic monument which features depictions of the Seven Chieftains of the Magyars, who are believed to have led the Hungarian people from central Asia to the Carpathian basin.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Atop the central pillar is the Archangel Gabriel, who is holding the Hungarian crown. At either side of the central column are two matching colonnades, which depict a variety of other historical Hungarian figures. The impressive buildings at either side of the square are art galleries. Take care when crossing to the statue, because traffic around the monument can be erratic.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">A great way to explore the city:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.getyourguide.com/budapest-l29/superior-segway-city-tour-t29097/?partner_id=AS1RATG&amp;cmp=budapestsegway&amp;deeplink_id=099650fe-1e38-5470-9083-56210881d540\" target=\"_blank\" rel=\"nofollow noopener noreferrer\" data-gyg-scraped=\"1647160451924\">Budapest Segway Tour</a></p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">4. Margaret Island</h2>\r\n<figure id=\"attachment_6660\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6660\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6660 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Musical-Fountain-Margaret-Island-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Musical-Fountain-Margaret-Island-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Musical-Fountain-Margaret-Island-Budapest-300x225.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Musical-Fountain-Margaret-Island-Budapest-768x576.jpg 768w\" alt=\"Musical Fountain, Margaret Island\" width=\"720\" height=\"540\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/60584010@N00/22594531712/in/photolist-AqAT6A-9TDYVn-e9zVHw-8w5DPS-edKTST-ArPX9r-8w5Crd-8w2mQP-cDiBLJ-AqB6yb-dbQCX5-AaUW65-dZgTx9-8w5GVf-8w2rXr-4vLCpS-9TE3rR-6sCffj-7ZxdxT-8w2rTz-v5hmUy-EAkX5-8w2AWe-8w5E85-9TE7E8-fDn3Wv-8w2r7Z-8w2CHV-8w5G8A-8w2B2z-8w5o9b-8w5s9f-9TDVaB-8w2BHD-8w2oMZ-azyggY-fDDCN1-8w5Loq-975SMd-9RLYCr-8w5Bp9-hngf88-fgxYKg-8w2mor-9TGBfQ-e9J9Bu-dsgdSs-8w5HMs-8w2gvM-8w5s1E\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6660\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6660\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Musical Fountain, Margaret Island</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Margaret Island is a 2.5km long island which sits in the middle of the Danube, which is covered in parkland and recreational facilities.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">There are a number of companies which rent pedal carts, golf carts, and other self-powered vehicles, so that you can explore the island properly.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">A rubber-coated 5.5 km running track encircles the island, and is a popular jogging spot for runners who want to get away from the hustle and bustle of the city. One of the most famous features of the island is the &ldquo;music fountain&rdquo;, from which water regularly &ldquo;dances&rdquo; in time to classical music.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Other notable features on the island include medieval ruins and small aviary which primarily caters for injured water birds and wildfowl.</p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">5. Danube Promenade</h2>\r\n<figure id=\"attachment_6661\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6661\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6661 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Shoes-on-the-Danube-Promenade.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Shoes-on-the-Danube-Promenade.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Shoes-on-the-Danube-Promenade-300x200.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Shoes-on-the-Danube-Promenade-768x512.jpg 768w\" alt=\"Shoes on the Danube Promenade\" width=\"720\" height=\"480\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/samsamcardiff/6300722736/in/photolist-aALRmC-98RomQ-2MQVap-3456it-3456PK-enjito-6HtfYr-dACUB3-2GRiuZ-eMN5NL-6HxmDE-eQXYUZ-91gTsh-eQXY4H-dACV1f-bYj5hd-efwAEd-bYj51w-aMWFmi-4QMKNQ-9cgJng-2MViSW-8iLwDi-bVPPe5-6cugYA-pnPeRi-6cq8nR-66gURD-4zSEuC-cAHHTS-eQXXG8-dAxnyp-hRUmH-emTEhP-efqDkt-9mdzrt-6YNbRm-dACVpo-2MQVug-349Fh5-52aAws-d3y8LS-cAHGYb-2GVzdj-cQPAiQ-emTCBK-etCMo7-niR2kp-dACT8W-6YJ9NX\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6661\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6661\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Shoes on the Danube Promenade</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This stretch of the Danube walkway goes from the Elizabeth Bridge to the Chain Bridge, and is perfect for those who want a short, but interesting walk. Promenading along the Danube is a great way to see many of the most famous sights in the capital.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Looking over towards to Buda side of the river, you will see the Buda Castle, the Liberty Statue on Gellert Hill and the Fisherman&rsquo;s Bastion. On the Promenade side of the river you can enjoy restaurants, cafes, Szechenyi Istvan Square and a range of different sculptures, including the Little Princess.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Danube is also perfect for a river cruise, there&rsquo;s a fun dinner cruise and folk show (with gypsy music) which you can&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.getyourguide.com/budapest-l29/budapest-dinner-cruise-with-folk-show-t57378/?partner_id=AS1RATG&amp;cmp=budapestdinnercruisefolkshow&amp;deeplink_id=dcce6646-8589-518d-976c-1bdacab88ef1\" target=\"_blank\" rel=\"nofollow noopener noreferrer\" data-gyg-scraped=\"1647160451924\">book here</a>.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">6. House of Terror</h2>\r\n<figure id=\"attachment_6662\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6662\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6662 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/House-of-Terror-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/House-of-Terror-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/House-of-Terror-Budapest-300x225.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/House-of-Terror-Budapest-768x576.jpg 768w\" alt=\"House of Terror\" width=\"720\" height=\"540\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/pricey/936963907/in/photolist-2qNbJ8-9sZTqU-9sWRZx-ac5tZg-eTed6Q-9sZT8E-EAnVy-9sWYYe-9sZW69-8dTDxm-9sZV9u-9sWVz4-9sZXAw-6ndc6z-9sWYq6-9sZSks-o95akz-4zNoKM-7nUJ4d-8dQnUc-7bvm6o-9sZUwL-wCDiB-7R85m7-5NQW4-EAnW2-ozCfPW-eTe1Dy-59ujUz-oiaqEV-51FaBR-9sWUdB-o95ast-bCFU6i-9sZTNq-ac8m73-9bDyMe-cA4kRy-9sWWuH-8yE1Nn-CuhGV-CuhDA-CuhAX-dWWzRp-dX3g2b-CuhGc-4BLyEn-bpLXJU-4nX6wS-9BNrwr\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6662\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6662\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">House of Terror</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The House of Terror holds exhibitions about the successive Fascist and Communist regimes which ruled Hungary during the 20th Century. The building itself was the former headquarters of the Fascist Arrow Cross party, and the building was subsequently used as a prison and torture venue by the State Security services of Hungary.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">There is the opportunity to tour some of the prison area in the basement. The exhibition includes information about both regimes, as well as testimonials from some of the victims. As well as exhibitions about the fascist and communist &ldquo;security services&rdquo;, the building often houses other temporary exhibitions.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">7. St. Stephen&rsquo;s Basilica</h2>\r\n<figure id=\"attachment_6663\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6663\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6663 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/St.-Stephen%E2%80%99s-Basilica-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/St.-Stephen%E2%80%99s-Basilica-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/St.-Stephen%E2%80%99s-Basilica-Budapest-300x199.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/St.-Stephen%E2%80%99s-Basilica-Budapest-768x510.jpg 768w\" alt=\"St. Stephen&rsquo;s Basilica\" width=\"720\" height=\"478\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/thecameragirls/4840380934/in/photolist-8nJdBG-B4QsSp-B1ycSj-ApTsm2-9jAfYC-9jxb1p-A6PMBQ-3jRAvg-ApTy3y-9jAhom-azqL2c-aztr1G-Ascq64-AswnUZ-4pUeR4-azqKT8-9jAgaG-4pUdTV-ruoXJ7-A6XNAg-8tWZMM-8G8LQA-63kiUX-9jxaUn-aztqVh-3jRyyZ-B1ycQL-A6PMvN-A6XNrt-9jx9Ne-9jxsYe-B4QsM4-bFCP4r-9jAgqL-8foULP-541d9V-w144ov-fU4gvU-bAEJKV-azqLbi-9jAdY9-fm6vz1-A6XNqr-A6XNuK-bnKJRY-9jx9cP-5HKPGQ-7Dikiu-7DYFr8-bnKHB9\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6663\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6663\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">St. Stephen&rsquo;s Basilica</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This basilica is one of the most important religious buildings in Hungary, and visitors to the reliquary can see the (reported) right hand of Stephen, first King of Hungary. As this is a holy site, visitors who plan on entering the church are asked to keep their knees and shoulders covered.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Those with a head for heights can travel up to the base of the dome and look out over the city. On a clear day, this is a great vantage point from which to survey Budapest from the air. Classical music concerts and organ concerts regularly take place inside the Basilica, and sometime spill out into the square outside. Fun fact: You can book an Organ Concert in the Basilica&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.getyourguide.com/budapest-l29/organ-concert-in-st-stephen-s-basilica-t3897/?partner_id=AS1RATG&amp;cmp=budapestorganconcertbasilica&amp;deeplink_id=b791a4d2-406c-5aa4-a0fe-0555fc8c7477\" target=\"_blank\" rel=\"nofollow noopener noreferrer\" data-gyg-scraped=\"1647160451924\">right here</a>.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">8. Hungarian State Opera House</h2>\r\n<figure id=\"attachment_6664\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6664\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6664 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-State-Opera-House.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-State-Opera-House.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-State-Opera-House-300x199.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-State-Opera-House-768x509.jpg 768w\" alt=\"Hungarian State Opera House\" width=\"720\" height=\"477\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/ajh1963/7534192760/in/photolist-ctLH2b-7VuQH6-7VvmJR-edRywq-9Lys9M-cnFyR5-JGjDV-7o3QGS-9chcSu-4yWYmT-9LyGfc-nRQvMY-7nYVD2-ozEmwX-jp3Dk9-bTg7Hv-7o3Qxy-eztdgw-aiJ5vh-ozEmiF-7Vy6yq-6vEwAh-7nYVvM-5VDkmA-b53vrv-qUrjYN-8w2JhH-7nYVN6-7VvogP-7VyyzY-4z2eiG-diyPwT-7o3QAS-dbpcpY-fw4Dmq-jp3C8j-8grLFg-52MoY2-fvPnHK-7o3QRj-7nYVtM-7VvkGV-ezrUaF-d4sB51-jp4WG3-jp1ADT-bYj72d-cdP8Q-8gv3id-8w5LxE\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6664\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6664\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Hungarian State Opera House</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This Neo-Renaissance building was first opened in 1884, following a commission from Emperor Franz Joseph. Outside of the building, you can see statues to Ferenc Erkel (composer of the Hungarian National Anthem) and Ferenc Liszt (Hungarian composer).</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The 1200 seat auditorium is considered to be one of the best in the world for operatic performances, and it is well worth it to buy a ticket to a show.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Ticket prices start from as low as 500ft. If you cannot find time to see a show, guided tours of the Opera House are available during the day, although these usually need to be booked in advance.</p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">9. Fisherman&rsquo;s Bastion</h2>\r\n<figure id=\"attachment_6665\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6665\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6665 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Fisherman%E2%80%99s-Bastion-Budapest-scaled.jpg\" alt=\"Fisherman&rsquo;s Bastion\" width=\"720\" height=\"480\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"http://www.travelmoodz.com/en/destination/hungary/pest/budapest\" target=\"_blank\" rel=\"nofollow noopener\">travelmoodz</a></p>\r\n<span id=\"isc_attachment_6665\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6665\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Fisherman&rsquo;s Bastion</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Although the Fisherman&rsquo;s Bastion looks like a medieval monument, it was actually built in the early 20th century in a neo-Gothic style, specifically to act as a panoramic viewing platform across the Danube, Margaret Island and Pest.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">It is named after the Guild of Fishermen, which was responsible for defending that stretch of the city walls during the Middle Ages.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The seven towers of the Bastion represent the seven Magyar tribes that helped to settle the Magyar people in the Carpathian Basin. Come at sunset to see a particularly beautiful view of the city.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">10. Invisible Exhibition</h2>\r\n<figure id=\"attachment_6666\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6666\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6666 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Invisible-Exhibition-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Invisible-Exhibition-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Invisible-Exhibition-Budapest-300x169.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Invisible-Exhibition-Budapest-768x432.jpg 768w\" alt=\"Invisible Exhibition\" width=\"720\" height=\"405\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"http://www.lathatatlan.hu/en/approaching-step-by-step/\" target=\"_blank\" rel=\"nofollow noopener\">lathatatlan</a></p>\r\n<span id=\"isc_attachment_6666\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6666\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Invisible Exhibition</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Invisible Exhibition aims to give visitors the chance to experience what life is like for people who are completely blind. A registered blind guide will take you on a tour through various different artificially created environments (garden, supermarket, bar etc) which are in completely dark rooms.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">On arrival, you will be asked to turn off any potential light sources, such as mobile phones or digital watches, so that there will no light at all in the rooms. After the exhibition, you can enjoy dinner in the dark, served by blind waiters, who will help you to find your way around your dinner plate.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">You may also like:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.getyourguide.com/budapest-l29/private-art-nouveau-budapest-tour-t66304/?partner_id=AS1RATG&amp;cmp=budapestartnouveau&amp;deeplink_id=9608d2bb-e619-5c85-b8f0-67b1727ea239\" target=\"_blank\" rel=\"nofollow noopener noreferrer\" data-gyg-scraped=\"1647160451924\">Private Art Nouveau Budapest Tour</a></p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">11. Faust Wine Cellars</h2>\r\n<figure id=\"attachment_17002\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 760px;\" aria-describedby=\"caption-attachment-17002\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-17002 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Faust-Wine-Cellar-1024x768.jpg\" sizes=\"(max-width: 770px) 100vw, 770px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Faust-Wine-Cellar-scaled.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Faust-Wine-Cellar-300x225.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Faust-Wine-Cellar-768x576.jpg 768w\" alt=\"Faust Wine Cellar\" width=\"770\" height=\"578\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.facebook.com/FaustWineCellar\" target=\"_blank\" rel=\"nofollow noopener\">facebook</a></p>\r\n<span id=\"isc_attachment_17002\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-17002\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Faust Wine Cellar</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This historic wine cellar, which is located underneath the Buda Castle is the perfect place to sample wines from some of the 22 Hungarian wine regions. You will also have the opportunity to sample traditional Hungarian fruit palinka. A sommelier will give you tasting notes during your visit, so that you can get the most out of each wine that you try.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">There are a range of different tasting programmes available, depending on how much time you have and how much you want to spend. Due to the size of the cellars, you are advised to book ahead.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">12. Memento Park</h2>\r\n<figure id=\"attachment_6668\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6668\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6668 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Memento-Park-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Memento-Park-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Memento-Park-Budapest-300x225.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Memento-Park-Budapest-768x576.jpg 768w\" alt=\"Memento Park\" width=\"720\" height=\"540\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/morrissey/8258687656/in/photolist-dzMW7u-ac8ziJ-ac8zvu-aJjRqK-r3nKdp-ac8zSw-q6ncbs-aJjtLi-93gatX-aE3LS1-ac5HYt-93gawD-qKWXRZ-au2Aku-66Jv8m-93gamM-dwxGhr-8RjjmS-aJjNji-dwDcCu-3hVhgP-dwxGdc-93gaK2-3hZYbE-8SCQo1-ac5JrV-dwDcsb-5wgJXq-dwxGag-aJjqdk-9SNiAA-3hVC9x-dxpGsd-btCeTy-93jhBG-93jhGC-dwxGep-fKnYcK-6VnGRL-aJjxRx-aJjKmt-92ADsY-93gacV-fNYzLY-jv9NVc-aJjQqH-aJjTKx-93gazv-6ViMD8-dwDcA9\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6668\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6668\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Memento Park</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This wonderful sculpture park is now home to some of the many Communist monuments and statues which dotted the city during the Communist Era.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">These statues were either removed by the government as part of the decommunization process, or they were forcibly removed by the Hungarian people in protest at the previous regime.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">When the park was opened in 1993, it became a place to display some of these monuments and showcase an important part of the city&rsquo;s history.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">A small museum on site also includes temporary exhibitions about life under the communist regime, including information about the Hungarian Secret Police.</p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">13. Doh&aacute;ny Street Synagogue</h2>\r\n<figure id=\"attachment_6669\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 673px;\" aria-describedby=\"caption-attachment-6669\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6669 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Doh%C3%A1ny-Street-Synagogue-Budapest.jpg\" sizes=\"(max-width: 683px) 100vw, 683px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Doh%C3%A1ny-Street-Synagogue-Budapest.jpg 683w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Doh%C3%A1ny-Street-Synagogue-Budapest-200x300.jpg 200w\" alt=\"Doh&aacute;ny Street Synagogue\" width=\"683\" height=\"1024\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/pocait/7120166589/in/photolist-bRbHsV-9egjxH-8vjgbE-bYjdhd-bRbK74-8vjgq3-bRbHeK-Ascpdn-bCgZL9-bRbHpV-349bA9-bRbGfT-dzqqAR-bCh23q-jp46p2-pZcZmo-c7cee9-8iPLEL-8iLwvg-bRbKkX-8iLwAk-bDxKsB-joT69V-8vjga5-d3xHxA-c7ce3j-bCh1ms-bqCPD7-B1ybXo-qHM4nh-ennQ11-2PriJE-bCh3kE-bCh2ZA-iSQSaw-A6XMBx-B2EDbd-bCh2Qb-oJd6dv-jp2wYV-c7cdJd-bRbG2V-bRbEpc-bRbGrF-bCgWRd-6n7APL-bqCMZ3-jp44hX-bRbH3c-A6PLSo\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6669\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6669\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Doh&aacute;ny Street Synagogue</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This synagogue is currently one of the largest in the world outside of Israel, despite the fact that Hungary&rsquo;s Jewish population was significantly depleted during World War II.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The interior and the garden were restored in the 1990&rsquo;s, with much of the funding coming from the Hungarian Jewish diaspora population worldwide.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">In the garden you can see a weeping willow memorial, whose metal leaves bear the names of some of those killed during the war.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">There is also a memorial to Swedish diplomat Roual Wallenberg, who helped to save hundreds of Hungarian Jews from concentration camps and ghettos.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">14. Ecseri Flea Market</h2>\r\n<figure id=\"attachment_6670\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6670\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6670 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Ecseri-Flea-Market-Budapest-1024x677.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Ecseri-Flea-Market-Budapest-scaled.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Ecseri-Flea-Market-Budapest-300x198.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Ecseri-Flea-Market-Budapest-768x508.jpg 768w\" alt=\"Ecseri Flea Market\" width=\"720\" height=\"476\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/sybren/480128627/in/photolist-JqMvZ-bYjh2o-bYjgRo-2EyUZh-JqMvT-JqMw8-JrEmp-JqMv6-JqMvM-JrEmi-JrEmK-JqMwz-JrEmD-JrEmz-JrEmH\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6670\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6670\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Ecseri Flea Market</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This fantastic flea market on the outskirts of the city is a great place to find a bargain. It is possible to pick up a variety of different treasures from here, although you may have to search through stalls full of things that you consider to be trash in order to find them.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Even if you are not planning on buying anything, it is still possible to wander for hours amid the stalls, dreaming about the past owners of all of this bric-a-brac. Stalls sell everything from retro clothing, through to memorabilia from the Communist era.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Haggling is recommended, although you should expect to pay a bit more if you are not a local.</p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">15. Ruin Pubs</h2>\r\n<figure id=\"attachment_6671\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6671\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6671 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Ruin-Bar-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Ruin-Bar-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Ruin-Bar-Budapest-300x169.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Ruin-Bar-Budapest-768x433.jpg 768w\" alt=\"Ruin Bar\" width=\"720\" height=\"406\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/_chrisuk/11157851673/in/photolist-hZYUyZ-hZYotS-hZZ2Vn-hZY7Bb-hZZ7iF-f8Bj5w-hZY9FE-hZXLHy-hZYRK2-hZYoPE-hZY8du-hZXNXy-hZYf1N-8K6BhV-hZYdFo-8K6BNT-hZYq9o-hZZ9WB-hZYk4U-pmpMTi-hZZ5Dt-hZZ2un-hZY9KQ-hZY2T2-d7SDHW-qJYPhw-8K6ByF-CKovua-fw4W19-D6xhbA-CmuPc4-hZYdxZ-8K9Euf-fvPETx-fw4WuN-fw4VCN-fvPCM6-7aigRa-fvPFez-fsdHs2-fw4Khh-fw4Laj-fvPDh4-fvPuGz-fvPs2z-fvPriP-fw4GEm-fvPvTe-fvPrGv-fvPqjk\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6671\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6671\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Ruin Bar</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Budapest is famous for its &ldquo;ruin pubs&rdquo;, in which shabby-chic is the order of the day. The best ruin pubs are set up in sprawling, deserted buildings, which have been filled with comfortable, but slightly worn out furniture.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Each pub has its own unique style, so you will often find visitors referring to their favourite pub as &ldquo;the one with the&hellip;&rdquo;. Popular ruin pubs include Instant (&ldquo;the one with the crazy animal pictures&rdquo;), Fogas H&aacute;z (&ldquo;the one with the teeth), Kuplung (&ldquo;the one with the whale&rdquo;) and Szimpla.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">16. Central Market Hall</h2>\r\n<figure id=\"attachment_6672\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6672\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6672 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Central-Market-Hall-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Central-Market-Hall-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Central-Market-Hall-Budapest-300x200.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Central-Market-Hall-Budapest-768x512.jpg 768w\" alt=\"Central Market Hall\" width=\"720\" height=\"480\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/sanjibm/14169373112/in/photolist-nA6JYq-xxeEP-eKPzaj-n6U9s3-mGaryb-n6T8iR-mGdXvP-mFXuKp-n6TFJ4-mFVud8-mFXmCJ-mGfkHK-mTJqpg-xj3oVe-mGcFZw-ndxvDw-CmuvHp-BVgYck-CtMfwR-CrtzGJ-7ikYdh-GJYw9-t29Ltc-aXaA1H-efwngA-A7U4Qd-8foNwv-qwNeC5-53wFpH-zsBoYD-qwPaJo-GK2gK-d9U9pc-d4shtf-GJYhd-efqCq6-GK1Dp-aXazsV-aXaz1M-d4skof-97B7wH-d9U96a-97HrKn-dAFEVy-GK1Up-niQUxB-GJXSu-opiXVi-opjwep-oFwTnv\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6672\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6672\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Central Market Hall</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Great Market Hall in central Budapest is Budapest&rsquo;s most famous marketplace.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Whilst many locals still use the market hall as a place to buy their groceries, the market is incredibly popular with the tourists too.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Locally grown fruits and veg, and locally sourced meats are found on the lower floors, and souvenirs including lace, chess sets and leather goods are available in the upper floors.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">As well as individual ingredients, it is possible to pick up homemade local delicacies like goulash and langos from the food stall upstairs.</p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">17. Buda Castle Hill Funicular</h2>\r\n<figure id=\"attachment_6673\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 490px;\" aria-describedby=\"caption-attachment-6673\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-full wp-image-6673 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Buda-Castle-Hill-Funicular.jpg\" sizes=\"(max-width: 500px) 100vw, 500px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Buda-Castle-Hill-Funicular.jpg 500w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Buda-Castle-Hill-Funicular-200x300.jpg 200w\" alt=\"Buda Castle Hill Funicular\" width=\"500\" height=\"750\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/8771870@N06/14534189635/in/photolist-o9kwdt-9kXzQv-edRyu9-bytQex-c4fwwW-4puneo-bkyW1s-7VvbDx-bE6gj5-bE6fJQ-7WcTPo-c4eBaJ-xCVn6a-xCVkXi-7VuVcX-8mtKtN-8RZB32-ng7TzF-bytRwZ-iibdvj-eJA4si-9RMQw4-avSjkN-9FrcF7-v3pua-c4fzew-avPD8t-8nokw8-4AZNs2-8EhV2f-a7WWiq-c4fxLw-7VuSve-7Vv1h2-62BCja-9dajbh-8Rx5QH-7eDE2K-5xBJrn-5GchXt-7VyoYL-5Mf5uk-7e3MS7-evUHit-5Ggyzy-7VvdQR-4pumsL-5Mf5wi-5Mf5NB-4pukWA\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6673\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6673\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Buda Castle Hill Funicular</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This funicular, which first opened in 1870, is the second oldest funicular of its kind in the world. A system of weights and counterweights is used to help to raise the carriages up and down the hill. The funicular is the fastest way to get to the top of Castle Hill, and is exceedingly popular because of its panoramic views out across the Danube. (You can also get there with&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.getyourguide.com/budapest-l29/buda-castle-segway-tour-t47474/?partner_id=AS1RATG&amp;cmp=budapestbudacastlesegway&amp;deeplink_id=dafefc90-c0fb-539e-8dbc-d72e2562d52c\" target=\"_blank\" rel=\"nofollow noopener noreferrer\" data-gyg-scraped=\"1647160451924\">this Segway tour</a>)</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The speed of ascent was actually slowed down as of 1988, to give passengers more time to enjoy their ride. The track is open daily until 10pm, so it is also a great way to enjoy views of Pest at night.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">18. The Buda Hills</h2>\r\n<figure id=\"attachment_6674\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6674\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6674 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Buda-Hills-1024x678.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Buda-Hills-scaled.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Buda-Hills-300x199.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Buda-Hills-768x508.jpg 768w\" alt=\"Buda Hills\" width=\"720\" height=\"477\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"http://www.worldwidewriter.co.uk/2012/07/budapest-childrens-railway.html\" target=\"_blank\" rel=\"nofollow noopener\">worldwidewriter</a></p>\r\n<span id=\"isc_attachment_6674\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6674\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Buda Hills</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Buda Hills are one of the greenest areas of the city, and are very popular with Budapest citizens who want to take a little time away from city life.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">There are various hiking trails to follow and mountain bike paths as well, which range from easy to medium level of difficulty.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">For those who want to explore the hills in a more leisurely fashion, visit the Children&rsquo;s Railway, which is entirely staffed by children aged 10-14 (with the exception of some adult staff who are responsible for safety).</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">There are plenty of picnic spots dotted around so that you can enjoy a lovely packed lunch.</p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">19. Andr&aacute;ssy Avenue</h2>\r\n<figure id=\"attachment_6675\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 790px;\" aria-describedby=\"caption-attachment-6675\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-full wp-image-6675 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Andr%C3%A1ssy-Avenue.jpg\" sizes=\"(max-width: 800px) 100vw, 800px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Andr%C3%A1ssy-Avenue.jpg 800w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Andr%C3%A1ssy-Avenue-300x198.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Andr%C3%A1ssy-Avenue-768x506.jpg 768w\" alt=\"Andr&aacute;ssy Avenue\" width=\"800\" height=\"527\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/alcaide/5290750558/in/photolist-94wtPd-53ACcb-azttpL-bzeoqU-bzepAh-bN8XnP-bCgBWo-bN8Ug8-d7SsgN-zvHSXH-b76iQe-53AA8y-bzeinE-bN94v8-bzeha1-53ADjU-53wnPZ-d7Sswu-gFB5hD-bN8UDT-bN8TJk-bzed65-bN8ZXn-bzejis-bN91Na-bN8YoP-avznGa-8z3g6a-8YnggJ-bN8Tbz-bCgDjL-bN8ZaP-zvjfVj-s4i2oj-aZV2Z4-5UiRcg-53AJhW-7VWHGF-4sLS6H-7xbr7V-96puPE-53wixB-bzenR9-yzvhSP-bzeq1d-53AuZ1-bzekVs-bRbh8H-bCgDmW-53whK4\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6675\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6675\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Andr&aacute;ssy Avenue</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This wonderful boulevard takes visitors from Erzs&eacute;bet Square in central Pest, out to the City Park. Due to its interesting cultural heritage, it was declared a World Heritage Site in 2002.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Taking a walk down Andr&aacute;ssy is a great way to see a number of Budapest&rsquo;s different architectural styles, including the Hungarian National Opera House, neo-renaissance townhouses and mansions, and a number of different national embassies.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">If you do not fancy promenading down the boulevard, the metro line which runs underneath the Avenue is the third oldest underground railway in the world.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">20. Liberty Statue</h2>\r\n<figure id=\"attachment_6676\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6676\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6676 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Liberty-Statue-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Liberty-Statue-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Liberty-Statue-Budapest-300x225.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Liberty-Statue-Budapest-768x576.jpg 768w\" alt=\"Liberty Statue\" width=\"720\" height=\"540\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/rol1000/10223430875/in/photolist-gzpKPz-5vJyWG-5vJz2h-8uKiTH-5vEf6T-s9LjCC-7FeHPz-c3STwA-c3dsk5-2Besep-c3REiw-c5D44L-6gkoCQ-cUwZdq-7oMEF-zE9Jti-jnZXF-co7f7y-dN6Nws-73zfh7-a3ibZg-jrUHm-6p8FSF-5vEfdK-798fYM-nuyj8P-bTHz-5xG6Gu-6VYFFk-5xBHbr-8wThrU-5vYbEG-5xG5CE-4BQRx9-Er7rc-c7ceL3-nwk7Ek-4L7mcH-Er7dN-BpCfoq-fw4Bsd-8wQhuM-eVyT57-da6d12-jo12Q-eVyNQf-nPNKTD-97Ly8J-8RnkWg-tMM31T\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6676\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6676\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Liberty Statue</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Liberty Statue on Gellert Hill is one of the few prominent Communist statues which remained in situ after the transition to democracy, in part because of its iconic location overlooking the city.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The statue was first erected in 1947 to commemorate the Soviet troops who lost their lives liberating the country, however the engraving was later changed so that it commemorated &ldquo;all who sacrificed their lives for the independence, freedom, and prosperity of Hungary&rdquo;.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The panoramic views from underneath the statue are unparalleled, and help to make the walk to the top of the hill well worth it.</p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">21. Citadella</h2>\r\n<figure id=\"attachment_6677\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6677\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6677 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Citadella-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Citadella-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Citadella-Budapest-300x200.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Citadella-Budapest-768x511.jpg 768w\" alt=\"Citadella\" width=\"720\" height=\"479\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/oansari/2984062096/in/photolist-5xG6Gu-zsBrsg-9eneak-9eqjcL-9eqiQU-bgUeqc-9eqhCw-9eqcWW-nd4Mhf-9eqib9-ajWNon-9m7WYS-9m4T9B-nd4Rpo-nd4YBu-nwkanz-9kYdjp-CJaiEs-7oME6-5Kt7Nv-nd4UPj-dRzFyt-6juA8c-5xBHbr-5xG5CE-nuyj8P-nwk7Ek-ApvCJ8-6juzXK-6SoNCQ-2ZL5CB-9Lkz3e-nuyeMe-7oMEu-A819JD-3KW42N-nd4yy8-55SgP-5v1dPT-eUAEqi-348ShY-wpaHT-rRxEwW-bgUaai-bgUEfv-AWAKZ-iKJmK-5xBHxk-5xBHNt-5xBHAM\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6677\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6677\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Citadella</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Citadel, which sits atop Gellert Hill, was constructed by the Hapsburgs following the failed Hungarian War of Independence.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">It was thought that its prime strategic position would make it easy to control both Buda and Pest, should any future uprisings occur.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Troops were stationed at the Citadel until 1897. Soviet forces once again used the fortress to control the city during the 1956 Hungarian Revolution, and tanks which were situated there fired down on the city.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Citadel now houses a restaurant, a hotel and a museum.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">22. Sz&eacute;chenyi Thermal Baths</h2>\r\n<figure id=\"attachment_6681\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6681\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6681 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Sz%C3%A9chenyi-Thermal-Baths-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Sz%C3%A9chenyi-Thermal-Baths-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Sz%C3%A9chenyi-Thermal-Baths-Budapest-300x225.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Sz%C3%A9chenyi-Thermal-Baths-Budapest-768x576.jpg 768w\" alt=\"Sz&eacute;chenyi Thermal Baths\" width=\"720\" height=\"540\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/harquail/11870170884/in/photolist-j5VJfG-a9yfUU-dBHF19-26yEqh-dnjEX-vZT1o3-a7R7pf-4LZuGp-m7z44g-9jATss-ggtLMK-9STwtw-9SQHaZ-9jxNe4-9jxNtp-9jxN3F-9jxMWp-9STzkm-atzy2D-wEhqBo-9jxNo8-wWa3ab-wWU7o8-dbhoWN-9jxPha-6MfAVz-dnjEW-gDZ1N2-ACdTs5-pmHgA4-dAN9yp-9jATR1-59yzao-9jATyo-XrRHD-9jxPug-9STypm-9jxNFF-9jxPBa-7qNKae-j5TyvB-pimofC-aU6bPV-dnjEY-dnhmH-dnjEZ-B9VLsf-aU6bwx-dJQNkq-dnhmD\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6681\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6681\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Sz&eacute;chenyi Thermal Baths</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Sz&eacute;chenyi Baths complex is the largest &ldquo;medicinal&rdquo; bath centre in Europe. The waters are rich in sulphates, calcium, magnesium, bicarbonate and fluoride, which are believed to help patients with degenerative joint illnesses and other medical issues.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">For those who just want to enjoy the relaxing powers of the thermal pools, there are a variety of different thermal pools on site, as well as saunas and steam rooms.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Massages and beauty treatments are also available at an additional fee.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The two outdoor pools are fantastic places to visit on a cold, dark night, as the steam rising from the hot water makes the whole place seem wonderfully mysterious.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">23. City Park</h2>\r\n<figure id=\"attachment_6678\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6678\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6678 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/City-Park-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/City-Park-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/City-Park-Budapest-300x225.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/City-Park-Budapest-768x576.jpg 768w\" alt=\"City Park\" width=\"720\" height=\"540\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/topbudapest/9365921431/in/photolist-fgCN8Z-fgCMur-fgT2UJ-defJpL-jb8qN-defJyf-fgCMSe-7nZUgk-fgCN2Z-fgCMXr-jv83cD-fgT2Js-t2bsug-uPTNBC-fgT2EN-ABzbw8-ABzbkM-AAnEyC-4ctmKS-8CA9gY-v6YpRA-ddhCjF-wwf8n-jbh4m-7mRwCm-oPKsu3-7nZUqK-6xp3ZW-v7Nr8r-7o4PbQ-ddhCxY-d7SvrA-nfs6sv-dnoHY-97LyyU-nhuPok-4syCGj-aPUZKD-4syCvN-bMcPZB-5qZwAu-iHZSqs-ipCHGX-C6hEZd-4DLphj-c8HVS9-3T3yyK-dCMYwQ-n2Az2V-55SSbY\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6678\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6678\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">City Park</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The City Park is a wonderful leisure facility for the citizens of Budapest, and includes sports facilities, swimming baths, and a boating lake.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">During the winter months, the boating lake is transformed into one of Europe&rsquo;s largest ice rinks.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The park is also home to the Budapest Municipal Zoo and Botanical Gardens, the Budapest Circus and the Vajdahunyad Castle (housing the Museum of Hungarian Agriculture).</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Just outside of the park you can find the Time Wheel, which is one of the largest sand timers in the world.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">In this sand timer, all of the grains of glass take 1 year to fall from the top section to the bottom, and the timer is rotated every New Year.</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">24. Hungarian National Museum</h2>\r\n<figure id=\"attachment_6679\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6679\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6679 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-National-Museum-Budapest.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-National-Museum-Budapest.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-National-Museum-Budapest-300x225.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Hungarian-National-Museum-Budapest-768x576.jpg 768w\" alt=\"Hungarian National Museum\" width=\"720\" height=\"540\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://www.flickr.com/photos/topbudapest/8316133744/in/photolist-dESmQh-9EApXH-9t1o9o-9sXabF-9t1qko-dELKFx-dEM158-dEKjAe-dEKkEi-dEQKwU-dESab1-dESfRE-dEKxRg-dELGUp-dESiDw-dES4eL-dEKhct-dEQPcd-dEKtLi-dER1H5-dEKD6z-dEQEgq-dEQToN-dELuXe-dELsie-dESpsy-dELXtF-9EAmvD-dESeUb-dEQMnQ-9FoJS3-9EFgK1-9ECitx-9EE9yJ-9FkCW4-9FoKCE-9FkUQR-9FoTYG-9EFcv9-9FoxNS-9FkVMx-9FkKrc-dEShxY-dELEbF-dEKnQR-dESdcu-dELwMp-dEQNfq-dESbgd-dERSud\" target=\"_blank\" rel=\"nofollow noopener\">flickr</a></p>\r\n<span id=\"isc_attachment_6679\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6679\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Hungarian National Museum</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The Hungarian National Museum is home to thousands of exhibits detailing the history, art, religion and archaeology of the country, including exhibitions from areas which are now considered to be outside of Hungary&rsquo;s borders.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The spectacular Neoclassical museum building itself is worth looking at, even if you do not plan on touring the inside.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">The peaceful gardens outside of the museum are considered to be a popular meeting spot, and are particularly popular during the summer months.</p>\r\n<div class=\"content_hint\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</div>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 35px; font-weight: 400; line-height: 1.2em; color: #222222; background-color: #ffffff;\">25. Aquincum Museum and Ruin Garden</h2>\r\n<figure id=\"attachment_6680\" class=\"wp-caption aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5em; padding: 0px; border: 0px; clear: both; display: block; max-width: 100%; position: relative; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff; width: 710px;\" aria-describedby=\"caption-attachment-6680\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><span class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"><img class=\"size-large wp-image-6680 with-source\" style=\"box-sizing: inherit; height: auto; max-width: 100%; position: relative; vertical-align: bottom; display: block; margin: 0px auto;\" src=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Aquincum-Museum-and-Ruin-Garden-1024x682.jpg\" sizes=\"(max-width: 720px) 100vw, 720px\" srcset=\"https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Aquincum-Museum-and-Ruin-Garden-scaled.jpg 1024w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Aquincum-Museum-and-Ruin-Garden-300x200.jpg 300w, https://cdn.thecrazytourist.com/wp-content/uploads/2016/03/Aquincum-Museum-and-Ruin-Garden-768x511.jpg 768w\" alt=\"Aquincum Museum and Ruin Garden\" width=\"720\" height=\"480\" loading=\"lazy\" /></span></span>\r\n<p class=\"wp-caption-text\" style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 5px 0px 0px; border: 0px; font-size: 12.75px; opacity: 0.8;\">Source:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" href=\"https://en.wikipedia.org/wiki/Budapest\" target=\"_blank\" rel=\"nofollow noopener\">wikipedia</a></p>\r\n<span id=\"isc_attachment_6680\" class=\"isc-source aligncenter\" style=\"box-sizing: inherit; clear: both; display: inline-block; margin: 0px auto; position: relative;\"></span>\r\n<figcaption id=\"caption-attachment-6680\" class=\"wp-caption-text\" style=\"box-sizing: inherit; font-size: 12.75px; padding-top: 5px; opacity: 0.8;\">Aquincum Museum and Ruin Garden</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">This is a great chance to explore some of Hungary&rsquo;s ancient history. Aquincum was a Roman city which stood where Budapest stands today, and served as an important military base in the ancient Roman Empire.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">It is possible to walk around some of the ruins, including those of an ancient gladiatorial amphitheatre, and other structures, such as the city bathhouse.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">In the museum itself, you can view various Roman relics, and a working replica of famous water organ which was discovered in the area in 1931.</p>\r\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 1.5em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; background-color: #ffffff;\">Want to know what to do in Budapest with the little ones? Check out this post by trip101:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #ff036e;\" title=\"Things to do in Budapest with kids\" href=\"https://trip101.com/article/family-fun-20-things-to-do-in-budapest-with-kids\">Things to do in Budapest with kids</a></p>\r\n<p>&nbsp;</p>', 12, 11, 0, 'published', '2022-03-13 07:46:13', '2022-03-13 07:46:13');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(14, '14 INTERESTING FACTS ABOUT POLAND', '/facts-about-poland', 'Every year, the nation of Poland, which is a member of the European Union, attracts around 20 million visitors.', '<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you&rsquo;re looking to learn a little more about Poland, you&rsquo;ve come to the right place. We&rsquo;ve put together a list of 14 fascinating facts about Poland to educate you on all the wonders of the nation. Even if you&rsquo;ve already done a lot of research and reading about the nation, we bet you won&rsquo;t have heard all of these fun facts!</p>\r\n<h2 id=\"h-1-poland-is-home-to-some-amazing-wildlife\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"1_Poland_Is_Home_To_Some_Amazing_Wildlife\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>1. Poland Is Home To Some Amazing Wildlife</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191862 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODYzOjgxOA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Wildlife-Bison.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Wildlife-Bison.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Wildlife-Bison-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Wildlife-Bison-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Wildlife-Bison-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Wildlife-Bison-1536x1024.jpg 1536w\" alt=\"Facts about Poland Abundant Wildlife Bison\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-Wildlife-Bison.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Poland is home to some truly mind-blowing animal species. Since the nation offers a range of varied habitats, there are so many different types of animals across the country, some of which you may never have seen or even heard about before.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The vast, primeval&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.poland.travel/en/discover/unesco-sites/bialowieza-forest-the-national-park\" target=\"_blank\" rel=\"noreferrer noopener\">forest of&nbsp;Bialowieza</a>&nbsp;is one of the best places to see different animal species. Venturing into the forest is like stepping through a portal and going back hundreds of years to the days when Europe was mostly forest. Among the dense greenery, all kinds of different species thrive, including herds of European bison, the heaviest land animals in Europe!&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The trees are home to some slightly more menacing animals too since packs of wolves make their home there. But don&rsquo;t worry, wolves rarely attack humans, so the forest is safe for visitors, providing they follow basic safety protocol and stick to the track.</p>\r\n<h2 id=\"h-2-poland-has-its-own-seven-wonders\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"2_Poland_Has_Its_Own_Seven_Wonders\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>2. Poland Has Its Own Seven Wonders</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191866 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY4Ojc4MA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Language.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Language.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Language-760x505.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Language-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Language-768x510.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Language-1536x1020.jpg 1536w\" alt=\"Facts About Poland Unesco World Heritage Sites\" width=\"1600\" height=\"1063\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-Language.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You&rsquo;ve probably heard of the&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/wonders-of-the-world/\">Seven Wonders of the World</a>. But did you know that Poland has its own Seven Wonders? The list of wonders was put together by the Polish newspaper&nbsp;Rzeczpospolita in consultation with the general public. Polish people take great pride in their list of unique, historic wonders.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The list of wonders is comprised of:</p>\r\n<ol style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Wieliczka Salt Mine, one of the world&rsquo;s oldest salt mines</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Torun Old Town, a historic settlement in the northwest of Poland and a UNESCO World Heritage Site</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Malbork Castle, the world&rsquo;s biggest castle</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Wawel Castle and Cathedral, 13th and 14th-century buildings exhibiting various different architectural styles</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Elblag Canal,&nbsp;</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Zamosc Old Town, a 50-mile canal and one of Poland&rsquo;s official national Historic Monuments</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Krakow Market Square and Old Town, the largest Medieval town square in Europe</li>\r\n</ol>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This list demonstrates the wide variety of amazing sites&nbsp;across Poland, as well as the diversity of attractions. Each attraction welcomes hundreds of thousands of visitors each year, from history buffs to nature lovers.&nbsp;</p>\r\n<h2 id=\"h-3-polish-beer-is-flavored-with-syrup\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"3_Polish_Beer_Is_Flavored_With_Syrup\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>3. Polish Beer Is Flavored With Syrup</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191870 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODc0OjczNQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Beer.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Beer.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Beer-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Beer-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Beer-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Beer-1536x1023.jpg 1536w\" alt=\"Interesting Poland Facts Beer\" width=\"1600\" height=\"1066\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-Beer.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Like most other countries in Europe, beer is extremely popular in Poland. But beer in Poland tends to be a little different from&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/category/travel-tips/\" target=\"_blank\" rel=\"noreferrer noopener\">what most visitors expect</a>. One of the most fun facts about Poland is that in the average Polish pub, sweet syrups are often added to pints of beer.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Popular flavors include apple, raspberry, and ginger. While it may not be what visitors are used to, Polish drinkers swear by drinking sweetened beer and many&nbsp;newcomers soon pick up a liking for the sweeter taste.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">As well as flavored beer, Polish vodka ( which is considered traditional Polish alcohol) is extremely popular. Some claim vodka was invented in Poland, though that claim is hotly contested by Russians.&nbsp;</p>\r\n<h2 id=\"h-4-some-interesting-sports-are-popular-in-poland\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"4_Some_Interesting_Sports_Are_Popular_In_Poland\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>4. Some Interesting Sports Are Popular In Poland</h2>\r\n<figure class=\"wp-block-image size-full is-style-default\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191863 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODc5OjczMw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Sports.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Sports.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Sports-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Sports-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Sports-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Sports-1536x1024.jpg 1536w\" alt=\"fun Facts About Poland Sports Speedway\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-Sports.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px;\">WROCLAW, POLAND &ndash; JULY 29, 2017: Speedway couple turnament final race Poland &ndash; Australia during The World Games 2017. In action Bartosz Zmarzlik (W) and Maciej Janowski (Y).</figcaption>\r\n</figure>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Poland is a nation of sports lovers. The population is&nbsp;very&nbsp;active, with many Poles of all ages spending plenty of time in the great outdoors. But along with well-known&nbsp;games like soccer and basketball, some slightly more obscure sports are well-loved in Poland.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">One of the most interesting sports trends in the country is speedway.&nbsp;Speedway is a motorcycle racing sport, where bikes with no brakes hurtle around a circular dirt track. Tens of thousands of spectators follow teams in the Polish Extraleague and the atmosphere at matches is electric!</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Volleyball is another extremely popular sport in Poland. The Polish national teams have won numerous gold medals in European championships, and games are often held at large soccer stadiums in front of enthusiastic crowds. Whatever the sport, the crowds are just as important as the players, demonstrating a passion for sport rarely seen outside the nation.&nbsp;</p>\r\n<h2 id=\"h-5-many-famous-inventions-originate-in-poland\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"5_Many_Famous_Inventions_Originate_In_Poland\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>5. Many Famous Inventions Originate In Poland</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Some of the most interesting facts about Poland concern the country&rsquo;s contributions to science. Many game-changing inventions originated from inside Poland, or from Polish nationals living elsewhere. Perhaps that&rsquo;s because Poland has always made education a priority area, forming the world&rsquo;s first Ministry of Education, all the way back in 1773.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The bulletproof vest, walkie-talkie, paper clips, graphene, and even the toothbrush all have their origins in Poland!&nbsp;One famous Polish scientist is Marie Curie,&nbsp;who conducted trailblazing research on radioactivity, helping the world to understand the effects of radiation and earning a Nobel Prize in physics for her efforts. Although she&rsquo;s often referred to as French and was indeed naturalized as a French citizen, her birthplace was in Warsaw.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Polish scientists continue to innovate and discover at various top-class research institutions across the nation. Today, university education is free for Polish citizens, and Polish residents benefit&nbsp;from one of the best education systems in the world.&nbsp;</p>\r\n<h2 id=\"h-6-mushroom-foraging-is-a-popular-activity-across-the-nation\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"6_Mushroom_Foraging_Is_A_Popular_Activity_Across_The_Nation\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>6. Mushroom Foraging Is A Popular Activity Across The Nation</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191864 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODg5OjkyMQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Mushroom-Foraging.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Mushroom-Foraging.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Mushroom-Foraging-731x507.jpg 731w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Mushroom-Foraging-1538x1067.jpg 1538w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Mushroom-Foraging-150x104.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Mushroom-Foraging-768x533.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Mushroom-Foraging-1536x1066.jpg 1536w\" alt=\"Facts about Poland Mushroom Foraging\" width=\"1600\" height=\"1110\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-Mushroom-Foraging.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Sports aren&rsquo;t the only popular outdoor activity in Poland. Many&nbsp;residents of Poland travel&nbsp;the forests and countryside, collecting edible mushrooms with their families.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">In September and October, Polish people flock to mushroom hotspots to enjoy finding these woodland delicacies. Later, the mushrooms could go into making a wide variety of&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/polish-foods/\" target=\"_blank\" rel=\"noreferrer noopener\">yummy dishes</a>, including zapiekanka, also known as Polish pizza. But the main appeal of mushroom picking lies in exploring the forest and spending time with friends and family.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Mushroom pickers use local knowledge to ensure they&rsquo;re picking safe, edible fungi. Especially in the countryside, many Polish people have a deep connection to the land and know-how to safely collect food from the wilderness.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Learn more about some amazing Polish food&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/polish-foods/\">with our guide here!</a></p>\r\n<h2 id=\"h-7-the-polish-air-force-had-a-big-impact-in-world-war-ii\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"7_The_Polish_Air_Force_Had_A_Big_Impact_In_World_War_II\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>7. The Polish Air Force Had A Big Impact In World War II</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191871 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk1OjgwNw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-WWII.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-WWII.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-WWII-757x507.jpg 757w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-WWII-1594x1067.jpg 1594w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-WWII-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-WWII-768x514.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-WWII-1536x1028.jpg 1536w\" alt=\"Facts About Poland in WWII\" width=\"1600\" height=\"1071\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-WWII.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">One of the proudest parts of Poland&rsquo;s history is the role their intrepid fighter pilots played in World War II. Poland was the first country to fall to Nazi, Germany and was fully occupied by late 1939. But that didn&rsquo;t stop many Polish people from fighting, as they fled across Europe before joining another country&rsquo;s military and returning to fight.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Long after Poland had been occupied, the nation&rsquo;s pilots continued to fight courageously.&nbsp;Traveling across&nbsp;Europe in the air or on foot, they would eventually end up in various countries, with many finding their way to Britain where they flew for the UK&rsquo;s Royal Air Force.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">During the famous Battle of Britain,&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.iwm.org.uk/history/the-polish-pilots-who-flew-in-the-battle-of-britain\" target=\"_blank\" rel=\"noreferrer noopener\">145 Polish fighter pilots</a>&nbsp;fought in the air. One pilot,&nbsp;Sergeant Antoni Glowacki, shot down five German bombers in a single day! These Polish pilots are today honored as some of the bravest and most effective men to see combat against the Nazis.</p>\r\n<h2 id=\"h-8-gdansk-is-a-city-of-contrasts\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"8_Gdansk_Is_A_City_Of_Contrasts\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>8. Gdansk Is A City Of Contrasts</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191868 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTAwOjc0NQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-gdansk.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-gdansk.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-gdansk-760x505.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-gdansk-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-gdansk-768x510.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-gdansk-1536x1020.jpg 1536w\" alt=\"Facts about Poland Gdansk\" width=\"1600\" height=\"1063\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-gdansk.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Poland has a lot of interesting cities, all with different attractions. But one of the most interesting is Gdansk. Before WWII, the city was an independent city-state, as well as an important trading destination for other countries, meaning it has a unique character and history.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Gdansk is packed full of signs of its fascinating history. The city is home to many Dutch-style buildings, Prussian sculptures, and other nods to its cosmopolitan history. The various museums and galleries spread across the city are another major&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/experiences/\" target=\"_blank\" rel=\"noreferrer noopener\">tourist draw</a>, documenting the eclectic culture which has thrived in the city over the centuries.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Gdansk draws in huge numbers of tourists each year, with many coming to enjoy the varied cultural sites and soak in the atmosphere. From the old town to the docks, there&rsquo;s something for everyone in this unique city.&nbsp;</p>\r\n<h2 id=\"h-9-poland-is-home-to-the-world-s-largest-castle\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"9_Poland_Is_Home_To_The_Worlds_Largest_Castle\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>9. Poland Is Home To The World&rsquo;s Largest Castle</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191865 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTA1OjkxNg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Malbortk-Castle.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Malbortk-Castle.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Malbortk-Castle-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Malbortk-Castle-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Malbortk-Castle-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Malbortk-Castle-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Malbortk-Castle-1536x1152.jpg 1536w\" alt=\"Interesting Poland Facts Largest Castle Malbork\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-Malbortk-Castle.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://whc.unesco.org/en/list/847/\" target=\"_blank\" rel=\"noreferrer noopener\">Malbork Castle</a>&nbsp;in the town of Malbork isn&rsquo;t just Poland&rsquo;s biggest castle. It&rsquo;s the biggest castle in the entire world!&nbsp;The castle looks like something from a fairy tale, with its beautiful brick spires, towers, and walls. With its riverfront location, it&rsquo;s one of the most beautiful sites in the country.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The thirteenth-century fortress was built by Teutonic Knights during the Thirteen Years&rsquo; War, served as a residence of Polish kings, and was partially destroyed during WWII.&nbsp;Today, it&rsquo;s a popular tourist destination and stands as one of Poland&rsquo;s 17 UNESCO World Heritage sites.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Visitors who wish to get up close and personal with this Medieval wonder can&nbsp;explore the grounds and visit the castle museum. There&rsquo;s plenty of history and folklore to be discovered across these vast grounds.</p>\r\n<h2 id=\"h-10-heavy-metal-is-extremely-popular-in-poland\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"10_Heavy_Metal_Is_Extremely_Popular_In_Poland\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>10. Heavy Metal Is Extremely Popular In Poland</h2>\r\n<figure class=\"wp-block-image size-full is-style-default\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191867 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTEwOjgwNg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Heavy-Metal-Music.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Heavy-Metal-Music.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Heavy-Metal-Music-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Heavy-Metal-Music-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Heavy-Metal-Music-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-Heavy-Metal-Music-1536x1024.jpg 1536w\" alt=\"Facts about Poland Music Festivals\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-Heavy-Metal-Music.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Musically, Poland might be best-known for its classical composers like Frederic Chopin. If that&rsquo;s all you know about Polish music, you might have an idea that it&rsquo;s classy and refined. But today, the music scene has a little more edge and many young Poles are fans of death metal.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Artists like Acid Drinkers, Behemoth, and Vader all have hundreds of thousands of dedicated fans. On public transport, it&rsquo;s not uncommon to hear screamed vocals and heavy guitar riffs pouring out from headphones all around you!</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Heavy metal festivals are also common in Poland. One of the most popular is the Mystic Festival, held at the vast Krakow Arena, attracting tens of thousands of metalheads from all over the world.&nbsp;</p>\r\n<h2 id=\"h-11-every-polish-name-has-its-own-holiday\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"11_Every_Polish_Name_Has_Its_Own_Holiday\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>11. Every Polish Name Has Its Own Holiday!</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Take a look at a Polish calendar and you might notice something interesting.&nbsp;Each date is marked with two common Polish given names.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">These dates are known as name days and they&rsquo;re very important in Poland. When a name day rolls around each year, the lucky folk with the matching name will receive gifts, flowers, and congratulations from friends and family!</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Some names appear twice or more on the calendar. When this occurs, a person takes the first name day after their birthday to celebrate. It should be noted that this custom is not celebrated in the regions of Upper Silesia and Kashubia.</p>\r\n<h2 id=\"h-12-there-are-over-a-hundred-bird-sanctuaries-in-poland\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"12_There_Are_Over_A_Hundred_Bird_Sanctuaries_In_Poland\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>12. There Are Over A Hundred Bird Sanctuaries In Poland</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191869 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTE5Ojk2Mw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-biebrza-Bird-Sanctuary.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-biebrza-Bird-Sanctuary.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-biebrza-Bird-Sanctuary-689x507.jpg 689w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-biebrza-Bird-Sanctuary-1449x1067.jpg 1449w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-biebrza-Bird-Sanctuary-150x110.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-biebrza-Bird-Sanctuary-768x565.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Poland-biebrza-Bird-Sanctuary-1536x1131.jpg 1536w\" alt=\"Fun Facs about Poland Bird Sanctuaries\" width=\"1600\" height=\"1178\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Poland-biebrza-Bird-Sanctuary.jpg\" data-pin-description=\"14 Interesting Facts About Poland\" data-pin-title=\"14 Interesting Facts About Poland\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">A key part of Poland&rsquo;s thriving biodiversity is its bird population. There are hundreds of different avian species to be seen across the country, including some unique to the region.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">There are also more than a hundred bird sanctuaries spread across the nation. Visit one of these sanctuaries and you could see rare species like the white-tailed eagle (the national bird of Poland), barn-owl, kingfisher, cormorants, and many more.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Sanctuaries are spread across the country&rsquo;s various habitats and often have facilities for visitors to hike around the area, or hunker down with a pair of binoculars and see how many different species they can spot.&nbsp;</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"13_The_polish_Language_is_Extremely_Difficult\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>13. The Polish Language Is Extremely Difficult</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">One of the most interesting facts about Poland is that the Polish language is considered one of the toughest languages to learn in the world. What makes it so complicated is that it uses seven cases, and 32 letters, which makes for really complex grammar.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Pair that with how hard pronunciation is and you can see why it is one of the hardest languages to learn in the world. 97 percent of people who live in Poland speak Polish as their first language.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"14_Poland_Has_17_Nobel_Prize_Winners\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>14. Poland Has 17 Nobel Prize Winners</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Noble Prize is one of the most distinguished awards you can win in the world and Poland has won in both Literature and Peace. These include Maria Sklodowska-Curie (physics and chemistry) Henryk Sienkiewicz Joseph Conrad and Frederic Chopin just to name a few.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Most people don&rsquo;t know that Marie Curie was the first woman to win the Nobel Prize and the only person to win it 2 times.</p>', 12, 11, 0, 'published', '2022-03-13 07:50:45', '2022-03-13 07:50:45');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(15, 'OTTAWA CANAL CRUISE – COMPLETE 7 DAY ITINERARY', 'ottawa-canal-cruise', 'Boating the Rideau Canal is one of the best things to do in Canada, and luckily going through the historic lock system can be done without owning your own boat.', '<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\">The Rideau Canal Le Boat Experience</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193100 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY3Ojg5MQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Experience.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Experience.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Experience-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Experience-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Experience-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Experience-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Experience-1536x1152.jpg 1536w\" alt=\"The Ottawa Canal Cruise Experience with Le Boat Canada\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Experience.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The trip begins with an hour-long introduction to your Le Boat. During this time you&rsquo;ll be shown how to read the maps and charts, how to use the stove, and where all of the important instruments and equipment are, show you the knots you&rsquo;ll need to tie up at the docks, but most importantly, how to drive the boat!</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/places-to-visit-on-the-rideau-canal/\">Le Boats on the Rideau Canal</a>&nbsp;are very easy to drive. The Horizon Fleet boats are all equipped with front and rear thrusters making it easy to turn on a dime. When pulling up to moor, it&rsquo;s easy to park between boats are in tight spaces by using the side thrusters to inch into a slot. All Le Boats have throttlers on them so you can&rsquo;t go faster than 10 km per hour making it easy to cruise and relax throughout your trip.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-179045 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODcwOjEwMzY=-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson-1536x1152.jpg 1536w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson-853x640.jpg 853w\" alt=\"Le Boat Canada Driving Lesson\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Le-Boat-Canada-Rideau-Canal-Driving-Lesson.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">After your lesson and if you feel confident, your instructor will guide you through the first set of locks teaching you lock etiquette and how to safely pull in. You will have all the tools you need to comfortably cruise through the Rideau Canal on your luxury yacht.</p>\r\n<div class=\"gb-container gb-container-fe17f7d6\" style=\"box-sizing: inherit; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<h4 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-size: 28px; font-style: inherit; line-height: 1.4em; color: #3c4053;\">Save 10% on select boats rentals on the Rideau Canal</h4>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Save 10%</span>&nbsp;on&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.leboat.com/boats/horizon-2\">Horizon 2</a>&nbsp;and&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.leboat.com/boats/horizon-3\">Horizon 3</a>&nbsp;boat rentals on the&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.leboat.com/boating-holidays/canada/rideau-canal\">Rideau Canal, Canada</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Valid on departures from&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">June&nbsp;to August 2022</span></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Valid on short breaks and 7-10+night cruises</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">New bookings only, combinable with other offers for up to 15% maximum discount</li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Horizon_Fleet\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Horizon Fleet</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-179120 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODc3Ojc3MA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Horizon-Fleet-Canada.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Horizon-Fleet-Canada.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Horizon-Fleet-Canada-760x428.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Horizon-Fleet-Canada-150x84.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Horizon-Fleet-Canada-768x432.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Horizon-Fleet-Canada-1536x864.jpg 1536w\" alt=\"Le Boat Horizon Fleet Smiths Falls Ontario Canada\" width=\"1600\" height=\"900\" data-pin-media=\"https://theplanetd.com/images/Le-Boat-Horizon-Fleet-Canada.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We booked a four-bedroom, four-bath for 3 couples. My parents came with us as well as my brother and Sister in Law. We also had two cats on board. Le Boat is pet-friendly, so you can bring the entire family including the pets along!</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Each room has its own bathroom and shower. There is a large sitting area inside that comfortably sat all of us. Even though there are four bedrooms and four baths, I think it would have been crowded downstairs with couples, but there are the upper deck and picnic tables where people can spread out. If the weather is good, it would be easy to fit four couples in the Horizon 4. We cruised during fall colours, so we had some very cold days and nights and spent more time downstairs than we did during our summer vacation on the Horizon 3.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-179044 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODgwOjc5Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Horizon-3-Interior.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Horizon-3-Interior.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Horizon-3-Interior-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Horizon-3-Interior-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Horizon-3-Interior-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Horizon-3-Interior-1536x1024.jpg 1536w\" alt=\"Le Boat Canada Horizon 3 Interior\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Le-Boat-Canada-Horizon-3-Interior.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The kitchen has two small refrigerators, and there is a barbecue up top, so cooking is easy on Le Boats. We also brought coolers to store drinks and food as the refrigerators on the Horizon 4 aren&rsquo;t full size. There is plenty of deck space, so you don&rsquo;t have to worry about it being crowded.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"How_to_Choose_Which_Direction_to_go\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>How To Choose Which Direction To Go</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193107 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODgzOjgyOA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Direction.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Direction.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Direction-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Direction-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Direction-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Direction-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Direction-1536x1152.jpg 1536w\" alt=\"Ottawa Canal Map\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Direction.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Choosing to take a boat trip on the Rideau Canal is the easy part, but choosing which way to go can make for a tough decision. Smiths Falls is located almost directly in the middle of the Rideau Canal. If you go north, you&rsquo;ll be heading to Ottawa, Canada&rsquo;s capital city. If you go south, you&rsquo;ll be making your way to Kingston, Canada&rsquo;s first capital city. Both are great cities to visit and both directions offer interesting stops along the way, so today, we are going to give you some insight into choosing which way to boat on the Rideau Canal from Smiths Falls.</p>\r\n<h2 id=\"h-overview-of-north-and-south\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Overview_of_North_and_South\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Overview Of North And South</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193128 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODg2OjEwNzg=-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa-640x960.jpg\" sizes=\"(max-width: 640px) 100vw, 640px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa-640x960.jpg 640w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa-338x507.jpg 338w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa-712x1067.jpg 712w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa-100x150.jpg 100w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa-768x1152.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa-1024x1536.jpg 1024w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa-150x225.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-North-to-Ottawa.jpg 1067w\" alt=\"Ottawa Canal cruise North \" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-North-to-Ottawa.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We had the opportunity to drive both north and south and can attest that both are superb trips. They are, however, quite different, so choosing which way to go depends on the type of trip you are looking to have.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When boating north towards Ottawa, there are many more locks to go through. Between Smiths Falls and Ottawa, there are 12 lock stations containing 21 locks. The waterways are narrower and the trip feels more like an actual canal trip than a boating vacation in Ontario. It is possible to get to Ottawa and back on a week-long trip when going in this direction.</p>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-179043 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODg5OjEwNzY=-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance-1536x1152.jpg 1536w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance-853x640.jpg 853w\" alt=\"Le Boat Canada Houseboats\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Le-Boat-Canada-Rideau-Canal-Chaffeys-Entrance.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When going south, the bodes of water are larger and you will be driving on lakes that are connected by locks. At times you don&rsquo;t even feel like you are on a canal system, but instead in Ontario&rsquo;s cottage country. There are more choices of towns to stop at and you can do more of a loop instead of an out and back trip.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Unless you have a lot of time, most people won&rsquo;t make it all the way to Kingston and instead usually stop at the top of Jones Falls Locks, which is often considered the most beautiful locks on the Rideau Canal. To get to Jones Falls, you&rsquo;ll go through 8 lock stations (after departing Smiths Falls) with only 8 locks which means you have more cruising time and less time inside the locks.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">For more information on cruising south on the Rideau Canal, read our full post here:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://theplanetd.com/le-boat-rideau-canal/\">Amazing Rideau Canal Cruise &ndash; A Unique Cruise Holiday with Le Boat</a></span></p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"The_Trip_North_to_Ottawa\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>The Trip North To Ottawa</h2>\r\n<div class=\"wp-block-image is-style-default\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193103 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk0Ojg5Mw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Lock-Station.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Lock-Station.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Lock-Station-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Lock-Station-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Lock-Station-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Lock-Station-1536x1024.jpg 1536w\" alt=\"Ottawa Cana Boat Cruise in Autumn\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Merrickville-Lock-Station.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We were most excited to spend the night downtown Ottawa on our boat when planning our northern Le Boat trip. Le Boats park right downtown in front of the Rideau Center with views of the Parliament Buildings and the Fairmont Chateau Laurier. In past trips to Ottawa, we&rsquo;ve seen yachts moored in the canal and could only dream of making this trip. Now that it is possible to rent a houseboat, anyone can join this exclusive club of boating the historic Rideau Canal.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Day_1_Merrickville_4_hours\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Day 1 Merrickville (4 Hours)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193099 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk3OjkwOQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Shops.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Shops.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Shops-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Shops-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Shops-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Shops-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Shops-1536x1152.jpg 1536w\" alt=\"Ottawa Canal Merrickville\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Merrickville-Shops.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The first stop from Smiths Falls is the town of Merrickville where we moored for the night on the blue line of the Merrickville locks. When mooring in Merrickville, we suggest staying at the top of the locks so you can easily enjoy exploring the town. You don&rsquo;t have far to go the next day, and Merrickville is definitely a highlight of this Rideau Canal cruise.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193101 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk5OjkxMA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Town.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Town.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Town-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Town-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Town-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Town-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Town-1536x1152.jpg 1536w\" alt=\"Ottawa Canal Downtown Merrickville\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Merrickville-Town.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This was our favourite stop on our northern Rideau Canal route. Merrickville has everything you could need. Merrickville was voted &ldquo;Canada&rsquo;s Most Beautiful Village by Communities in Bloom&rdquo; and it is very pretty. There is plenty of shopping downtown and we picked up quite a few items for our house. Make sure to stroll its main street and pop into the shops. Our favorite place was the Gray Art Glass Studio where we bought a gorgeous centerpiece for our dining room table and Stella Luna Cafe for gelato. The owner studied the art of making Gelato at Carbigiani Gelato University&nbsp;in Bologna! Dave and I have actually toured that university!</p>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Merrickville_Ruins\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Merrickville Ruins</h3>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193127 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTAyOjExMDM=-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins-640x960.jpg\" sizes=\"(max-width: 640px) 100vw, 640px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins-640x960.jpg 640w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins-338x507.jpg 338w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins-712x1067.jpg 712w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins-100x150.jpg 100w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins-768x1152.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins-1024x1536.jpg 1024w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins-150x225.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Merrickville-Ruins.jpg 1067w\" alt=\"Merrickville Ruins on the Ottawa Canal\" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Merrickville-Ruins.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">A highlight of visiting Merrickville is walking to the beautiful ruins. The foundation of an abandoned woolen mill is located in a picturesque setting just on the other side of the Rideau Canal from town. There&rsquo;s a walking trail, beautiful stone foundations, a power generator, and views of the Rideau Canal. Don&rsquo;t miss walking across the bridge to see them. While docked in Merrickville, you can also visit the old blockhouse that has been turned into a museum.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Day_2_-_Burritts_Rapids_3_hours\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Day 2 &ndash; Burritts Rapids (3 Hours)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193105 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTA1OjgwOA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Burritts-Rapids.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Burritts-Rapids.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Burritts-Rapids-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Burritts-Rapids-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Burritts-Rapids-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Burritts-Rapids-1536x1024.jpg 1536w\" alt=\"Burritts Rapids on the Ottawa Canal\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Burritts-Rapids.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The next stop is Burritts Rapids. When leaving Merrickville there are three locks and a swing bridge to go through. Once you are through these locks, it&rsquo;s not long until you are at Burritt Rapids. At Burritts Rapids there is a nice hiking trail, The Tip to Tip. It is a 4.3 kilometer is an out &amp; back trail&nbsp;where you&rsquo;ll pass the village of Burritts Rapids as you make your way out to the tip.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193104 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTA3Ojg4NQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Rideau-Woodland-Ramble.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Rideau-Woodland-Ramble.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Rideau-Woodland-Ramble-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Rideau-Woodland-Ramble-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Rideau-Woodland-Ramble-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Rideau-Woodland-Ramble-1536x1024.jpg 1536w\" alt=\"Rideau Woodland Ramble near Burritts Rapids on the Ottawa Canal\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Rideau-Woodland-Ramble.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">For us, the highlight here was visiting Rideau Woodland Ramble. If you&rsquo;ve rented bicycles with your Le Boat (or bringing your own) you can bike to this beautiful display garden and center. It is only 1.7 km from the Burritts Rapids Lock station, so you can also walk there. These gardens are beautiful with walking trails through gorgeous displays. There&rsquo;s even a labyrinth in the garden where you can take a moment for quiet meditation. There are Japanese maples, quiet ponds, garden gnomes, and the Totem Trail. Make sure to spend some time perusing the gardens and stocking up on supplies for your own garden.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It&rsquo;s dinner on the boat tonight as you enjoy a beautiful sunset from the lock station.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Day_3_-_Long_Island_5_hours\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Day 3 &ndash; Long Island (5 Hours)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193122 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTExOjc4Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Long-Island.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Long-Island.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Long-Island-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Long-Island-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Long-Island-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Long-Island-1536x1024.jpg 1536w\" alt=\"Long Island on the Rideau Canal to Ottawa\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Long-Island.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Today is a long boating day so try to be first through the locks so you can make your way to Long Island. Once you are through the Merrickville Locks, it&rsquo;s smooth sailing as you cruise the Rideau River to Manotick. This is a long portion with beautiful scenery and it is along this route that the big homes start coming into view. We just loved sitting on the deck watching the scenery go by.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Today is a good day to have someone prepare lunch and snacks on board so you can make good time. We wanted to get through the Long Island locks because it is still a long way to Ottawa. But it is important to note that when going through lock stations, you need to be at the locks 1.5 hours before closing. Since we were cruising in Autumn to see the fall colors, that meant that the lock station closed at 5 pm, so if we were to get through, we needed to arrive at 3:30. Luckily we timed this day to happen on the weekend when hours are longer, so by leaving at 9 am sharp, we easily made it through the Long Island locks.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193106 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTE0Ojg5Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Lunch-Onboard.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Lunch-Onboard.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Lunch-Onboard-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Lunch-Onboard-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Lunch-Onboard-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Lunch-Onboard-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Lunch-Onboard-1536x1152.jpg 1536w\" alt=\"BBQ Lunch on the way to Ottawa Canal Long Island\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Lunch-Onboard.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This is definitely a lock station that you want to go through if you can and park at the bottom because it is another long day on the water, so you will want to be able to leave bright and early. If you park at the top, you&rsquo;ll need to wait for the lock station to open at 9 am.</p>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Black_Rapids_1_hour\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Black Rapids (1 hour)</h3>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193108 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTE3Ojc5OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Black-Rapids.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Black-Rapids.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Black-Rapids-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Black-Rapids-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Black-Rapids-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Black-Rapids-1536x1024.jpg 1536w\" alt=\"Black Rapids Lock Station Rideau Canal Le Boat\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Black-Rapids.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We made such good time, that once we got through the Long Island Locks, we decided to push forward to Black Rapids Locks. It is less than 10 km from Long Island to Black Rapids and since we were boating on the weekend, we had time to make it through Black Rapids to stay at the bottom of the locks so that we could push forward to Ottawa first thing in the morning.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It had started to rain, so this was a good time to make up some distance. We moved downstairs to drive from below in warmth and comfort and continued to Black Rapids where we spent the night at the bottom of the locks. We were the last boat through and Now had a very short day ahead of us to Ottawa.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Day_4_Ottawa_4_Hours\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Day 4 Ottawa (4 Hours)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193121 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTIxOjc2Mw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-City-color.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-City-color.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-City-color-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-City-color-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-City-color-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-City-color-1536x1024.jpg 1536w\" alt=\"Ottawa Canal Le Boat Fall\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-City-color.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It&rsquo;s an exciting day because it&rsquo;s the final stretch to the Capital City of Canada. The homes are closer together and continue to grow in size with floatplanes and motorboats lining their docks. Friendly kayakers wave as you pass and after passing marinas and bridges, the channel narrows as you approach Ottawa. There are two final locks to pass through before reaching the city of Ottawa. Hartwell and Hogs Back locks. At this point, you definitely feel like you are in an urban environment. Cyclists ride their bicycles along the shore, people are walking along the path and crowds stop to watch the boats go through the locks. Luckily you&rsquo;ve been driving Le Boat for four days by now, so you go through the locks like a pro!</p>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Dows_Lake\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Dows Lake</h3>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193109 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTI0Ojg0Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Dows-Lake.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Dows-Lake.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Dows-Lake-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Dows-Lake-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Dows-Lake-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Dows-Lake-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Dows-Lake-1536x1152.jpg 1536w\" alt=\"Dows Lake neat Ottawa on the Canal\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Dows-Lake.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It is now time to make a decision. Dows Lake is just outside of downtown Ottawa, but it is a great place to moor for the evening. The large marina has three restaurants and watersport rentals. This is also the place where you&rsquo;ll want to pump out your boat and fill up your water. You can park here for the night, but to stay at the Marina, there is an extra cost. (approximately $80)</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Day_5_Downtown_30_minutes_to_1_hour\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Day 5 Downtown (30 Minutes To 1 Hour)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193120 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTI3OjM1NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-downtown-view-640x960.jpg\" alt=\"Downtown view on the Ottawa Canal Le Boat\" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-downtown-view.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">After pumping out and filling our water, we continued on to downtown Ottawa from Dows Lake. This way, we could park in the heart of the city and are within walking distance from most of the city&rsquo;s top attractions. The cruise into Ottawa is beautiful. Many people pay for a Rideau Canal cruise from downtown, and on Le Boat, you get to drive your own boat on this historic stretch of waterway. From Dows Lake to Downtown, it&rsquo;s just a 20 to 30-minute cruise, but you do need to wait for one lift bridge. The phone number is on your charts, so call ahead to let the bridge master know you are on your way. They&rsquo;ll then stop traffic and let you through.</p>\r\n<div class=\"gb-container gb-container-5a1ee32d\" style=\"box-sizing: inherit; margin-top: 30px; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Read all these Ottawa Guides to help you plan your visit</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/web-stories/things-to-do-in-ottawa-story/\">Top Things to do in Ottawa</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/places-to-visit-in-ottawa/\">Places to Visit in Ottawa this Autumn</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/ottawa-museums-guide/\">15 Awesome Ottawa Museums &ndash; What Not to Miss and Why</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/nordik-spa-ottawa/\">Nordik Spa Nature &ndash; A Guide to Ottawa&rsquo;s Luxury Spa Escape</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Things_to_do_in_Ottawa_near_Le_Boat_parking\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Things to do in Ottawa near Le Boat parking</h3>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193110 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTM0Ojg3OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Slip.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Slip.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Slip-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Slip-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Slip-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Slip-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Slip-1536x1152.jpg 1536w\" alt=\"Ottawa Canal Downtown slip Le Boat\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Downtown-Slip.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You now have the afternoon to enjoy all the amazing things to do in downtown Ottawa. We have visited this city often and have many articles on Ottawa attractions. But if it is your first time, you are in a great place because you are within walking distance to the ByWard Market, The Parliament Buildings, The Rideau Centre, and Sparks Street. The ByTown museum is close by and for just $2 you can go inside and walk through this pretty museum that traces the history of Ottawa known then as Bytown</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We suggest heading to ByWard Market for dinner. There are plenty of places to eat in the market and be sure to go up to the top of the Andaz Hotel for cocktails on the rooftop bar and amazing views of Ottawa.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193112 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTM3Ojg4Mw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sparks-Street.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sparks-Street.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sparks-Street-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sparks-Street-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sparks-Street-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sparks-Street-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sparks-Street-1536x1152.jpg 1536w\" alt=\"Explore Sparks street in Ottawa, Canada\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Sparks-Street.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Before dinner, take a stroll to Parliament Hill to see the government parliament buildings and to look across the Ottawa River at the city of Gatineau Quebec. On your way back, hop up to Sparks Street. This pedestrian street is great for a stroll and comes out at the War Memorial where you can see the changing of the guards at the Tomb of the Unknown soldier at 9 am and 5 pm.</p>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Evening\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Evening</h3>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193116 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQwOjg3NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Haunted-Tour.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Haunted-Tour.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Haunted-Tour-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Haunted-Tour-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Haunted-Tour-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Haunted-Tour-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Haunted-Tour-1536x1152.jpg 1536w\" alt=\"Haunted walking tour in Ottawa Le Boat\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Haunted-Tour.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">An evening in Ottawa offers countless things to do. One fun thing for the whole family is to take a haunted walking tour. Learn about the city of Ottawa and its history as you stroll to some of its famous sights while hearing fun stories about ghosts in the night.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Day_6_Ottawa_Optional_Full_Day_in_Ottawa\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Day 6 Ottawa (Optional Full Day In Ottawa)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193111 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQzOjg5Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Unknown-Soldier.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Unknown-Soldier.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Unknown-Soldier-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Unknown-Soldier-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Unknown-Soldier-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Unknown-Soldier-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Unknown-Soldier-1536x1152.jpg 1536w\" alt=\"Full day tour in Ottawa with Le Boat\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Unknown-Soldier.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you only have one day in Ottawa, the best thing to do is to take a tour. We booked an e-bike tour with&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://escapebicycletours.ca/\" target=\"_blank\" rel=\"noreferrer noopener\">Escape Bicycle Tour</a>s and it was an excellent way to visit all the top Ottawa attractions. What we loved about e-biking is that it is perfect for a multi-generational trip. My parents joined us on this trip and so did my brother and sister-in-law. We all have different levels of cycling, but riding an e-bike levels the playing field. It&rsquo;s so easy!</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193113 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQ1OjM0OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-E-bike-Tour-640x960.jpg\" alt=\"E-Bike Tour in Ottawa Escape Bike Tours\" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-E-bike-Tour.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We met our guide Andrew at their office on Sparks Street. It&rsquo;s a fantastic location where you can order specialty coffees, beer, and wine. We had coffee before the tour and wine after. After learning how to navigate the bikes we followed Andrew through the city on the well-appointed bike paths. Ottawa is a very bikeable city. My family was a bit worried about cycling in the city, but their nerves were quickly eased once we started cycling. There were paths throughout the city and when we were in traffic, bikes had their own lanes.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193114 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQ3Ojg0NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Gatineau.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Gatineau.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Gatineau-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Gatineau-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Gatineau-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Gatineau-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Gatineau-1536x1152.jpg 1536w\" alt=\"Gatineau Quebec from Ottawa on Bike Tour\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Gatineau.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The tour took us across the Ottawa River into Quebec where we saw Canada&rsquo;s busiest Museum, The Canadian Museum of History. We then cycled across the Alexandra Bridge on the designated bike and walking path to the National Gallery of Canada where we saw the amazing Maman spider installation and took in the views of the Notre Dame Basilica. It was then out to Sussex Drive including to see Rideau Hall, the home of Canada&rsquo;s governor-general. The tour was beautiful, showcasing the best of the city while enjoying the great outdoors.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-192699 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://youtu.be/qzPk7Y2n0O8\" target=\"_blank\" rel=\"noopener\"><img id=\"OTQ5OjgyMQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/rideau-canal-to-ottawa-video.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/rideau-canal-to-ottawa-video.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/rideau-canal-to-ottawa-video-760x429.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/rideau-canal-to-ottawa-video-150x85.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/rideau-canal-to-ottawa-video-768x433.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/rideau-canal-to-ottawa-video-1536x867.jpg 1536w\" alt=\"le boat canal cruise to Ottawa\" width=\"1600\" height=\"903\" data-pin-media=\"https://theplanetd.com/images/rideau-canal-to-ottawa-video.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></a></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://youtu.be/qzPk7Y2n0O8\" target=\"_blank\" rel=\"noreferrer noopener\">Watch the full video here</a></figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">After our tour, we had some beer and wine on the Escape Bicycle outdoor patio on Sparks Street. It&rsquo;s such a great atmosphere, and owner Maria came out to join us for a cappuccino. When visiting Ottawa, make sure to book a tour or bicycle rental with Escape Bicycle Tours,&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://escapebicycletours.ca/\" target=\"_blank\" rel=\"noreferrer noopener\">visit their website for details.</a></p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193117 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTUxOjg2Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Esacape-Manor.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Esacape-Manor.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Esacape-Manor-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Esacape-Manor-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Esacape-Manor-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Esacape-Manor-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Esacape-Manor-1536x1152.jpg 1536w\" alt=\"Escape Manor in Ottawa\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Esacape-Manor.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">In the evening we had a fun family bonding experience at Escape Manor where we worked together as a team to get out of the Asylum. It was the first escape room we&rsquo;ve ever done and I highly recommend doing it. Especially if you are with your family.</p>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Dinner\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Dinner</h3>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193118 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTU0OjM1OQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Flora-Hall-Brewing-640x960.jpg\" alt=\"Flora Hall Brewing Ottawa for dinner\" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Flora-Hall-Brewing.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Taking a walk along Bank Street we made our way to Flora Hall Brewing for dinner. This is a fantastic setting in an old garage. The local craft brewery has an extensive menu from falafels and beet hummus to Burrata salads. It&rsquo;s all open-concept and you can see the vats of beer behind glass where they make their own microbrew. It&rsquo;s then back to the boat for cocktails on deck with the Ottawa lights glistening in the smooth waters of the canal.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Day_7_-_Ottawa_to_Long_Island_3-4_hours\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Day 7 &ndash; Ottawa To Long Island (3-4 Hours)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193119 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTU3OjkwNw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Cruising.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Cruising.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Cruising-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Cruising-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Cruising-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Cruising-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Downtown-Cruising-1536x1152.jpg 1536w\" alt=\"Ottawa Canal Views from Le Boat\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Downtown-Cruising.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Today it is time to say goodbye to Ottawa and start making your way back to Smiths Falls. You&rsquo;ll be backtracking so you&rsquo;ll be seeing a lot of the same scenery, but this is a fun time to stop at the places you may have missed. Since we b-lined it through Long Island from to Black Rapids on the way to Ottawa, we decided to go directly to stay overnight at Long Island on the way back.</p>\r\n<figure class=\"wp-block-image size-large\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193130 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTU5OjcxNA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Day-7-1600x1067.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Day-7.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Day-7-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Day-7-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Day-7-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Day-7-1536x1024.jpg 1536w\" alt=\"Ottawa Canal Cruise Day 7\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Day-7.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Long Island is a lovely lock station with three locks and a swing bridge. It&rsquo;s amazing to watch the Parks Canada staff at work. They crank the locks by hand and push the trolley to open the swing bridge in circles. It&rsquo;s truly like stepping back in time.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">From the Long Island Locks, it is a 15-minute bike ride to the town of Manotick. It&rsquo;s worth going into this historic town with 1860s Watson&rsquo;s Mill being the highlight. There are several cafes and specialty shops for shopping, or you can simply relax on one of its patios.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Day_8_-_Nicolson_Locks\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Day 8 &ndash; Nicolson Locks</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193125 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTYzOjgwOQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sunset-Final-Day.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sunset-Final-Day.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sunset-Final-Day-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sunset-Final-Day-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sunset-Final-Day-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Sunset-Final-Day-1536x1024.jpg 1536w\" alt=\"Rideau Canal Ottawa Final Day\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Sunset-Final-Day.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Today is a leisurely cruise as you make your way back along the Rideau Canal. It&rsquo;s impossible to get to Smiths Falls in a day from Long Island, so you can decide where you want to spend the night on the way back. You may want to revisit Merrickville to enjoy some delicious pizza at Iron Forge Pizza, or you may want a night on the water at Burrits Rapids or Nicholsons Lock. We made it through Burrits Rapids and decided to stay at Nicholsons to enjoy an evening in peace and quiet on Le Boat.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Using the barbecue on the upper deck, we enjoyed cocktails and dinner as the sun set over the water. It was the perfect ending to our last night on the boat.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Final_Day_-_On_to_Smiths_Falls\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Final Day &ndash; On To Smiths Falls</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193126 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTY3Ojc4OQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Smiths-Falls.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Smiths-Falls.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Smiths-Falls-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Smiths-Falls-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Smiths-Falls-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Smiths-Falls-1536x1024.jpg 1536w\" alt=\"Ridea Canal Fall colours Smiths Falls\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Smiths-Falls.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Our final push had us leaving first thing in the morning to get through to Smiths Falls where we backed the boat into the slip. We&rsquo;ve come a long way baby, 8 days ago we were nervous about driving Le Boat&rsquo;s Horizon Four and now we&rsquo;re backing it with precision right into the slip alongside the rest of the fleet.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">As one person went to get the carts to help carry our gear, the rest started to unload the boat and get it ready for sign-off. It was a wonderful week with family on the Le Boat Horizon Four and it was bittersweet saying goodbye to it. We highly recommend getting your family or close friends together to share this once-in-a-lifetime experience.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Le_Boat_and_Rideau_Canal_-_The_Deails\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Le Boat And Rideau Canal &ndash; The Deails</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193123 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTcxOjc1NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Details.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Details.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Details-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Details-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Details-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Details-1536x1024.jpg 1536w\" alt=\"Le Boat and Rideau Canal - The Deails\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Details.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Le Boat is located in Smiths Falls in the center of the Rideau Canal. Boat vacations can go either way along the canal from Smiths Falls and don&rsquo;t need to go all the way to Ottawa on your trip. We met many people who took shorter three to four-day trips to Merrickville or Burritts Rapids. You also don&rsquo;t need to spend two nights in Ottawa.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We wanted my family to experience our favourite city in Ottawa, so we chose to spend an extra night in the city, but you can choose to stay as long or as short as you like. The only extra cost for Le Boaters in Ottawa on the canal is the cost of power which is $9.20 per night.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Rideau_Canal_Hours\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Rideau Canal Hours</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-193102 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTc1OjcyMQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Hours.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Hours.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Hours-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Hours-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Hours-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Ottawa-Canal-Cruise-Hours-1536x1024.jpg 1536w\" alt=\"Rideau Canal Hours\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Ottawa-Canal-Cruise-Hours.jpg\" data-pin-description=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" data-pin-title=\"Ottawa Canal Cruise - Complete 7 Day Itinerary\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When planning your Le Boat route on the Rideau Canal, it&rsquo;s important to look at lock times. Le Boats can only go 10 km per hour, so you can&rsquo;t zip from lock to lock. It is meant to be a slow and relaxing boat rip on the Rideau. So, make sure to know how far you want to travel and how much you want to see before booking the number of days from Smiths Falls.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Getting to Ottawa in 3 days is doable, but it is important to make good time between locks and to plan if you are going to stay at the top or bottom of locks. Going through the locks can take a while and if you park at the top but want to move on in the morning, you won&rsquo;t be able to until the lock opens. We often went through the lock in the evening so that we could park at the bottom and be on our way first thing in the morning.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Lock times change depending on the time of the year and you need to be at the locks 1 1/2 hours before they close as it can take that long to get boats through. So if the locks close at 4:00 pm in the autumn, you will need to be going through by 2:30 at the latest.</p>', 15, 11, 0, 'published', '2022-03-13 07:53:02', '2022-03-13 07:53:02');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(16, '16 BEST CITIES IN CALIFORNIA', 'best-cities-in-california', 'The sunny skies of California have long attracted those in the U.S. and travelers around the world. It’s a state where modern cities mix with iconic national parks and famous coasts.', '<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">After visiting California multiple times it is difficult to put these cities in any particular order. We love the laid-back feel of places like Monterey but we also love the beautiful and interesting Spanish influences on display in Santa Barbara and San Diego. Ther is a reason California is one of our favorite places to visit in the United States. So, without any further adieu here are our choices for the best cities in California.</p>\r\n<h2 id=\"h-1-los-angeles\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"1_Los_Angeles\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>1. Los Angeles</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191881 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY0Ojc5MA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Los-Angeles.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Los-Angeles.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Los-Angeles-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Los-Angeles-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Los-Angeles-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Los-Angeles-1536x1024.jpg 1536w\" alt=\"Beuatifl California Cities Los Angeles\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Los-Angeles.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The city of Angels, where people come from far and wide to seek fame and fortune under the warm western sky. It&rsquo;s a city that blends new-world charm and modern skyscrapers with California&rsquo;s cool and beach bum culture.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Los Angeles is the biggest of the California cities on our list. In fact, it&rsquo;s the second-biggest metropolis in the US, based on population. It&rsquo;s home to around 3.8 million people who combine to make L.A. a must-stop on your travels.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Travelers will find an endless list of things to do, from chasing celebrities through Hollywood and Beverly Hills to high-end shopping on Rodeo Drive. Add in the nearby beaches, epic food truck scene, plenty of live sport and entertainment, and your itinerary will fill up quickly.</p>\r\n<div class=\"gb-container gb-container-79d806c4\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Read More About Los Angeles:</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-things-to-do-in-los-angeles/\">25 Best Things to Do in Los Angeles &ndash; Hollywood, Hikes, And More</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-los-angeles/\">Where to Stay in Los Angeles &ndash; 4 Hotels to Make You Feel Like a Star</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-2-san-francisco\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"2_San_Francisco\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>2. San Francisco</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191876 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODczOjc4Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Californai-San-Francisco.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Californai-San-Francisco.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Californai-San-Francisco-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Californai-San-Francisco-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Californai-San-Francisco-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Californai-San-Francisco-1536x1024.jpg 1536w\" alt=\"San Francisco Skyline\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Cities-in-Californai-San-Francisco.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">As you venture out of Los Angeles, it can often feel like a sprawling suburbia. You won&rsquo;t have that sensation in San Francisco. Its rolling hills simply won&rsquo;t let you. Across the 50-square mile city, there are 40 tumbling hills, some so steep they become their own attractions. But they&rsquo;re just one piece to explaining how beautiful the Golden State city is.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The city juts out into San Francisco Bay where you&rsquo;ll find rows of early Victorian-era homes. They were first constructed with a nod to the homes in London Row and, combined with the skyscrapers, have created a splendid skyline. But it&rsquo;s the local parks that lead to the water and the iconic Golden Gate Bridge that really showcase the city&rsquo;s splendor.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">From the Presidio of San Francisco, wander across the famous bridge for views of Alcatraz, San Pablo Bay and the Pacific.</p>\r\n<div class=\"gb-container gb-container-c8ba241c\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Plan your San Francisco Vacation with these Resources</span>:</p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-hikes-in-san-francisco/\">13 Great Hikes in San Francisco, California</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-san-francisco-accommodation-neighborhoods/\">Where to Stay in San Francisco &ndash; A Guide To The Best Neighborhoods</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/72-hours-in-san-francisco/\">72 Hours in San Francisco &ndash; A Local&rsquo;s Guide to the City</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/san-francisco-photography-guide/\">The Ultimate San Francisco Photography Guide</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-3-san-diego\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"3_San_Diego\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>3. San Diego</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191878 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODgyOjc2Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-San-Diego.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-San-Diego.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-San-Diego-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-San-Diego-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-San-Diego-768x513.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-San-Diego-1536x1025.jpg 1536w\" alt=\"San Diego Skyline sunset\" width=\"1600\" height=\"1068\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-San-Diego.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">San Diego is the younger brother that&rsquo;s now all grown up. Once in the shadow of the two aforementioned cities, San Diego has stepped into the light. It&rsquo;s slowly changed the once-solid tourist path that linked Los Angeles, San Francisco, Las Vegas, and the national parks.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">With beautiful weather year-round, San Diego is able to show off its diverse personality without taking a breather. Its compact downtown area is teeming with exciting bars, a happening live music scene, and the charming Gaslamp Quarter. Plus, their Taco Tuesdays are the best in the state.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Heading out of the inner-city, you&rsquo;ll have an embarrassment of local riches to choose from. For beaches, you&rsquo;ve got Coronado, Mission, Pacific, and Ocean to choose from. The latter is home to the best post-surf burgers on the planet at Hodads.</p>\r\n<div class=\"gb-container gb-container-85d53bd9\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Read More About San Diego:</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-things-to-do-in-san-diego/\">The Best Things to Do in San Diego &ndash; A Local&rsquo;s Guide to the City</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-san-diego-accommodation-neighborhoods/\">Where To Stay In San Diego &ndash; The Best Neighborhoods &amp; Areas</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-4-santa-barbara\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"4_Santa_Barbara\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>4. Santa Barbara</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191877 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODkxOjc4NQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Santa-Barbara.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Santa-Barbara.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Santa-Barbara-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Santa-Barbara-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Santa-Barbara-768x513.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Santa-Barbara-1536x1025.jpg 1536w\" alt=\"Santa Barbara Beach\" width=\"1600\" height=\"1068\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Santa-Barbara.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Santa Barbara is tucked away on the Pacific Coast, ninety minutes north of Los Angeles. Known as the&nbsp;<em style=\"box-sizing: inherit;\">Queen of Missions</em>, it just might be the most beautiful city in California.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The picturesque city was flatted after a major earthquake in 1925. It was painstakingly rebuilt with Spanish Colonial Revival architecture, heavily influenced by the city&rsquo;s most iconic landmark, the Mission Santa Barbara.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Its Spanish connection is visible throughout the state, but perhaps no more than in Santa Barbara. The mission was first built in 1820 and has been a part of local life ever since. Mission-style architecture can be found throughout town, including throughout the palm-lined downtown streets.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Santa Barbara is an easy city to explore on foot, with each attraction seemingly no further than a brief stroll away. With its location on the West Coast, visitors will also find miles of elegant beaches to complement the gorgeous city.</p>\r\n<div class=\"gb-container gb-container-6ce271a6\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Read More About California:</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-beaches-in-california-usa/\">24 Of The Best Beaches in California</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-hikes-in-california/\">15 Best Hikes in California &ndash; Inspiration to Get Outdoors</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-5-palm-springs\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"5_Palm_Springs\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>5. Palm Springs</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191882 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTAxOjc5Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palm-Springs.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palm-Springs.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palm-Springs-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palm-Springs-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palm-Springs-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palm-Springs-1536x1024.jpg 1536w\" alt=\"Cities in California Palm springs\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Palm-Springs.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Many of the best cities in California can be found along the coast. After all, when you picture the Golden State, the mind drifts to endless beaches and west-facing sunsets. But Palm Springs is not like any of the other cities.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">In the center of Southern California, Palm Springs was once the playground for the rich and famous. But over the years it&rsquo;s become the perfect winter escape for average Joe and Jane. Visitors will discover a town that effortlessly blends fashion and art with nature.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It&rsquo;s a city that promises to surprise you as you can easily spend the day at a resort pool party before checking out one of the many high-end local galleries and museums. To stretch your legs, embark on captivating hikes, or simply take the Aerial Tramway to epic mountaintop views.</p>\r\n<h2 id=\"h-6-san-jose\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"6_San_Jose\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>6. San Jose</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Many people may connect San Francisco with Silicon Valley. However, San Jose is the heart of the world&rsquo;s tech capital.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">San Jose is at the southern end of the Bay Area and connects the local community to an abundance of nearby outdoor adventures. A few minutes from town and you&rsquo;ll find yourself in Castle Rock State Park. After getting about on foot, freshen up and explore the local vineyard scene, which is a quiet rival to the famous Napa Valley.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Back in San Jose, check out the Historic Downtown District with old Italianate and Spanish architecture. Admire the beguiling facades before experiencing the eclectic international food scene. With dinner done, stick around for San Jose&rsquo;s burgeoning nightlife.</p>\r\n<h2 id=\"h-7-monterey\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"7_Monterey\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>7. Monterey</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191908 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTEwOjc1NA==-1\" class=\"nitro-lazy\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"data:image/svg+xml;nitro-empty-id=OTEwOjc1NA==-1;base64,PHN2ZyB2aWV3Qm94PSIwIDAgMTYwMCAxMDY3IiB3aWR0aD0iMTYwMCIgaGVpZ2h0PSIxMDY3IiB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciPjwvc3ZnPg==\" sizes=\"(max-width: 1600px) 100vw, 1600px\" alt=\"Cannery Row in Monterey\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Monterey.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Once the capital of Spanish California, Monterey is now a historic seaside city. It grew to become the center of the cannery industry, best described in Joseph Steinbeck&rsquo;s novel,&nbsp;<em style=\"box-sizing: inherit;\">Cannery Row.&nbsp;</em>Today, the row has been revitalized to become the center of Monterey&rsquo;s mouthwatering dining scene along the city&rsquo;s beautiful waterfront.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Monterey is a wonderful destination for families. You&rsquo;ll find a wide range of activities to pursue, from the outdoors and beaches to fascinating museums and the beloved aquarium.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Cannery Row, the Old Fisherman&rsquo;s Wharf, and 17-Mile Drive combine to make the scenic city incredibly romantic. You and your partner can spend the day kayaking around the bay before returning to the waterfront for golden hour with Monterey&rsquo;s twinkling lights splashing against the Pacific.</p>\r\n<div class=\"gb-container gb-container-7ea6c4a7\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover the Monterey Area:</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-monterey-california/\">20 Great Things to do in Monterey, California</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-carmel-by-the-sea/\">Things to do in Carmel by the Sea, California</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-8-sacramento\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"8_Sacramento\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>8. Sacramento</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191879 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTE5Ojc3NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Sacramento.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Sacramento.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Sacramento-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Sacramento-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Sacramento-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Sacramento-1536x1024.jpg 1536w\" alt=\"City of Sacramento California\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Sacramento.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Northeast of San Francisco is Sacramento, the capital of California. You may not have the beautiful west coast beaches. But what you will discover is a city teeming with history and culture.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The city is placed at the confluence of the Sacramento and American rivers. The latter stemming from nearby lakes and the Sierras. They combine to offer visitors some fun outdoor adventures, including kayaking and rafting. While a day at the lake, is the perfect way to relax and escape the bustling downtown.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Speaking of, Sacramento&rsquo;s inner city is packed with museums and renowned art galleries. But not to be missed is Old Sacramento, with several captivating buildings from the mid-1800s. Come for the sights and stay for the vibrant food scene.</p>\r\n<div class=\"gb-container gb-container-1a2074b5\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Read More About Sacramento:</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-sacramento/\">28 Things to do in Sacramento</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-9-san-luis-obispo\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"9_San_Luis_Obispo\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>9. San Luis Obispo</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">With a local population of under 50,000, San Luis Obispo may be small, but it remains one of the best cities in California. So come and discover why it&rsquo;s known as the&nbsp;<em style=\"box-sizing: inherit;\">happiest city in America.</em></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It&rsquo;s a place where residents are as welcoming as the eternal blue sky. The charming, leafy downtown streets are a pleasure to explore on foot. They lead you along rows of old Spanish architecture from the 19th century now home to modern restaurants. None better than those on Higuera Street, which traverses San Luis Obispo&rsquo;s beautiful waterfront.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Each weekend, you&rsquo;ll come across local markets that bring everyone together. While you&rsquo;re less than 30 minutes from the coast to one side and the mountains on the other.</p>\r\n<h2 id=\"h-10-long-beach\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"10_Long_Beach\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>10. Long Beach</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191880 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTMyOjg2MQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Long-Beach.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Long-Beach.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Long-Beach-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Long-Beach-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Long-Beach-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Long-Beach-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Long-Beach-1536x1152.jpg 1536w\" alt=\"The city of Long Beach California\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Long-Beach.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Long Beach is one of the most famous cities in the state. Here, residents get to enjoy the big city life while having all the benefits of a small beach town. No wonder it&rsquo;s called the&nbsp;<em style=\"box-sizing: inherit;\">beach with benefits.</em></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Thanks to its proximity to Los Angeles, it&rsquo;s easy to get to Long Beach. But don&rsquo;t worry about hiring a car as you can use the city&rsquo;s Passport bus service for a free way to get to the best local attractions. These include nature centers, Japanese gardens, multiple art museums, and the iconic ocean liner, Queen Mary.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This is all complemented by the fabulous waterfront and seemingly endless balmy weather. Go for a swim or sunbathe before checking out the happening nightlife. Read more about the&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-beaches-in-california-usa/\">24 Of The Best Beaches in California</a></p>\r\n<h2 id=\"h-11-oakland\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"11_Oakland\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>11. Oakland</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">On the other side of San Francisco Bay is Oakland. The city has followed a similar path to New York&rsquo;s Brooklyn and is the perfect complement to the high-brow city of San Francisco.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Hip and up-and-coming, it&rsquo;s a city with a genuine soul. Something you can sense as you wander around the city teeming with repurposed districts now packed with boutiques, restaurants, and artisanal wares.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Oakland has always been a melting pot. Its history plays out in its dining scene, where you&rsquo;ll find authentic dishes from a range of international cuisine. But as the city rises into a modern metropolis, its festival and events calendar continues to fill up. Visitors will find an exciting range of street fairs and community festivals that showcase the local culture.</p>\r\n<h2 id=\"h-12-napa\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"12_Napa\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>12. Napa</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191909 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQxOjMyNw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Napa-640x960.jpg\" alt=\"Wine region of Napa from above\" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Napa.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Oakland and San Fran are gateways to one of the US&rsquo; top wine regions. Napa is the chief city within the Napa Valley region, home to renowned vineyards that are equally pleasant on the eyes.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Napa&rsquo;s downtown has seen a resurgence over the last decade as more people move out of the major cities. Split by the beautiful Napa River, enjoy the local food scene, public markets, or even take a romantic gondola ride down the river.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Of course, the main attraction is the wineries. The Napa Valley has over 300 spread throughout Sonoma County. Take the Napa Valley Wine Train to experience the best local wines and gourmet food. All within a century-old railcar.</p>\r\n<h2 id=\"h-13-eureka\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"13_Eureka\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>13. Eureka</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Originally founded as a seaport in the 1850s, the well-preserved town of Eureka remains much as it was. Its Old Town is now on the National Register of Historic Places with rows of 19th-century buildings. Here you&rsquo;ll discover opulent Victorian mansions once home to rich lumber merchants. Some have even been transformed into adorable bed and breakfasts.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Wander along the historic streets admiring the exquisite facades now harboring cafes, galleries, and restaurants. From there, head to the Waterfront and explore the seaside boardwalk.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Eureka is a brief trip from the Oregon border in Northern California. This places the city in redwood country with beautiful forests enveloping the city to the east. While the quaint northern beaches are just a short walk away.</p>\r\n<h2 id=\"h-14-palo-alto\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"14_Palo_Alto\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>14. Palo Alto</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191883 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTUwOjc2Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palo-Alto.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palo-Alto.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palo-Alto-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palo-Alto-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palo-Alto-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Palo-Alto-1536x1024.jpg 1536w\" alt=\"Downtown Palo Alto California\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Palo-Alto.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Between San Fran and San Jose, Palo Alto is an innovative city home to one of California&rsquo;s leading universities. Palo Alto is a mix of old and new. It&rsquo;s home to historic buildings that have stood the test of time while being at the center of the modern world.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Some of these historic buildings are home to must-see attractions such as the Cantor Arts Center and the Museum of American Heritage. While exploring the Stanford University campus will give you a great example of Palo Alto&rsquo;s split personality.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The tree-lined downtown streets lead to over 30 public parks, none better than Rinconada Park. The city&rsquo;s pleasant year-round weather means there&rsquo;s no bad time to enjoy a picnic. From there, enjoy the great dining scene fueled by local students before exploring the Baylands Nature Preserve.</p>\r\n<h2 id=\"h-15-fresno\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"15_Fresno\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>15. Fresno</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Home to the fifth largest population in California, Fresno is the gateway to some of the state&rsquo;s best national parks. But the city itself is well worth a visit. It&rsquo;s a family-friendly destination with plenty of attractions to keep young and old entertained.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Fresno features a number of wonderful parks to enjoy, including Roeding and Woodward parks. The former is home to the city&rsquo;s Chaffee Zoo and features a sprawling lake within its 90 acres. Woodward Park is on the San Joaquin River and is home to the annual Shakespeare Festival.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You&rsquo;ll find live sporting events, including regular baseball and basketball, in town. But the real action lies east where you&rsquo;ll find Yosemite and the Sierra National Forest.</p>\r\n<h2 id=\"h-16-healdsburg\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"16_Healdsburg\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>16. Healdsburg</h2>\r\n<figure class=\"wp-block-image size-full\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191884 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTU5Ojc1OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Healdsburg.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Healdsburg.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Healdsburg-760x505.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Healdsburg-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Healdsburg-768x510.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-California-Healdsburg-1536x1020.jpg 1536w\" alt=\"Healdsburg in Sonoma county California\" width=\"1600\" height=\"1063\" data-pin-media=\"https://theplanetd.com/images/Cities-in-California-Healdsburg.jpg\" data-pin-description=\"16 Best Cities in California\" data-pin-title=\"16 Best Cities in California\" /></div>\r\n</figure>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Healdsburg is another city within Sonoma County that should be on your radar. The rolling hills and glistening lakes lead you to the charming town, a popular weekend destination for those in San Francisco.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Because of this, you&rsquo;ll find plenty of elegant bed and breakfasts. Plus cozy cafes and cute bakeries all within the heart of wine country. Healdsburg is bucolic but far from a sleepy small town. Just steps away from award-winning wineries, Healdsburg shares Napa Valley&rsquo;s love of great wine and mouthwatering food.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Food and drink aside, you&rsquo;ll find plenty of adventure on the local trails that take you into deep valleys and preserves. You can also paddle down the Russian River jumping between nearby beaches as you go.</p>', 15, 11, 0, 'published', '2022-03-13 07:55:14', '2022-03-13 07:55:14');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(17, 'CARIBBEAN FOOD: 20 DISHES YOU CAN TRY IN THE CARIBBEAN OR AT HOME', 'best-caribbean-food', 'The beautiful Caribbean region encompasses several island nations, including the Bahamas, Antigua and Barbuda, Grenada, Barbados, and Dominica (to name just a few).', '<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Caribbean foods are some of the unique dishes in the world, primarily consisting of fresh-caught seafood, local fruits, and other regional delights. If you&rsquo;re thinking of visiting the Caribbean soon, or you&rsquo;d like to prepare some Caribbean recipes at home, read on to learn about the most iconic dinners and desserts that hail from this tropical paradise.</p>\r\n<h2 id=\"h-1-baigan-choka-roasted-eggplant\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"1_Baigan_Choka_Roasted_Eggplant\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>1.&nbsp;Baigan Choka (Roasted Eggplant)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191991 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODYyOjc1OQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Baigan-Choka.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Baigan-Choka.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Baigan-Choka-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Baigan-Choka-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Baigan-Choka-768x513.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Baigan-Choka-1536x1025.jpg 1536w\" alt=\"Baigan Choca Foods of the Caribbean\" width=\"1600\" height=\"1068\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Baigan-Choka.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">There are many&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/10-great-things-to-do-in-the-caribbean/\" target=\"_blank\" rel=\"noreferrer noopener\">great things to do</a>&nbsp;in the Caribbean, and sitting down for a hot bowl of Baigan Choka might be one of them! This Trinidadian dish consists of&nbsp;roasted eggplant flavored with garlic, onion, and butter.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Due to its fire-roasted preparation and savory ingredients, Baigan Choka is an exceptionally filling dish. It&rsquo;s often paired with a local beer or glass of mauby.&nbsp;Baigan Choka is very similar to traditional Indian cuisine. That&rsquo;s because Trinidad has a large and historically prevalent Indian population.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Many of the Caribbean recipes you&rsquo;ll find in&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.britannica.com/place/Trinidad-and-Tobago/People\" target=\"_blank\" rel=\"noreferrer noopener\">Trinidad and Tobago</a>&nbsp;have uniquely Indian origins and feature curries, mashed vegetables, and flat roti or naan bread. This is an easy one to make at home&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.mydaintysoulcurry.com/baigan-choka/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">especially with this recipe!</a></p>\r\n<h2 id=\"h-2-conch-ceviche\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"2_Conch_Ceviche\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>2. Conch Ceviche</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-192000 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY3Ojc1NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-conch-Ceviche.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-conch-Ceviche.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-conch-Ceviche-760x484.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-conch-Ceviche-150x96.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-conch-Ceviche-768x489.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-conch-Ceviche-1536x978.jpg 1536w\" alt=\"Conch cevicehe in Bahamas\" width=\"1600\" height=\"1019\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-conch-Ceviche.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When eating in the Caribbean, you won&rsquo;t want to miss the opportunity to try fresh conch ceviche. This seafood-based dish is one of the most popular meals in the Bahamas, partially due to its bright colors but primarily&nbsp;because of its&nbsp;rich flavor.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Conch is a&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.britannica.com/animal/conch\" target=\"_blank\" rel=\"noreferrer noopener\">type of sea snail</a>&nbsp;that lives in pointed concentric shells. They&rsquo;re readily available along the warm-water coasts of the Bahamas but challenging to find in restaurants elsewhere.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This ceviche consists of a finely-chopped conch that&rsquo;s tossed in lime juice, salt, and a little bit of orange juice. It&rsquo;s also seasoned with chopped onions, tomatoes, and peppers.&nbsp;Every bite of conch ceviche is bound to transport you to a sunny white sand beach, with the sound of gentle waves caressing your eardrums.&nbsp;</p>\r\n<h2 id=\"h-3-flying-fish-and-cou-cou\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"3_Flying_Fish_and_Cou-Cou\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>3. Flying Fish And Cou-Cou</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191999 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODcyOjgwNw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Flying-Fish-Cou-Cou.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Flying-Fish-Cou-Cou.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Flying-Fish-Cou-Cou-760x504.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Flying-Fish-Cou-Cou-150x99.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Flying-Fish-Cou-Cou-768x509.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Flying-Fish-Cou-Cou-1536x1018.jpg 1536w\" alt=\"Flying Fish and Cou-Cou in Barbados\" width=\"1600\" height=\"1060\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Flying-Fish-Cou-Cou.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Did you know that Barbados is&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.nationalgeographic.co.uk/travel/2018/10/barbados-flying-fish-and-cou-cou\" target=\"_blank\" rel=\"noreferrer noopener\">home to flying fish</a>? These winged fish can leap from the water, making them look like aquatic dragons.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">These creatures are one of the most popular options for fish dinners in Barbados and are its national dish. Though&nbsp;flying fish are prepared in various ways, one of the most popular methods is frying them.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Fried flying fish is&nbsp;typically paired with Cou-Cou (also spelled Coucou), a side dish made of ground okra and cornmeal. With a consistency similar to grits, Cou-Cou makes the perfect bed for freshly-fried flying fish. That&rsquo;s because Cou-Cou can absorb all the flavors of the fish, including its oils. As such, not a single bit of food goes to waste!</p>\r\n<h2 id=\"h-4-pineapple-chow\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"4_Pineapple_Chow\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>4. Pineapple Chow</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Pineapple Chow is another Trinidadian dish you won&rsquo;t want to skip while visiting the&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/beautiful-caribbean-islands/\" target=\"_blank\" rel=\"noreferrer noopener\">beautiful Caribbean islands</a>. But, of course, this dish&rsquo;s ingredients and preparation are simple enough to ensure that&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.carolinescooking.com/pineapple-chow/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">you can make it from home!</a></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Essentially, Pineapple Chow is chopped chunks of pineapple, pineapple juice, chopped garlic cloves, and plenty of fresh cilantro leaves. These ingredients are tossed in a bit of salt, pepper, and lime juice to give them an extra kick.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Don&rsquo;t be surprised if restaurants offer a small bowl of Pineapple Chow&nbsp;before dinner, as this delectable dish is often served as an appetizer.</p>\r\n<h2 id=\"h-5-mofongo\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"5_Mofongo\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>5. Mofongo</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191993 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODgxOjcxMA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Mofongo.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Mofongo.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Mofongo-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Mofongo-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Mofongo-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Mofongo-1536x1024.jpg 1536w\" alt=\"Puerto Rican Mofongo \" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Mofongo.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When eating at Caribbean restaurants, there&rsquo;s a good chance that you&rsquo;ll see Mofongo on the menu. This Puerto Rican dish is made of mashed, fried green plantains seasoned with garlic.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The garlic-infused plantain mixture is also combined with fried pork skin to give it a unique texture and smoky pork flavor. It&rsquo;s formed into a small half-dome and often served over a bed of shrimp or beside a small bowl of fish broth.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Overall, Mofongo has a similar consistency to mashed potatoes, but its flavor is a unique blend of sweet and savory. Like many other Caribbean dishes, it&rsquo;s a perfect mixture of locally-available ingredients.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Check out more tasty ideas in our article on&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://theplanetd.com/popular-puerto-rican-dishes/\">The Best Puerto Rican Food!</a></span></p>\r\n<h2 id=\"h-6-gizzada\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"6_Gizzada\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>6. Gizzada</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When in Jamaica, do as Jamaicans do and enjoy some fresh coconut tarts, also called Gizzada. These tiny pinched sweets feature a buttery shortbread crust and a filling of&nbsp;finely chopped (or grated) coconut, brown sugar, and cinnamon.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Other spices, like nutmeg and vanilla, are also commonly added to give Gizzada its distinct flavor.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Each bite of Gizzada is a blend of tropical spice and sweet coconut. If you&rsquo;re feeling adventurous, you can try making this dish at home, as the filling doesn&rsquo;t need to be cooked or baked.</p>\r\n<h2 id=\"h-7-conch-fritters\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"7_Conch_Fritters\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>7. Conch Fritters</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191994 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODkxOjg2NQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Conch-Fritters.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Conch-Fritters.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Conch-Fritters-716x507.jpg 716w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Conch-Fritters-1507x1067.jpg 1507w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Conch-Fritters-150x106.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Conch-Fritters-768x544.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Conch-Fritters-1536x1088.jpg 1536w\" alt=\"Conch fritters a staple food of the Caribbean\" width=\"1600\" height=\"1133\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Conch-Fritters.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Conch is prevalent throughout the Caribbean and is far easier to catch than fish. As a result, conch meat makes several appearances throughout Caribbean cuisine.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">But while some dishes feature raw conch, (yes conch may be eaten raw) others deep fry these chopped-up gastropods, resulting in a savory snack that&rsquo;s often served in bars alongside rum-based beverages.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you&rsquo;re planning on heading out for a fun night in the Caribbean, be sure to order a small plate of conch fritters. These crispy conch bites can keep your hunger at bay while you enjoy the tropical drinks and soak up the local nightlife.</p>\r\n<h2 id=\"h-8-goat-water\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"8_Goat_Water\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>8. Goat Water</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The name of this dish might make you picture a tall glass of goat&rsquo;s milk or a suspicious bowl of goat-flavored water. But the truth of Goat Water (also called Mannish Water) is far more mouthwatering.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It&rsquo;s essentially a mutton stew featuring onions, carrots, and finely chopped peppers. And if you&rsquo;re planning on stopping by Montserrat, you&rsquo;ll find Goat Water on almost every menu!&nbsp;That&rsquo;s because Goat Water is the national dish of Montserrat! It&rsquo;s a flavorful representation of the nation&rsquo;s&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.irishtimes.com/life-and-style/generation-emigration/the-caribbean-irish-the-other-emerald-isle-1.2610681\" target=\"_blank\" rel=\"noreferrer noopener\">unique Irish history</a>&nbsp;and Caribbean location.&nbsp;</p>\r\n<h2 id=\"h-9-spiced-plantains\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"9_Spiced_Plantains\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>9. Spiced Plantains</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-large\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191998 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk5Ojc5Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Spiced-Plantains-1600x1067.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Spiced-Plantains.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Spiced-Plantains-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Spiced-Plantains-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Spiced-Plantains-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Spiced-Plantains-1536x1024.jpg 1536w\" alt=\"Spiced Plantains caribbean cuisine\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Spiced-Plantains.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Another simple snack readily available throughout the Caribbean are spiced plantains. That&rsquo;s because plantain trees are&nbsp;everywhere throughout the Caribbean islands.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Brought to the Islands by Indian immigrants, plantains are a staple crop and make up a hefty portion of the Caribbean diet.&nbsp;Spiced plantains are sliced into potato-chip-like rounds and deep-fried until golden. Though crisp on the outside, they feature a soft center that&rsquo;s slightly sweet. Often coated in onion and ginger powder, this meal is an ideal sweet and savory combination. These are also great snacks that&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.homemadezagat.com/2016/08/sweet-and-spicy-fried-plantain.html\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">you can make at home!</a></p>\r\n<h2 id=\"h-10-trinidad-doubles\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"10_Trinidad_Doubles\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>10. Trinidad Doubles</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When walking the boulevards of Trinidad, it&rsquo;s challenging to resist the savory scents wafting from streetside food carts. If you&rsquo;re unsure which popular&nbsp;street food to try, it might be wise to start with Trinidad Doubles.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Typically served in a small pouch of wax paper, Trinidad Doubles are small sandwiches made of two pieces of soft fried bread. These rounds protect a soft spiced chickpea filling that&rsquo;s flavored with garlic, salt, fresh herbs, and turmeric.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you&rsquo;re looking for a vegetarian alternative to fill your belly, you&rsquo;ll want to pick up a few handfuls of Trinidad Doubles soon after arriving.</p>\r\n<h2 id=\"h-11-pepper-pot\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"11_Pepper_Pot\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>11. Pepper Pot</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191996 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTA3OjczNQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Pepper-Pot.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Pepper-Pot.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Pepper-Pot-760x452.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Pepper-Pot-150x89.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Pepper-Pot-768x457.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Pepper-Pot-1536x914.jpg 1536w\" alt=\"Caribbean Guyanese Pepperpot\" width=\"1600\" height=\"952\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Pepper-Pot.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Pepper pot (sometimes spelled pepperpot) is a dish that&rsquo;s popular throughout the whole of the Caribbean. You can find it in Jamaica, Guyana, the Bahamas, and almost every island in the Caribbean Sea.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Interestingly, this meal has a U.S. equivalent, called&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.inquirer.com/food/philadelphia-pepper-pot-soup-stew-history-20200212.html\" target=\"_blank\" rel=\"noreferrer noopener\">Philadelphia Pepper Pot</a>. This American version comes directly from Caribbean immigrants and enslaved people who lived in Philadelphia during the late 1700s and early 1800s.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">One of the reasons why Pepper Pot is such a popular dish is because it&rsquo;s one of the most savory and filling Caribbean dinners. Made of sweet potatoes, cubed beef, onions, and garlic, it packs plenty of protein and nutrients into a delicious hearty stew. Though the Caribbean rarely experiences cold temperatures, you can&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.africanbites.com/pepperpot/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">prepare Pepper Pot at home</a>&nbsp;when the weather is chilly to warm your body and transport your palate to tropical Caribbean climes.</p>\r\n<h2 id=\"h-12-black-cake\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"12_Black_Cake\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>12. Black Cake</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Though white-floured angel cake might be a popular dessert in European countries, Black Cake is the go-to cake for those in the Caribbean.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It&rsquo;s far denser than cakes served in the U.S. and part of Europe, and it&rsquo;s also soaked in rum! Made of ground fruits like cherries, currants, and raisins, this cake has a naturally sweet flavor accentuated by its rum dressing.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Still, if you plan on enjoying dessert while&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/destinations/caribbean/caribbean-travel-tips/\" target=\"_blank\" rel=\"noreferrer noopener\">visiting the Caribbean</a>, be sure to order a small piece. Because this cake is far thicker than the fluffier cakes found in the U.S., a few bites are all it takes to fill you up.</p>\r\n<h2 id=\"h-13-trinidadian-curry-chicken\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"13_Trinidadian_Curry_Chicken\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>13.&nbsp;Trinidadian Curry Chicken</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-192002 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTE2Ojc0Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Trinidadian-Curry-Chicken.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Trinidadian-Curry-Chicken.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Trinidadian-Curry-Chicken-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Trinidadian-Curry-Chicken-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Trinidadian-Curry-Chicken-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Trinidadian-Curry-Chicken-1536x1024.jpg 1536w\" alt=\"Trinidadian Curry Chicken caribbean cuisine\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Trinidadian-Curry-Chicken.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Trinidadian curry chicken is a mouthwatering meal with notable Indian influences. It&rsquo;s often served with a hefty portion of basmati rice, completing the dish&rsquo;s distinctly Indian look and flavor profile.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Made with onions, garlic, potatoes, curry powder, and cilantro paste, the Trinidadian version has a slightly green hue that might be somewhat off-putting if you&rsquo;re unfamiliar with Indian cuisine.&nbsp;We suggest adding a little coconut milk to make it a little creamier. But you&rsquo;ll be glad to you ordered this dish after you take your first bite of tender, flavorful curried chicken.</p>\r\n<h2 id=\"h-14-la-bandera-national-dish\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"14_La_Bandera_national_Dish\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>14. La Bandera (National Dish)</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">La Bandera is one of the most popular dishes in the Dominican Republic. Its literal translation is &ldquo;the flag&rdquo; because it resembles the Dominican Republic&rsquo;s flag, so it is no surprise that it is the country&rsquo;s national dish.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you have a robust appetite, you&rsquo;ll love La Bandera. It features a bed of rice topped with seasoned kidney beans, sliced tomatoes, and a spiced side of meat.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The precise type of meat included in this dish varies, but the most popular options are often chicken or beef. The final result is a full plate that&rsquo;s lovingly arranged to resemble the nation&rsquo;s flag.</p>\r\n<h2 id=\"h-15-chapea\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"15_Chapea\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>15. Chapea</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Caribbean is home to several seafood-based and meat-based dishes. As a result, vegetarian visitors may not be sure which meals to try while visiting. Fortunately, Chapea is widely available and entirely meat-free.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Originating from the Dominican Republic, Chapea is a vegetable stew made of rice, onions, carrots, and cabbage. It also incorporates crushed tomatoes and butternut squash, and pinto beans. After chowing down on a bowl of Chapea, you&rsquo;ll have enough energy to take on the day with confidence.</p>\r\n<h2 id=\"h-16-callaloo\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"16_Callaloo\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>16.&nbsp;Callaloo</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191997 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTI3OjcyOQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Callaloo.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Callaloo.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Callaloo-760x505.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Callaloo-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Callaloo-768x511.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Callaloo-1536x1021.jpg 1536w\" alt=\"Jamaican Callaloo Caribbean Foods\" width=\"1600\" height=\"1064\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Callaloo.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Jamaican Callaloo is a vegetarian dish of callaloo (a leafy green similar to collard greens), Scotch bonnet peppers, onions, and garlic. But don&rsquo;t let these simple ingredients fool you!</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Each bite of tender Jamaican Callaloo is bursting with flavor, partially thanks to the addition of tomatoes and fresh thyme leaves.&nbsp; To make Jamaican Callaloo, you&rsquo;ll simply&nbsp;chop the ingredients, wash them, and add them to a hot skillet. After stirring these components for about ten minutes, you&rsquo;ll be ready to plate and serve.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Because this dish doesn&rsquo;t require a ton of preparation and cooks quickly, it&rsquo;s a fantastic Caribbean dish to try when you&rsquo;re short on time. Of course, getting your hands on callaloo might be challenging, but you can substitute kale or collard greens in a pinch.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more delicious Jamaican Cuisine in our article&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://theplanetd.com/jamaican-food-traditional-dishes/\">Jamaican Food &ndash; 20 Traditional Dishes You Have to Try</a></span></p>\r\n<h2 id=\"h-17-tchaka\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"17_Tchaka\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>17. Tchaka</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The island of Haiti also&nbsp;lies in the Caribbean Sea, and its most famous dish might be Tchaka, a stew made of corn, red beans, and pig feet. As such, this dish is one of the most memorable meals you can experience when eating Caribbean dinners.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Seasoned with orange juice and bay leaves, the flavor of this dish is challenging to describe. This Haitian food is both savory and bitter, with hints of tropical flavors combined with earthy ingredients.</p>\r\n<h2 id=\"h-18-jerk-chicken\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"18_Jerk_Chicken\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>18. Jerk Chicken</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-192001 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTM2OjgyNw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Jerk-Chicken.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Jerk-Chicken.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Jerk-Chicken-590x507.jpg 590w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Jerk-Chicken-1242x1067.jpg 1242w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Jerk-Chicken-150x129.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Jerk-Chicken-768x660.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Jerk-Chicken-1536x1320.jpg 1536w\" alt=\"Jerk Chicken in Jamaica\" width=\"1600\" height=\"1375\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Jerk-Chicken.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Jamaican cuisine is typically hearty, filling, and spicy. These qualities are all hallmarks of one of the most popular Jamaican dishes, jerk chicken.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Jerk chicken is a type of grilled chicken that&rsquo;s marinated in a thick mixture of spices, including onions, peppers, cloves, and chiles. These seasonings imbue the chicken with the most savory flavors of the Caribbean. It is usually served with a side of rice and peas.</p>\r\n<h2 id=\"h-19-creole-bread\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"19_Creole_Bread\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>19. Creole Bread</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Sometimes, you want a simple snack that&rsquo;s filling and tasty. If you find yourself yearning for a sweet and straightforward snack, you&rsquo;ll want to try Creole Bread.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">These fluffy buns are made with coconut milk and coconut oil, imbuing it with a distinctly tropical flavor profile that can brighten your day and wake up your tastebuds. Though these rolls are most popular in Jamaica and the Bahamas, you&rsquo;ll likely find them available from street vendors throughout the Caribbean.</p>\r\n<h2 id=\"h-20-tamarind-balls\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"20_Tamarind_Balls\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>20. Tamarind Balls</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-large\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191992 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQzOjc4MQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Tamarind-Balls-1600x1067.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Tamarind-Balls.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Tamarind-Balls-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Tamarind-Balls-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Tamarind-Balls-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Caribbean-Foods-Tamarind-Balls-1536x1024.jpg 1536w\" alt=\"Tamarind Balls Caribbean Dessert\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Caribbean-Foods-Tamarind-Balls.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Tamarind Balls look like tiny donut holes, and they&rsquo;re one of the most popular Caribbean dessert options. As you might suspect, this sweet treat is made using tamarind, a peanut-like fruit of the tamarind tree (Tamarindus indica).&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Tamarind trees made their way to Barbados several centuries ago, like&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://socanth.cofc.edu/documents/Barbados_31st%20Annual%20Meeting.pdf\" target=\"_blank\" rel=\"noreferrer noopener\">the African baobab tree</a>. Since that time, they&rsquo;ve become a staple crop of Barbados, the Bahamas, and Jamaica.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Much like with bananas, the taste of tamarind depends on its ripeness. That said, tamarind is typically both sweet and bitter.&nbsp;For that reason, tamarind is often combined with sugar and coconut to diminish its sourness and highlight its natural sweetness. Tamarind balls are an excellent example of this practice.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">To make tamarind balls, you&rsquo;ll remove the shells covering the sweet tamarind, cover the dark fruit with sugar and coconut, then mush the fruit together to form small balls.&nbsp;You&rsquo;ll then roll each ball across a shallow plate of sugar, giving each one a sugared coating. After that, they&rsquo;re ready to enjoy!</p>\r\n<h2 id=\"h-try-caribbean-food-today\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Try_Caribbean_Food_Today\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Try Caribbean Food Today</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-large\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191990 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQ5Ojc4Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Best-Caribbean-Foods-1500x1067.jpg\" sizes=\"(max-width: 1500px) 100vw, 1500px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Best-Caribbean-Foods-1500x1067.jpg 1500w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Best-Caribbean-Foods-713x507.jpg 713w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Best-Caribbean-Foods-150x107.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Best-Caribbean-Foods-768x546.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Best-Caribbean-Foods-1536x1092.jpg 1536w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Best-Caribbean-Foods.jpg 1600w\" alt=\"Caribbean Foods you have to try\" width=\"1500\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Best-Caribbean-Foods.jpg\" data-pin-description=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" data-pin-title=\"Caribbean Food: 20 Dishes You Can Try in the Caribbean or At Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Caribbean dishes are diverse and often feature European, Indian, and South American influences. But&nbsp;Caribbean food&nbsp;is also uniquely local, consisting of ingredients grown or harvested throughout the islands.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">While you could try Caribbean food by visiting a local restaurant specializing in these traditional dishes, visiting the Caribbean is the best way to enjoy an authentic taste. No matter your specific destination, you&rsquo;re bound to enjoy the fresh-caught fish and sweet fruits so prevalent in this region.</p>', 17, 11, 0, 'published', '2022-03-13 07:57:10', '2022-03-13 07:57:10');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(18, 'TIPS TO VISIT ANNE FRANK HOUSE, AMSTERDAM', 'anne-frank-house-amsterdam', 'A visit to Anne Frank House is usually at the top of people’s list when traveling to Amsterdam.', '<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Since its initial opening in 1960, The Anne Frank House has been attracting more than a million visitors each year. While the Anne Frank House Museum is very busy, the house is a moving space and one that is worth the visit. We visited Anne Frank House on our first visit to Amsterdam years ago and it has always stayed with us.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Since it had been a while, we enlisted the help of KT Browne to update the details of how to enter, and what to expect during your visit to the Anne Frank House Museum.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191929 lazyloading\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODYyOjcwOA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-huis-museum.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-huis-museum.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-huis-museum-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-huis-museum-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-huis-museum-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-huis-museum-1536x1024.jpg 1536w\" alt=\"anne frank house amsterdam\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/anne-frank-huis-museum.jpg\" data-pin-description=\"Tips to Visit Anne Frank House, Amsterdam\" data-pin-title=\"Tips to Visit Anne Frank House, Amsterdam\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Anne Frank House Amsterdam</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The annex, still visible today, was hidden from view by nearby houses during the war, which made it the perfect hiding place. The house itself was used by Otto Frank to run his workshop which he rented from the Pieron Family. The ground and first floor were used for his business and the rest was used as offices space and storage.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When things became too dangerous, the Frank family used the second and third floors to go into hiding. Business continued as usual on the ground and first floors and the only access to their hiding place was through the bookcase. The unusual setup of the house, made it so that nobody took notice of what went on behind the bookcase in the secret annex of Prinsengracht 263.</p>\r\n<h2 id=\"h-tips-for-visiting-anne-frank-house\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"Tips_for_visiting_Anne_Frank_House\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Tips For Visiting Anne Frank House</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Located right at Prinsengracht 263 in Amsterdam on the Prinsengracht Canal, the inhabited rooms of the Anne Frank House are only&nbsp;500 square feet in total. It consists of the main house and the hidden annex, which is where Anne Frank went into hiding and wrote her famous, beloved diary during World War II.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">There are numerous exhibition spaces throughout the museum that show various pages from her notebook, a wide array of artifacts, bookcases, and former living spaces.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-large\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-191935 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY4OjcyMQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Anne-Frank-Museum-entrance-1600x1067.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Anne-Frank-Museum-entrance.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Anne-Frank-Museum-entrance-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Anne-Frank-Museum-entrance-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Anne-Frank-Museum-entrance-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Anne-Frank-Museum-entrance-1536x1024.jpg 1536w\" alt=\"\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Anne-Frank-Museum-entrance.jpg\" data-pin-description=\"Tips to Visit Anne Frank House, Amsterdam\" data-pin-title=\"Tips to Visit Anne Frank House, Amsterdam\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Queues at Anne Frank Museum Entrance &ndash; Prinsengracht 263</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Tickets to The Anne Frank House are&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">only sold online&nbsp;</span>and allocated for specific time slots&mdash;so be sure to show up on time! Because the house is so popular, crowds are common at the Anne Frank House Museum entrance, but they do seem to disappear once inside thanks to the time slots, along with a quiet and calm atmosphere. Purchase your tickets to Anne Frank House<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.annefrank.org/en/museum/tickets/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">&nbsp;in advance here.</a></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you cannot make your time and tickets are already booked, they do not reschedule or offer refunds, so make sure you will be going on the day you plan for. They used to allot 20% of tickets to be sold on the same day, but that option is no longer available.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Once inside, there are no guided tours offered at Anne Frank House, but there is a free audio tour. It certainly deepens the experience, so we highly recommend it.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Photography is not allowed inside the museum in order to preserve the quality of the artifacts. Leave your camera at home. Also, since there are many narrow stairwells, the house isn&rsquo;t recommended for people with mobility issues.</p>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Introductory_Program\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Introductory Program</h3>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Before visiting Anne Frank House make sure to read the Diary of Anne Frank. It will give you a deeper understanding of the experience. You can&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://amzn.to/2TDLZc0\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">purchase it here on Amazon&nbsp;</a>for Kindle, audiobook, hardcover or paperback.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you haven&rsquo;t read the book, you can purchase a 30-minute introductory program that you can do before your visit. You will learn of the history of Anne Frank and about the persecution of Jews during the Second World War. It will give you a better understanding to help prepare you for your visit.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Above all, visit The Anne Frank House with an open heart and mind; it&rsquo;s a little slice of a very important part of history that we all would benefit from knowing more about.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"What_to_Expect_in_Anne_Frank_House\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>What To Expect In Anne Frank House</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-93641 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODc4OjMyMg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-house_Bookcase-640x960.jpg\" alt=\"anne frank house amsterdam\" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/anne-frank-house_Bookcase.jpg\" data-pin-description=\"Tips to Visit Anne Frank House, Amsterdam\" data-pin-title=\"Tips to Visit Anne Frank House, Amsterdam\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Entrance to Secret Annex at Anne Frank House Museum</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Visitors have the chance to wander through the museum&rsquo;s many rooms, nooks, and crannies to get a real sense of Anne Frank&rsquo;s experience. Through quotes, photos, film images, and a wide range of original items (including her beloved diary), Anne Frank is brought to life in an authentic yet respectful way. It&rsquo;s an experience that shouldn&rsquo;t be missed.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The house&rsquo;s steep stairwells and original artifacts are incredibly moving, a walk through Anne Frank House pulls you back in time.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Visitors can wander freely throughout Anne Frank House, so be sure to take as much in as you can. Also, don&rsquo;t miss the hinged bookcase and the entrance to the secret annex behind it&mdash;it&rsquo;s extraordinary.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The main exhibition space is a thoughtful, rich tribute to the persecution and discrimination of Anne and thousands of Jews faced during the war. Much of the Anne Frank House museum is perfectly preserved, making the experience of visiting incredibly authentic, if not slightly haunting.</p>\r\n<h3 id=\"h-about-anne-frank\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"About_Anne_Frank\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>About Anne Frank</h3>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\"><img id=\"ODg0OjY2Mg==-1\" class=\"wp-image-93640 lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-547x627.jpg\" sizes=\"(max-width: 547px) 100vw, 547px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-547x627.jpg 547w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-150x172.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-255x292.jpg 255w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-768x880.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank-600x688.jpg 600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/anne-frank.jpg 898w\" alt=\"anne frank diary\" width=\"547\" height=\"627\" data-pin-media=\"https://theplanetd.com/images/anne-frank.jpg\" data-pin-nopin=\"true\" />\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Anne Frank</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Anne Frank was a young Jewish girl, who hid from the Nazis with her family and four other people in the &ldquo;secret annex&rdquo; of this 17th-century canal house during World War II. Anne remained hidden in the annex for two years and one month until the Nazi authorities raided the space, arrested her and others she was in hiding with. They deported them to concentration camps which ultimately led to her death where she died of Typhus Fever at the age of 15.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Only Anne&rsquo;s father Otto survived the concentration camps. It was recently discovered that Arnold van den Bergh, a Jewish figure in Amsterdam betrayed Anne Frank&rsquo;s family to save his own. After 70 years of speculation, a team of investigators finally put the pieces of the puzzle together.&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.bbc.com/news/world-europe-60024228\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">You can read more details here.</a></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">In 1947, Anne posthumously became world-famous because of the diary she wrote while in hiding for two years. Her diary along with hundreds of loose pages chronicled her life in poetic detail.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Diary of Anne Frank is a detailed account of daily events, along with her fears, hopes, and dreams, and has come to be loved by millions around the world not only because of the acute insight it offers about the nature of man but because it&rsquo;s beautifully written.&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.amazon.com/gp/product/0553296981/ref=as_li_tl?ie=UTF8&amp;camp=1789&amp;creative=9325&amp;creativeASIN=0553296981&amp;linkCode=as2&amp;tag=tpld-20&amp;linkId=fafe2959d46b767f66c9734253ec7641\" target=\"_blank\" rel=\"noreferrer noopener\">Read it now and order on Amazon</a></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you are visiting Amsterdam, save these Anne Frank House tips to Pinterest for future travel planning.</p>', 12, 11, 0, 'published', '2022-03-13 08:02:52', '2022-03-13 08:02:52');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(19, '25 BEST THINGS TO DO IN DUBAI', 'best-things-to-do-in-dubai', 'Looking for thing things to do in Dubai? Beyond the skyscrapers and shopping malls, there are many Dubai attractions that will surprise you.', '<h2 id=\"h-1-go-up-the-burj-khalifa\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\">1. Go Up The Burj Khalifa</h2>\r\n<div class=\"wp-block-image size-full wp-image-78654\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189002 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY2OjEwMzU=-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-640x960.jpg\" sizes=\"(max-width: 640px) 100vw, 640px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-640x960.jpg 640w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-338x507.jpg 338w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-712x1067.jpg 712w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-100x150.jpg 100w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-768x1152.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-1024x1536.jpg 1024w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-150x225.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline.jpg 1067w\" alt=\"things to do in Dubai  burj khalifa\" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-skyline.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">View of the Burj Khalifa from Shangri-La Hotel</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Whenever we have the chance to go somewhere high, we take it and you cannot miss going up the world&rsquo;s tallest building for a view of the city. The Burj Khalifa is at the top of all Dubai attractions. It is located in the heart of the city with gorgeous views. Take the high-speed elevator up 160 stories to see awe-inspiring views of Dubai. Be warned though, it&rsquo;s crowded and once you&rsquo;ve had a look at the city, there&rsquo;s not a lot more to do up there.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189003 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY4Ojc3OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-2.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-2.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-2-760x428.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-2-150x84.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-2-768x432.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-skyline-2-1536x864.jpg 1536w\" alt=\"best things to do in Dubai city view\" width=\"1600\" height=\"900\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-skyline-2.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">View from the Burj Khalifa</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It is that famous building that Tom Cruise climbed during Mission Impossible and while standing outside the viewing platform, you gain a lot of respect for the man. That takes a lot of nerve. While the experience isn&rsquo;t as good as other high-rise attractions we&rsquo;ve taken around the world, you can&rsquo;t visit Dubai without going up the elevator. Get your skip-the-line tickets to the 124th, 125th, and 148th floors of the Burj Khalifa. Zip past the security lines for optimal timing. This tour offers easy cancellation within 24 hours of departure.&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-burj-khalifa\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">Purchase refundable tickets here.</a></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Burj Khalifa doesn&rsquo;t have the wow factor of&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/cn-tower-edgewalk-taking-on-the-worlds-highest-sky-walk/\" rel=\"noopener\">Toronto&rsquo;s CN Tower</a>&nbsp;or even the&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-things-to-do-in-calgary-alberta/\">Calgary Tower glass floor</a>&nbsp;but if there is one thing to do in Dubai it is ticking off the bucket list of going up the world&rsquo;s tallest building.</p>\r\n<h2 id=\"h-2-explore-the-dubai-marina\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"2_Explore_the_Dubai_Marina\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>2. Explore The Dubai Marina</h2>\r\n<div class=\"wp-block-image wp-image-78651 size-full\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189006 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODcyOjc4NQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-marina-1.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-marina-1.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-marina-1-760x428.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-marina-1-150x84.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-marina-1-768x432.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-marina-1-1536x864.jpg 1536w\" alt=\"dubai things to do marina\" width=\"1600\" height=\"900\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-marina-1.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">The Marina is great for walking and quieter moments</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Downtown Dubai isn&rsquo;t much of a walking city, but when you make your way to the Dubai Marina, it&rsquo;s your chance to stroll along the promenade. There are coffee shops and restaurants surrounding the second-largest man-made marina in the world where you can book boat tours and dinner cruises from here. It was definitely our favourite place to hang out in the evening in Dubai.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Like all things in Dubai, it too is surrounded by mammoth skyscrapers and shopping malls that take your breath away. For one of the more thrilling things to do in Dubai, make your way to the Amwaj Towers to take a zip line over the marina to the Dubai Marina Mall. Dubai does everything bigger and better and this zipline is one of the steepest and fastest in the world. Reaching speeds of 80 km an hour, you&rsquo;ll soar through the air for one kilometer.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-yacht-tour\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"This breakfast yacht tour (opens in a new tab)\">This breakfast yacht tour</a>&nbsp;is a great way to experience the decadence of travel in Dubai at an affordable price where you&rsquo;ll enjoy a morning cruise with breakfast. (also available for a sunset bbq cruise). You&rsquo;ll sail from the marina to Blue Water Island, the Dubai Eye, and views of Jumeira Beach, The Palm Jumeirah, and the Burj Al Arab.</p>\r\n<h2 id=\"h-3-go-to-the-beach\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"3_Go_to_the_Beach\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>3. Go To The Beach&nbsp;</h2>\r\n<div class=\"wp-block-image wp-image-78653 size-full\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189025 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODc3OjgxMA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-beach-view.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-beach-view.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-beach-view-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-beach-view-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-beach-view-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-beach-view-1536x1024.jpg 1536w\" alt=\"What to do in Dubai Burj Al Arab \" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-beach-view.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">The Burj Al Arab &ndash; 7 Star Hotel in Dubai</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Dubai has fabulous sandy beaches. Like all beach destinations, there are a lot of watersports to choose from and many beach chairs or free patches up sand to set up shop for the day to catch some rays. The famous Burj Al Arab towers over a public beach. If you are rich enough to stay there, go for it! It&rsquo;s Dubai&rsquo;s most luxurious accommodation being a 7-star hotel. You can actually book a&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-burj-al-arab\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"stay at this hotel on TripAdvisor (opens in a new tab)\">stay at this hotel on TripAdvisor</a>. It is cheaper than you think!</p>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Habtoor_Grand_Resort\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Habtoor Grand Resort</h3>\r\n<div class=\"wp-block-image wp-image-78661\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189094 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODgwOjgzMg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Harbtoor-Grand.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Harbtoor-Grand.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Harbtoor-Grand-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Harbtoor-Grand-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Harbtoor-Grand-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Harbtoor-Grand-1536x1024.jpg 1536w\" alt=\"where to stay in Dubai Harbtoor Grand\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Harbtoor-Grand.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">The view from Habtoor Grand</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We booked a hotel directly on the beach and quickly learned that Dubai is an amazing beach vacation. Located on the Persian Gulf the waters are warm and welcoming. The&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/habtoorgranddubai\" target=\"_blank\" rel=\"nofollow noopener\">Habtoor Grand Resort</a>&nbsp;Autograph Collections has a private beach and is within walking distance to the Dubai Marina and The Walk at Jumeirah Beach Residence (JBR). This hotel has a private beach, infinity pool, and plenty of amenities, but there is plenty of public access as well.&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">See rates for this resort on&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://theplanetd.com/habtoorgranddubai\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">TripAdvisor.</a></span></p>\r\n<h2 id=\"h-4-explore-the-dubai-mall\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"4_Explore_the_Dubai_mall\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>4. Explore The Dubai Mall</h2>\r\n<div class=\"wp-block-image size-full wp-image-78655\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189005 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODgzOjgxNA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-more-malls.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-more-malls.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-more-malls-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-more-malls-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-more-malls-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-more-malls-1536x1024.jpg 1536w\" alt=\"places to visit in dubai - dubai mall\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-more-malls.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">The Dubai Mall is probably the most famous thing to do in Dubai</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">I know we didn&rsquo;t rave about the malls in our opening paragraph, but it is a spectacle to see them. In fact, the malls are definitely the top Dubai attractions to add to your list. The Dubai Mall is the largest shopping mall in the world with so much more than clothing stores and boutiques. The Dubai Mall houses the Dubai Aquarium and Underwater Zoo&nbsp;where you can even go diving with sharks, there&rsquo;s a skating rink, waterfalls, and top-name brand clothing outlets where you can get lost for the day. You can even have a shark or maYou can even take a gondola ride around Dubai lake just like you are transported to Venice.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"5_Mall_of_the_Emirates\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>5. Mall Of The Emirates</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189113 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODg2Ojg5OQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Emirates-Mall.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Emirates-Mall.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Emirates-Mall-758x507.jpg 758w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Emirates-Mall-1596x1067.jpg 1596w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Emirates-Mall-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Emirates-Mall-768x514.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Emirates-Mall-1536x1027.jpg 1536w\" alt=\"Top things to do in Dubai Emirates Mall\" width=\"1600\" height=\"1070\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Emirates-Mall.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you go to the Mall of the Emirates, you can go snowboarding or skiing at Ski Dubai, catch a movie or do some shopping. There are malls galore in Dubai and many hotels offer free shuttles to visit them where you can freely spend your hard-earned money. One of the coolest things we saw before going to Dubai was Ski Dubai &ndash; an indoor ski resort. Coming from Canada, it is totally a novelty to be able to ski indoors.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189102 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODg4Ojc1Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Top-Things-to-do-in-Dubai-night.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Top-Things-to-do-in-Dubai-night.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Top-Things-to-do-in-Dubai-night-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Top-Things-to-do-in-Dubai-night-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Top-Things-to-do-in-Dubai-night-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Top-Things-to-do-in-Dubai-night-1536x1024.jpg 1536w\" alt=\"Dubai Malls\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Top-Things-to-do-in-Dubai-night.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Dubai takes everything to the next level and the malls of Dubai have everything you could possibly want in a holiday destination. From skiing to ice skating, water parks, and even scuba diving in a massive fish tank, you name it, Dubai has thought of it. You never have to leave the mall! Plus, you can relax in air conditioning to escape the Dubai heat as the desert temperatures soar outside.</p>\r\n<h2 id=\"h-6-dubai-fountain-show\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"6_Dubai_Fountain_Show\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>6. Dubai Fountain Show</h2>\r\n<div class=\"wp-block-image size-full wp-image-78646\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189103 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODkxOjgzNA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Fountain-Show.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Fountain-Show.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Fountain-Show-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Fountain-Show-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Fountain-Show-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Fountain-Show-1536x1024.jpg 1536w\" alt=\"things to see in dubai fountain show\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Fountain-Show.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">The water show of Dubai</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Just like the Bellagio in&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/off-the-strip-things-to-do-outside-las-vegas/\">Vegas,&nbsp;</a>Dubai has its own waterfall show at the Dubai Fountain. Only the fountain show in Dubai is bigger and better making it the top of all fountain shows in the world. Make sure to add going to the Dubai fountain to your list of must-see Dubai attractions.</p>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189007 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODkzOjc2Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-lights.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-lights.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-lights-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-lights-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-lights-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-lights-1536x1024.jpg 1536w\" alt=\"what to do in Dubai Fountain show near Burj\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-lights.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Each night a display of water and light runs for 10 minutes every half an hour until midnight. The songs, light show, and choreography change again and again so you never have to see the same show twice. Pick a seat and have a drink at one of the restaurants on the lake where the show is located or get there 10 minutes early for a front-row premium spot right at the fence. I really enjoyed the show myself.</p>\r\n<h2 id=\"h-7-take-a-scenic-flight\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"7_Take_a_Scenic_Flight\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>7. Take A Scenic Flight</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-188999 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk2Ojc4Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-waterfront.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-waterfront.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-waterfront-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-waterfront-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-waterfront-768x513.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-waterfront-1536x1025.jpg 1536w\" alt=\"things to do in dubai scenic flight\" width=\"1600\" height=\"1068\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-waterfront.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Burj Al Arab from Above</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">A scenic flight also offers amazing views of the Dubai skyline and the Persian Gulf coast. We found that Dubai was so spread out it was difficult to really take in the views of the skyscrapers from different vantage points, but a flight fixed that problem. Being on a scenic flight allowed to see all of the magnificent modern architecture in the city.&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">Watch or&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://www.youtube.com/watch?v=7uLnwmpSw4o\" target=\"_blank\" rel=\"noopener\">Scenic flight of Dubai on YouTube</a></span></p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"8_Palm_Jumeirah\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>8. Palm Jumeirah</h2>\r\n<div class=\"wp-block-image size-full wp-image-78649\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189095 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk5OjgzMg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Palm-Jumeirah.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Palm-Jumeirah.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Palm-Jumeirah-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Palm-Jumeirah-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Palm-Jumeirah-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Palm-Jumeirah-1536x1024.jpg 1536w\" alt=\"what to see in dubai palm jumeirah\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Palm-Jumeirah.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Scenic flight over the palm jumiera</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Dubai is a city that&rsquo;s definitely meant to be seen by air. A bird&rsquo;s eye of The Palm Jumeirah is astounding. This engineering feat is difficult to comprehend as Dubai has built an extra 650km of coastline for its residents to work and play. There&rsquo;s also a map of The World with hundreds of man-made islands waiting to be developed 14 km off the shore of Dubai. The Palm Jumeirah is a wonder of the modern world. This archipelago of islands juts out into the Persian Gulf and is completely man-made.</p>\r\n<h2 id=\"h-9-atlantis-the-palm-dubai\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"9_Atlantis_The_Palm_Dubai\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>9. Atlantis The Palm Dubai</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189090 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTAyOjg2OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Aquaventure-Waterpark.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Aquaventure-Waterpark.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Aquaventure-Waterpark-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Aquaventure-Waterpark-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Aquaventure-Waterpark-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Aquaventure-Waterpark-1536x1024.jpg 1536w\" alt=\"places to visit in dubai atlantis Dubai\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Aquaventure-Waterpark.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Palm is a tourist destination unto itself with the famous Atlantis &ndash; The Palm occupying its shores. There are jet boat rides, paddling, and plenty of water sports but one of the bucket list extreme adventures to be had in Dubai is to skydive over The Palm. You can stay at the Atlantis &ndash; The Palm. It has all the amenities you could want in a five-star resort for both families and couples.&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">Skydive Dubai offers tandem skydiving over Dubai.&nbsp;</span>Visit their website for more extreme adventures in Dubai.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"10_Aquaventure_Waterpark\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>10. Aquaventure Waterpark</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Everyone wants to catch a glimpse of Atlantis Dubai, but you don&rsquo;t have to break the bank to stay there. Visitors can buy a pass to play in Dubai&rsquo;s top-rated attraction. It is the world&rsquo;s largest waterpark with world-record-holding waterslides for thrill-seekers, and it houses Dubai&rsquo;s largest aquarium. Just make sure you DON&rsquo;T swim with the dolphins. That&rsquo;s not cool. The Lost Chambers Aquarium houses 65,000 marine animals.</p>\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 30px; line-height: 1.4em; color: #3c4053; background-color: #fafafa;\"><span id=\"Dubai_Water_Parks\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Dubai Water Parks</h3>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Dubai is the epicenter of water parks. Perhaps it&rsquo;s the hot Dubai heat or just the sheer amount of money that goes through the city, but each water park keeps getting bigger and better. Here are a few of the top water parks in Dubai.</p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Wild Wadi &ndash; This is the original Dubai water park located beside Jumeirah Beach Hotel and Burj Al Arab</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Laguna Waterpark &ndash; Located on the beach in La Mer it Laguna Water Park has four zones for fun, relaxation, slides, surf and splash.</li>\r\n</ul>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you aren&rsquo;t staying at the Atlantis, you can&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-speed-boat-tour\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"book this Speedboat tour.  (opens in a new tab)\">book this Speedboat tour</a>&nbsp;to take in the skyline as you cruise along the Persian Gulf to see Palm Jumeirah and Atlantis Hotel, and Bluewater Island.</p>\r\n<h2 id=\"h-11-wander-through-the-souks\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"11_Wander_Through_the_Souks\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>11. Wander Through The Souks</h2>\r\n<div class=\"wp-block-image size-full wp-image-78652\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189091 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTExOjg0OQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Wander-the-Souks.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Wander-the-Souks.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Wander-the-Souks-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Wander-the-Souks-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Wander-the-Souks-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Wander-the-Souks-1536x1024.jpg 1536w\" alt=\"places to visit in Dubai souks\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Wander-the-Souks.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Wandering the souks</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Dubai is a new city and it&rsquo;s difficult to believe that there is somewhere that feels old, but the souks located in the old town actually do feel a lot like other souks we&rsquo;ve visited in the Middle East. The Souks are divided up and if you take a tour of Dubai, you will easily be able to visit many Dubai attractions in the old city including the gold souk, the spice souks and pick up some fantastic souvenirs. The souks were fascinating to visit. They are busy and filled with shoppers. We were told that 20% of the world&rsquo;s gold passes through the Gold Souk. I can only imagine how rich the people were walking these alleys. Get lost in the alleyways and streets that you can get lost in while shopping and exploring your way through a fast-disappearing Middle Eastern culture in Dubai.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"12_Jumeirah_Mosque\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>12. Jumeirah Mosque</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189093 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTE0OjgyMA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Jumeirah-Mosque.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Jumeirah-Mosque.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Jumeirah-Mosque-760x443.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Jumeirah-Mosque-150x87.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Jumeirah-Mosque-768x447.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Jumeirah-Mosque-1536x895.jpg 1536w\" alt=\"what to see in dubai jumeirah mosque\" width=\"1600\" height=\"932\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Jumeirah-Mosque.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Jumeirah Mosque is the only mosque open to non-Muslims in Dubai making it one of the top Dubai attractions. We visited this on an organized tour, but you can book one of the guided tours that happen daily at 10 and 2 pm. (It is closed on Fridays). Make sure you are properly dressed when visiting any mosque or place of worship keeping legs and shoulders covered.&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">Read more:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://theplanetd.com/etiquette-in-the-middle-east-need-to-know-travel-tips-for-men-and-women/\">Etiquette in the Middle East- Travel for Men and Women</a></span></p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"13_Dubai_Creek\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>13. Dubai Creek</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189092 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTE3Ojc5MQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Dubai-Creek.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Dubai-Creek.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Dubai-Creek-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Dubai-Creek-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Dubai-Creek-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Dubai-Creek-1536x1024.jpg 1536w\" alt=\"attractions in dubai dubai creek\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Dubai-Creek.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Make sure to take the water taxi along Dubai Creek, it&rsquo;s a great affordable way to explore the old part of town. Dubai Creek is divided into two parts, Deira and Bur Dubai. &nbsp;We took a&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-full-day-tour\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"full-day tour of Dubai  (opens in a new tab)\">full-day tour of Dubai&nbsp;</a>and it really allowed us to see a lot of the highlights of the city and the top Dubai attractions including this historic part of the city. Our favorite part of Dubai was going into the historic city.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">From Mosques to the Dubai Heritage Village, to river cruises and Bazaars, it is a great place to spend an afternoon. This guided walking tour allowed us to discover Old Dubai, the Spice Souk, Bur Dubai Souk, and the Fahidi district. We also had the chance to take a boat ride an abra boat, the oldest transportation in Dubai.</p>\r\n<h2 id=\"h-14-al-fahidi-historical-district\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"14_Al_Fahidi_Historical_District\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>14. Al Fahidi Historical District</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189099 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTIxOjkyMw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Al-Fahidi-Historic-District.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Al-Fahidi-Historic-District.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Al-Fahidi-Historic-District-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Al-Fahidi-Historic-District-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Al-Fahidi-Historic-District-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Al-Fahidi-Historic-District-1536x1024.jpg 1536w\" alt=\"places to visit in Dubai Al fahidi historic district\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Al-Fahidi-Historic-District.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">For more of Old Dubai, you must visit the Al Fahidi Historical District. Dating back to 1900, this district was built by the Persians who settled in Dubai after doing trade. Thanks to efforts by locals and even Prince Charles, the area was saved from demolition. It&rsquo;s a good thing because, with all the modern skyscrapers and buildings of Dubai, there is now a historical district to glimpse into Dubai&rsquo;s past. This Old Dubai Tour took us through many of the top Dubai attractions including the Grand Souk, a cruise along the water a visit to the Spice Souk, and the Gold Souk while sampling traditional street food.</p>\r\n<h2 id=\"h-15-visit-the-dubai-museum\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"15_Visit_the_Dubai_Museum\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>15. Visit The Dubai Museum</h2>\r\n<div class=\"wp-block-image wp-image-78647 size-full\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189004 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTI0Ojc5Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-museum-1.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-museum-1.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-museum-1-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-museum-1-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-museum-1-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-museum-1-1536x1024.jpg 1536w\" alt=\"what to see in dubai dubai museum\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-museum-1.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Make sure to visit the Dubai Museum</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We thoroughly enjoyed the Dubai Museum located in the old part of the city. As the call to prayers rang over the loudspeakers from nearby mosques we strolled through a display of Dubai&rsquo;s early days and humble beginnings. Follow the journey from its foundation as pearl divers. Pearl diving was prosperous for the people of Dubai and many in the the United Arab Emirates can trace their fortunes back to their pearl diving routes. The UAE was also prosperous during the silk route trading times to the discovery of oil. Today, the city is an example of the extreme wealth and flash of the 21st century. It&rsquo;s a fascinating display.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"16_Visit_the_Desert\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>16. Visit The Desert</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189010 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTI3Ojc1NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-desert.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-desert.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-desert-760x428.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-desert-150x84.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-desert-768x432.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-desert-1536x864.jpg 1536w\" alt=\"dubai desert what to see in dubai\" width=\"1600\" height=\"900\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-desert.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you&rsquo;ve never been to the desert, this will be an exciting excursion for you. Not all Dubai attractions are located in the megacity.&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">Visting the desert and getting out of town just might be the best thing to do in Dubai!</span>&nbsp;The Dubai Desert is part of the world&rsquo;s largest sand desert and there are countless desert safaris offering fun and fabulous experiences.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We took two desert excursions that were vastly different.</p>\r\n<h2 id=\"h-17-dune-buggy-tour\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"17_Dune_Buggy_Tour\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>17. Dune Buggy Tour</h2>\r\n<div class=\"wp-block-image size-full wp-image-78650\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189008 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTMxOjgxMQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-dune-buggy.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-dune-buggy.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-dune-buggy-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-dune-buggy-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-dune-buggy-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-dune-buggy-1536x1024.jpg 1536w\" alt=\"things to do in dubai desert sfari\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-dune-buggy.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Desert Safari in Dubai</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We love a dune buggy adventure. There&rsquo;s nothing better than suiting up and hightailing it over sand dunes. Our sunset excursion took us out on a dune buggy where we enjoyed panoramic views of the desert before having a traditional Bedouin meal while watching belly dancers and the mesmerizing Tanoura dancer twirl and twirl before our eyes. You can book your&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-dune-desert-safari\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"Dune Desert Safari tour here (opens in a new tab)\">Dune Desert Safari tour here</a>&nbsp;where you&rsquo;ll have a thrill in the desert that includes a 4WD adventure, sandboarding, and camel riding.</p>\r\n<h2 id=\"h-18-desert-coservation-reserve\" class=\"has-text-align-left\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"18_Desert_Coservation_Reserve\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>18. Desert Coservation Reserve</h2>\r\n<div class=\"wp-block-image size-full wp-image-78656\" style=\"box-sizing: inherit; margin-bottom: 1.2em; max-width: 100%; height: auto; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-188998 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTM0Ojc5Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-wildlife.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-wildlife.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-wildlife-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-wildlife-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-wildlife-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-wildlife-1536x1024.jpg 1536w\" alt=\"things to do in dubai arabian oxen\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-wildlife.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Arabian Oxen in the Desert</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you want to see some wildlife, you are almost guaranteed to see Arabian oxen (oryx) gathering at the watering hole on a Dubai Desert Conservation Reserve Tour. There are plenty of camels walking through the desert (all are domesticated) but they are still amazing to see, and you may catch sight of birds of prey. The tour we took offered a birds of prey show in the desert. We didn&rsquo;t love this part of it as we aren&rsquo;t fans of birds in captivity, but we did love seeing the animals in their natural habitat.</p>\r\n<h2 id=\"h-19-falcon-show\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"19_Falcon_Show\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>19. Falcon Show</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189115 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTM3Ojc5Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Falcon-Show.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Falcon-Show.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Falcon-Show-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Falcon-Show-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Falcon-Show-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Falcon-Show-1536x1024.jpg 1536w\" alt=\"things to do in dubai Falcon Show\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Falcon-Show.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">We did not enjoy the birds of prey show learning how they raise falcons.</span>&nbsp;Using falcons is an extinct way of hunting in Dubai and is simply cruel to keep birds locked in a cage with a bag over their head simply for the pleasure of watching them catch pieces of meat that they throw in the air. It&rsquo;s not my idea of fun and I wouldn&rsquo;t recommend it. But the conservation area alone was fantastic. Watch our video of our<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.youtube.com/watch?v=QL9UYexGg9g\">&nbsp;time in the desert of Dubai</a></p>\r\n<h2 id=\"h-20-bedouin-dinner-show-al-jabal-village\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"20_Bedouin_Dinner_Show_-_Al_Jabal_Village\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>20. Bedouin Dinner Show &ndash; Al Jabal Village</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189100 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQwOjg0NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Bedouin-Dinner-Show.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Bedouin-Dinner-Show.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Bedouin-Dinner-Show-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Bedouin-Dinner-Show-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Bedouin-Dinner-Show-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Bedouin-Dinner-Show-1536x1024.jpg 1536w\" alt=\"thigns to do in dubai bedouin\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Bedouin-Dinner-Show.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The best part of our desert Experience was the Al Jabal Village where we enjoyed dinner and a show filled with local cultural experiences. Seeing belly dancers, the spectacular Tanoura Dancing was definitely a highlight.</p>\r\n<h2 id=\"h-21-take-a-camel-ride\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"21_Take_a_Camel_Ride\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>21. Take A Camel Ride</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189101 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQzOjc3Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Camel-Ride.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Camel-Ride.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Camel-Ride-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Camel-Ride-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Camel-Ride-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Dubai-Camel-Ride-1536x1024.jpg 1536w\" alt=\"camel rides in dubai\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Dubai-Camel-Ride.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We&rsquo;re not huge fans of cheesy camel rides, but if you have never ridden a camel, getting on one to go for a short walk in the desert just might be the thing for you.&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-camel-safari\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"This highly rated tour (opens in a new tab)\">This highly-rated tour</a>&nbsp;takes you on a camel ride across the desert dunes to an oasis where you&rsquo;ll enjoy the Al Marmoom Reserve&nbsp;and ancient Bedouin Village. Enjoy Arabic coffee, watch traditional dance and enjoy a traditional meal.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you are looking for a memorable excursion in Dubai,&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-hot-air-balloon-ride\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"book a hot air balloon ride  (opens in a new tab)\">book a hot air balloon ride&nbsp;</a>over the desert. Imagine taking a hot air balloon ride over the Arabian Desert. What makes a hot air balloon ride unique in Dubai is you will see the desert and the Dubai skyline since the city is right beside the desert. You may also see wildlife like gazelles, camels, and birds of prey.</p>\r\n<h2 id=\"h-22-dubai-frame\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"22_Dubai_Frame\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>22. Dubai Frame</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189017 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQ3OjY0Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/dubai-frame-adobe.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/dubai-frame-adobe.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/dubai-frame-adobe-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/dubai-frame-adobe-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/dubai-frame-adobe-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/dubai-frame-adobe-1536x1023.jpg 1536w\" alt=\"\" width=\"1600\" height=\"1066\" data-pin-media=\"https://theplanetd.com/images/dubai-frame-adobe.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Want to see the most Instagrammable spot in Dubai? Be sure to check one of the newest Dubai attractions when you visit the city. The massive picture frame stands 500 feet tall and is the highest picture frame in the world. Guests can take an elevator up to the top for panoramic views of the city as they walk along the clear glass bridge that spans 313 feet. Book your tickets here to the Dubai Frame for incredible views of the skyline. The tour includes stops at The Pointe, Palm Jumeirah, and Atlantis plus the Blue Mosque. You&rsquo;ll also explore the gold and spice souks allowing you to experience views of both the old side and new side of the city from the Dubai Frame.</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"23_Dubai_Theme_Parks\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>23. Dubai Theme Parks</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Dubai has many attractions to keep families busy. Here are a few Amusement parks that are fun for the whole family.</p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">IMG World of Adventure is the world&rsquo;s biggest indoor amusement park</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Dubai Parks and Resorts is the largest integrated amusement park in the Middle East with four themes. Motionland combines DreamWorks Animation, Columbia Pictures and Lionsgate for a movie themed park, Bollywood Parks highlights the best from Bollywood Theater, there&rsquo;s Lego Water Parks and Legoland.</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Families may want to book a day at Legoland Dubai.&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-legoland\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"This pass offers entrance (opens in a new tab)\">This pass offers entrance</a>&nbsp;to Legoland where you&rsquo;ll enjoy 40 LEGO-themed rides, LEGO models and 6 themed lands.</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">Global Village is an entertainment complex and amusement park featuring cultures from around the world. At Global Village there&rsquo;s no need to travel the world, you can visit 90 countries all in one place.</li>\r\n</ul>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"24_Dubai_Miracle_Garden\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>24. Dubai Miracle Garden</h2>\r\n<figure class=\"wp-block-image\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><img style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" alt=\"\" data-pin-nopin=\"true\" /></figure>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Botanical Garden lovers will love the Dubai Miracle Garden that displays millions of flowers and plants. 150 million flowers to be exact. Like everything in Dubai, this garden is over the top spanning 72,000 square meters of displays from the floating lady to the floral clock. Umbrella Tunnel will get your Instagram mojo running or you may want to visit the sunflower field. The Dubai Miracle Garden is listed in the Guinness Book of World Records as the largest flower garden in the world. What doesn&rsquo;t Dubai have that&rsquo;s the biggest and best?</p>\r\n<h2 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"25_Day_Trip_to_Abu_Dhabi\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>25. Day Trip To Abu Dhabi</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189018 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTU2OjgyNA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-abu-dhabi-adobe.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-abu-dhabi-adobe.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-abu-dhabi-adobe-760x471.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-abu-dhabi-adobe-150x93.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-abu-dhabi-adobe-768x476.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-dubai-abu-dhabi-adobe-1536x951.jpg 1536w\" alt=\"Things to do Dubai Day trip to Abu Dhabi\" width=\"1600\" height=\"991\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-dubai-abu-dhabi-adobe.jpg\" data-pin-description=\"17 Fabulous and Fun Things to do in Dubai\" data-pin-title=\"17 Fabulous and Fun Things to do in Dubai\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">It&rsquo;s easy to visit Abu Dhabi from Dubai. You can book a day tour to see the capital of the United Arab Emirates. This tour takes you along the Persian Gulf Coast to explore Abu Dhabi&rsquo;s top landmarks including:</p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">The Sheikh Zayed Grand Mosque &ndash; One of the world&rsquo;s largest mosques</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">The Observation Deck of Etihad Towers</li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\">And Arabian heritage Qasr Al Watan Palace</li>\r\n</ul>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">For something truly unique,&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/dubai-ferrari-world\" target=\"_blank\" rel=\"nofollow noindex noreferrer noopener\" aria-label=\"book this highly rated tour (opens in a new tab)\">book this highly rated tour</a>&nbsp;to Abu Dhabi includes Sheikh Zayed Mosque, The Emirates Palace, and other attractions in the morning before spending the afternoon at Ferrari World. Ferrari World is a Ferrari branded theme park housing the fastest roller coaster in the world, the world&rsquo;s highest non-inverted loop roller coaster, and the world&rsquo;s largest indoor amusement park. The countries of the United Arab Emirates certainly love to be number one in things, don&rsquo;t they? (both Ferrari World and&nbsp;<span style=\"box-sizing: inherit; font-size: inherit;\">IMG World of Adventure</span>&nbsp;claim to be the world&rsquo;s largest indoor theme parks. Who am I to argue? They are both big)</p>', 18, 11, 0, 'published', '2022-03-13 08:04:57', '2022-03-13 08:04:57');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(20, 'BEST THAI FOOD – TRADITIONAL THAI DISHES TO EAT IN THAILAND OR AT HOME', 'traditional-thai-food', 'Whether you have visited Thailand or planning your future travels to the Land of Smiles, chances are you have already fallen in love with Thai food.', '<h2 id=\"h-1-thai-green-curry-gaeng-keow-wan\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\">1. Thai Green Curry (Gaeng Keow Wan)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-188890 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY4OjcwNA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Green-Curry.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Green-Curry.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Green-Curry-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Green-Curry-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Green-Curry-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Green-Curry-1536x1024.jpg 1536w\" alt=\"Thai food Green Curry Chicken\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Thai-Food-Green-Curry.jpg\" data-pin-description=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" data-pin-title=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Thai green chicken curry is my go-to dish in&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-thailand/\">Thailand</a>. This delicious creamy meal is served over jasmine rice and is one of the most filling and satisfying Thai meals to order.&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">At home, we make Thai curry with tofu instead of chicken</span>&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">for a vegetarian option</span>. The curry is made with green curry paste and coconut milk. I&rsquo;ve never been able to make my home Thai cooking taste quite as delicious as it tastes in Thailand, but it is still tasty.&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.allrecipes.com/recipe/141833/thai-green-curry-chicken/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">Get the recipe here.</a></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The curry is mixed with chilis, Thai basil, fish sauce, and added vegetables. There are a variety of vegetables to include from snow peas and eggplant to lime leaves and baby corn all served over freshly steamed rice. Make sure you visit Thailand to truly explore the best Thai cuisine. Read more:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/thailand-travel-tips-things-not-to-do/\">Thailand Travel Tips &ndash; 32 Things to Know Before you Go</a>.</p>\r\n<h2 id=\"h-2-cashew-nut-chicken-kai-med-ma-muang\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"2_Cashew_Nut_Chicken_Kai_Med_Ma_Muang\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>2. Cashew Nut Chicken (Kai Med Ma Muang)&nbsp;</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-188886 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODcyOjgzMw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Cashew-Nut-Chicken.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Cashew-Nut-Chicken.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Cashew-Nut-Chicken-621x507.jpg 621w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Cashew-Nut-Chicken-1307x1067.jpg 1307w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Cashew-Nut-Chicken-150x122.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Cashew-Nut-Chicken-768x627.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Cashew-Nut-Chicken-1536x1254.jpg 1536w\" alt=\"Cashew Nut Chicken Thai Foods\" width=\"1600\" height=\"1306\" data-pin-media=\"https://theplanetd.com/images/Thai-Food-Cashew-Nut-Chicken.jpg\" data-pin-description=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" data-pin-title=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This is Dave&rsquo;s favorite Thai food. When we are out for dinner in Thailand, I usually order a curry with jasmine rice (or sticky rice) and he orders Cashew Chicken with sticky rice. It&rsquo;s funny how we always fall back on our &ldquo;go to&rsquo;s&rdquo; It&rsquo;s also very easy to make at home. Start with stir-fried chicken and mix in roasted cashews, and garlic. When making the vegetarian version we use mushrooms. The key to cashew nut chicken is corn starch. When added to the dark soy sauce, hoisin sauce, black pepper, and rice vinegar, it thickens into a savory treat.<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.eatingthaifood.com/thai-cashew-chicken-recipe/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">&nbsp;Get the recipe here.</a></p>\r\n<h2 id=\"h-3-panang-curry\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"3_Panang_Curry\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>3. Panang Curry</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-188885 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODc1OjcxNw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Penang-Curry.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Penang-Curry.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Penang-Curry-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Penang-Curry-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Penang-Curry-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Penang-Curry-1536x1023.jpg 1536w\" alt=\"Thai Food Panang Curry with Chicken\" width=\"1600\" height=\"1066\" data-pin-media=\"https://theplanetd.com/images/Thai-Food-Penang-Curry.jpg\" data-pin-description=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" data-pin-title=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Panang curry is a bit milder than green curry but just as flavorful and is popular in southern Thailand. This savory yet sweet Thai curry is made from scratch in Thailand, but at home, you can buy Panang curry paste, (However, Panang curry paste is more difficult to find than green or red curry paste. You can usually find it in an Asian grocery store though)</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Panang Curry is orange in color and filled with vegetables in a coconut cream sauce. The delicious sauce is fille with flavours from kaffir lime leaves, Thai basil, fish sauce, and sugar that is served over fragrant jasmine rice. Usually, Thai curries are made with chicken when<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-thailand/\">&nbsp;visiting Thailand</a>&nbsp;but at home, we make it with tofu. Once I discovered Panang Curry, this was my go-to Thai dish and took over a green curry as my favorite Thai curry.&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://tastesbetterfromscratch.com/panang-curry/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">See how to make it here.</a></p>\r\n<h2 id=\"h-4-pad-thai\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"4_Pad_Thai\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>4. Pad Thai</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-188892 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODc5OjY3Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-Thai.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-Thai.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-Thai-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-Thai-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-Thai-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-Thai-1536x1024.jpg 1536w\" alt=\"Best Thai Food Pad Thai\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Thai-Food-Pad-Thai.jpg\" data-pin-description=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" data-pin-title=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Pad Thai is a staple dish in Thailand and is one of the most popular of all Thai foods. You can order Pad Thai from street vendors, at corner cafes, restaurants, and pretty much any shop on a street corner. Pad Thai is the national dish of Thailand so it is obvisouly the go-to for tourists grabbing lunch on&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-bangkok/\">Kho San Road</a>. This fried noodle dish is cheap and delicious and is mixed in a peanut sauce with either chicken or shrimp. See how to make Pad Thai at home with this&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://tastesbetterfromscratch.com/pad-thai/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">easy-to-follow recipe.</a></p>\r\n<h2 id=\"h-5-pad-see-ew-thai-stir-fried-noodles\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"5_Pad_See_Ew_Thai_Stir_Fried_Noodles\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>5. Pad See Ew (Thai Stir Fried Noodles)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-188887 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODgyOjY5OQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-See-Eiw.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-See-Eiw.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-See-Eiw-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-See-Eiw-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-See-Eiw-768x513.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Thai-Food-Pad-See-Eiw-1536x1025.jpg 1536w\" alt=\"Pad See Eiw Thai cuisine\" width=\"1600\" height=\"1068\" data-pin-media=\"https://theplanetd.com/images/Thai-Food-Pad-See-Eiw.jpg\" data-pin-description=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" data-pin-title=\"Best Thai Food - Traditional Thai Dishes to Eat in Thailand or at Home\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Pad See Eiw is a thick noodle dish in Thailand. These noodles are stir-fried in soy sauce creating a thick gravy. If you enjoy Pad Thai, this is a good option for your next Thai dish to try while in Thailand. Besides noodles it also is served with chicken, pork, or beef and vegetables can be cabbage or broccoli.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This is popular street food in Thailand and you will find many locals huddled around tables eating this savory meal. Pad See Ew is made with wide rice noodles, Gai Lan (Chinese broccoli). The stir-fried chicken is mixed with dark soy sauce, white vinegar, oyster sauce, and sugar.&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.recipetineats.com/thai-stir-fried-noodles-pad-see-ew/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">For the full recipe visit here.</a></p>', 13, 11, 0, 'published', '2022-03-13 08:06:30', '2022-03-13 08:06:30');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(21, 'PERUVIAN FOOD: 20 TRADITIONAL PERUVIAN DISHES YOU HAVE TO TRY', 'traditional-peruvian-food', 'Peru is famous for beautiful historical sites such as Machu Picchu, towering mountains, and the Amazon rainforest. It’s also famous for delicious cuisine that you can’t find anywhere else.', '<h2 id=\"h-1-ceviche-fish-cocktail\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\">1. Ceviche (Fish Cocktail)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189191 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY0Ojc2OQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Ceviche.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Ceviche.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Ceviche-676x507.jpg 676w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Ceviche-1423x1067.jpg 1423w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Ceviche-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Ceviche-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Ceviche-1536x1152.jpg 1536w\" alt=\"Peruvian Food Ceviche\" width=\"1600\" height=\"1200\" data-pin-media=\"https://theplanetd.com/images/Peruvian-Food-Ceviche.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you&rsquo;re looking for famous traditional Peruvian cuisine, you absolutely have to try Ceviche. Ceviche is one of the most famous types of food from Peru, and if you&rsquo;re looking for traditional&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-peru/\">things to do in Peru</a>, eating Ceviche should be at the top of your list.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Ceviche is essentially a dish made up of chunks of raw white fish. The chef cures the fish with lime juice and then tosses it in a blend of chopped onion, fresh cilantro, salt, and chili peppers. You&rsquo;ll often find a plate of ceviche served up alongside sliced sweet potato. The sweetness of the vegetable helps balance out the salty taste of the fish.&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">This is the perfect dish to make at home and<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://www.eatperu.com/ceviche-recipe/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">&nbsp;this recipe</a>&nbsp;tastes delicious!</span></p>\r\n<h2 id=\"h-2-lomo-saltado-chopped-steak\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"2_Lomo_Saltado_Chopped_Steak\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>2. Lomo Saltado (Chopped Steak)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189179 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODY4OjczNw==-1\" class=\"nitro-lazy\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"data:image/svg+xml;nitro-empty-id=ODY4OjczNw==-1;base64,PHN2ZyB2aWV3Qm94PSIwIDAgMTYwMCAxMDY3IiB3aWR0aD0iMTYwMCIgaGVpZ2h0PSIxMDY3IiB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciPjwvc3ZnPg==\" sizes=\"(max-width: 1600px) 100vw, 1600px\" alt=\"Peruvian Food Lomo saltado \" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Peruvian-Food-Lomo-Saltado.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Lomo Saltado is common at Peruvian meals. To make Lomo, Peruvians chop up onions, tomatoes, beef, and the famous&nbsp;<em style=\"box-sizing: inherit;\">aj&iacute; Camarillo</em>&nbsp;(Peruvian chili pepper) and combine them with soy sauce. This is one of those Peruvian dishes that is a perfect example of the influence of Chinese immigrants on the country. They mix these ingredients together in a rich stir fry.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Lomo Saltado is usually served on top of french fries. You&rsquo;ll also enjoy a steaming hot bowl of white rice to accompany the dish. This dish is really easy to make at home.<span style=\"box-sizing: inherit; font-weight: bold;\">&nbsp;Check out how to make it with&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://www.chilipeppermadness.com/recipes/lomo-saltado/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">this recipe.</a></span></p>\r\n<h2 id=\"h-3-pollo-a-la-brasa-grilled-chicken\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"3_Pollo_a_La_Brasa_Grilled_Chicken\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>3. Pollo A La Brasa (Grilled Chicken)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189185 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODcyOjc2OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Pollo-a-La-Brasa.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Pollo-a-La-Brasa.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Pollo-a-La-Brasa-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Pollo-a-La-Brasa-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Pollo-a-La-Brasa-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Pollo-a-La-Brasa-1536x1024.jpg 1536w\" alt=\"Peruvian Food pollo a la brasa\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Peruvian-Food-Pollo-a-La-Brasa.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Simple yet a staple of Peruvian cuisine, Polla a la Brasa is simply grilled chicken. To make the dish, chefs marinate a whole chicken in a mixture of chilis, cumin, and garlic.&nbsp; After roasting the meat over hot coals, the chef will slice it up and serve it with salad and french fries. If you really have an appetite you can even order an entire chicken!&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">Read:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://theplanetd.com/authentic-cusco-restaurants/\">5 Authentic Cusco Restaurants Where you Can Eat Like a Local</a></span></p>\r\n<h2 id=\"h-4-causa-rellena-layered-potato-casserole\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"4_Causa_Rellena_Layered_Potato_Casserole\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>4. Causa Rellena (Layered Potato Casserole)&nbsp;</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.peruforless.com/blog/causa-recipe/\">Causa Rellena</a>&nbsp;is a common Peruvian food and is very similar to a classic American potato salad. However, this salad has layers, making&nbsp;it a truly Instagram-worthy dish.&nbsp;Causa Rellena&nbsp;consists of mashed citrusy potatoes, avocado, chicken or tuna, and hard-boiled eggs. Many Peruvian chefs have fun getting creative with the toppings for this dish and introducing new ingredients.</p>\r\n<div class=\"gb-container gb-container-c731bd02\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<h3 style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-size: 30px; font-style: inherit; line-height: 1.4em; color: #3c4053;\"><span id=\"Read_More_About_the_Amazing_country_of_Peru\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>Read More About the Amazing country of Peru</h3>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/facts-about-peru/\">Fun Facts About Peru You Never Knew</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/rainbow-mountain-peru/\">What To Expect When Hiking Rainbow Mountain Peru</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-peru/\">21 of the Best Things to do in Peru</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/iquitos-tour/\">Things to do in Iquitos, Peru &ndash; A Gateway to the Amazon</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/paracas-peru-adventure/\">Paracas Peru &ndash; Adventures in the California Desert</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-5-aji-de-gallina-spicy-chicken\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"5_Aji_De_Gallina_Spicy_Chicken\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>5. Aji De Gallina (Spicy Chicken)</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you love spicy food you won&rsquo;t want to miss out on aji de gallina. Aji de gallina is a spicy chicken dish made from yellow chili peppers, cheese, and sliced hen.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">One&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/facts-about-peru/\">fun fact about Peru</a>&nbsp;is that chefs actually differentiate between chicken and hen. This dish&nbsp;uses hen, as opposed to chicken, which changes the texture of the dish. Aji de gallina is most commonly served with white rice. You may also enjoy potatoes alongside this dish. Either way, you&rsquo;ll fall in love with the nutty and spicy flavors of aji de gallina.&nbsp;</p>\r\n<h2 id=\"h-6-anticuchos-beef-heart-kebabs\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"6_Anticuchos_Beef_Heart_Kebabs\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>6. Anticuchos (Beef Heart Kebabs)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189184 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODg0OjczNg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Dishes-Anticuchos.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Dishes-Anticuchos.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Dishes-Anticuchos-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Dishes-Anticuchos-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Dishes-Anticuchos-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Dishes-Anticuchos-1536x1024.jpg 1536w\" alt=\"Peruvian Dishes Anticuchos\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Peruvian-Dishes-Anticuchos.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If wandering the streets of Peru is how you plan to spend your days exploring this country, make sure to pick up some Anticuchos. Anticuchos are meat skewers made out of marinated beef hearts.&nbsp; To make the marinade, Peruvian cooks mix smoked chili peppers, lime, cumin, and garlic. The combination pairs perfectly with the meat and creates an aromatic flavor.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Anticuchos are most often sold as street food and so&nbsp;get served alone. However, vendors typically have fried plantain slices or yuca fries on offer if you want a side dish. No matter which you choose you&rsquo;re sure to love this salty snack!</p>\r\n<h2 id=\"h-7-seco-de-carne-beef-stew\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"7_Seco_De_Carne_Beef_Stew\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>7. Seco De Carne (Beef Stew)</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">One of the simpler Peruvian dishes out there is Seco de Carne. This tasty stew&nbsp;combines a thick beef broth, generous cuts of meat, and a few traditional Andean ingredients that create a unique flavor.&nbsp;Fermented corn and cilantro bring out the flavor and transform this dish from an ordinary soup into a delicious meal. Peruvians serve this tasty meal with a side of rice.</p>\r\n<h2 id=\"h-8-cuy-chactado-fried-guinea-pig\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"8_Cuy_Chactado_Fried_Guinea_Pig\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>8. Cuy Chactado (Fried Guinea Pig)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189178 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODkwOjg2NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/traditional-Peruvian-Food-Fried-Guinea-Pig.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/traditional-Peruvian-Food-Fried-Guinea-Pig.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/traditional-Peruvian-Food-Fried-Guinea-Pig-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/traditional-Peruvian-Food-Fried-Guinea-Pig-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/traditional-Peruvian-Food-Fried-Guinea-Pig-768x513.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/traditional-Peruvian-Food-Fried-Guinea-Pig-1536x1025.jpg 1536w\" alt=\"traditional peruvian food Fried Guinea Pig\" width=\"1600\" height=\"1068\" data-pin-media=\"https://theplanetd.com/images/traditional-Peruvian-Food-Fried-Guinea-Pig.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you&rsquo;re wanting to step out of your comfort zone and try something truly exotic, you simply have to give&nbsp;Cuy Chactado&nbsp;a try. This dish is&nbsp;essentially a plate of fried guinea pigs.&nbsp;(we couldn&rsquo;t bring ourselves to try this while&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/day-trips-cusco-peru/\">visiting Peru</a>, they are just too darn cute &ndash; however, we saw them on the menu a lot when&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/machu-picchu-the-land-of-the-incas/\">visiting Machu Picchu</a>)</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Cuy is a dish that Peruvians enjoy on special holidays. The meat is highly sought after and is a delicacy throughout the country.&nbsp;While the idea of eating guinea pigs might not seem appealing, it&rsquo;s definitely worth a try. Guinea pig is low-fat meat that&rsquo;s high in protein and packed with important nutrients.</p>\r\n<h2 id=\"h-9-tiradito-de-pescado-sashimi\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"9_Tiradito_De_Pescado_Sashimi\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>9. Tiradito De Pescado (Sashimi)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189183 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk0Ojc5Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Tiradito-De-Pescado.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Tiradito-De-Pescado.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Tiradito-De-Pescado-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Tiradito-De-Pescado-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Tiradito-De-Pescado-768x511.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Tiradito-De-Pescado-1536x1022.jpg 1536w\" alt=\"Peruvian Food Tiradito De Pescado\" width=\"1600\" height=\"1065\" data-pin-media=\"https://theplanetd.com/images/Peruvian-Food-Tiradito-De-Pescado.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Do you love sushi? If so, you&rsquo;ll definitely want to order a plate of Tiradito de Pescado.&nbsp; Tiradito consists of thinly sliced raw fish. Since Peru borders the Pacific Ocean, there is plenty of fresh fish for use in this dish.&nbsp;To make tiradito, chefs create a special sauce using yellow chilis, garlic, and cilantro. Lime juice ties the sauce together and creates the perfect balance to the salty fish slices.&nbsp;&nbsp;</p>\r\n<h2 id=\"h-10-rocoto-relleno-stuffed-peppers\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"10_Rocoto_Relleno_Stuffed_Peppers\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>10. Rocoto Relleno (Stuffed Peppers)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189186 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk3Ojc1Mg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Rocoto-Relleno.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Rocoto-Relleno.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Rocoto-Relleno-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Rocoto-Relleno-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Rocoto-Relleno-768x513.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Rocoto-Relleno-1536x1025.jpg 1536w\" alt=\"Peruvian Food Rocoto Relleno\" width=\"1600\" height=\"1068\" data-pin-media=\"https://theplanetd.com/images/Peruvian-Food-Rocoto-Relleno.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Anyone who loves stuffed pepper should definitely give Rocoto Relleno a try. This dish is made out of spicy Peruvian peppers but is between&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.pepperscale.com/rocoto-pepper/\">4 and 40 times spicier</a>&nbsp;than jalape&ntilde;os.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Despite the heat of the pepper, this dish is full of flavor. Each pepper is filled with vegetables, ground meat, and cheese that balances out the spicy pepper and satisfies your palette.&nbsp;This dish is easy to make at home especially with&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.livinginperu.com/peruvian-recipe-how-to-make-rocoto-relleno-a-staple-from-arequipa/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">this recipe.</a></p>\r\n<h2 id=\"h-11-arroz-con-mariscos-rice-with-shellfish\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"11_Arroz_Con_Mariscos_Rice_With_Shellfish\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>11. Arroz Con Mariscos (Rice With Shellfish)</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Arroz con Mariscos is one of the most popular seafood dishes in Peru. This dish is quite similar to a traditional Spanish Paella but uses unique ingredients to create an explosion of flavor.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">To make this dish, Peruvian chefs first whip up a batch of spiced rice. They then add peas, carrots, and red bell pepper to the mix. Finally, the chef adds shellfish such as clams, shrimp, octopus, mussels, scallops, and squid to the rice.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">No matter what type of shellfish you enjoy eating, you&rsquo;re sure to find it rearing its head in a plate of Arroz con Mariscos. And, you can really bring out the flavor of this dish by adding a pinch of parmesan cheese on top of your meal!</p>\r\n<h2 id=\"h-12-pachamanca-hot-stone-barbecue\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"12_Pachamanca_Hot_Stone_Barbecue\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>12. Pachamanca (Hot Stone Barbecue)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189180 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTA1OjY5Nw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Pachamanca.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Pachamanca.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Pachamanca-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Pachamanca-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Pachamanca-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Pachamanca-1536x1024.jpg 1536w\" alt=\"Peru Foods Pachamanca \" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Peru-Foods-Pachamanca.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Pachamanca is a&nbsp;traditional Peruvian food&nbsp;that&rsquo;s native to the&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.britannica.com/place/Andes-Mountains\">Andes Mountain region</a>. What&rsquo;s different about this Peruvian cuisine is the way that chefs prepare the meal.&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">Check out more of the Andes at:&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad;\" href=\"https://theplanetd.com/machu-picchu-hike/\">Machu Picchu Hiking Tips &ndash; How To Hike the Inca Trail</a></span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">In a pachamanca, chefs mix meats, vegetables, and herbs together. Then, they dig a hole in the ground and fill it with hot stones. The chefs place the meat and vegetables in between the stones and then cover the hole with earth.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The&nbsp;chefs then leave the food to cook for several hours, taking in the smoke from the stones and enhancing the flavor. If you visit Peru, you can take part in your own&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://pie-experiences.com/es/pachamanca-dining-experience-peru-unlike/pachamanca-experience-2-2/\">Pachamanca experience</a>&nbsp;in the country&rsquo;s Sacred Valley.&nbsp;&nbsp;</p>\r\n<h2 id=\"h-13-adobo-pork-stew\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"13_Adobo_Pork_Stew\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>13. Adobo (Pork Stew)</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Adobo is a dish that hails from Arequipa and is simply a spicy pork stew. The dish is usually prepared for breakfast on Sunday morning and is made in a clay pot.&nbsp;Don&rsquo;t be confused by other countries offering adobo. Since the word means &ldquo;marinade,&rdquo; you&rsquo;ll find many dishes in other Latino countries serving their own style of this dish.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Peruvian adobo is made of pork, anatto seeds, smoky chili pepper, and fermented corn. The result is a delicious stew that you won&rsquo;t be able to get anywhere else!</p>\r\n<h2 id=\"h-14-leche-de-tigre-tiger-s-milk\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"14_Leche_De_Tigre_Tigers_Milk\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>14. Leche De Tigre (Tiger&rsquo;s Milk)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-Vertical\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189181 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTEzOjYwMw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Food-in-Peru-Leche-De-Tigre-640x960.jpg\" sizes=\"(max-width: 640px) 100vw, 640px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Food-in-Peru-Leche-De-Tigre-640x960.jpg 640w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Food-in-Peru-Leche-De-Tigre-99x150.jpg 99w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Food-in-Peru-Leche-De-Tigre-150x226.jpg 150w\" alt=\"Leche De Tigre Peruvian Food\" width=\"640\" height=\"960\" data-pin-media=\"https://theplanetd.com/images/Food-in-Peru-Leche-De-Tigre.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you already tried ceviche and loved it you&rsquo;ll want to give Leche de Tigre a try. This dish is made out of the juice of shellfish combined with lime juice, shrimp, fish, and baked corn kernels.&nbsp; Some chefs will add extra cilantro and lime to the snack to bring out the flavors. Try dipping saltines or other crackers in the cocktail for a delectable afternoon pick-me-up!</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Pro tip:</span>&nbsp;Leche de Tigre is said to cure hangovers. If you spent last night drinking one too many pisco sours, you can get back on your A-game with some Leche de Tigre.</p>\r\n<h2 id=\"h-16-jalea-fried-fish\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"16_Jalea_Fried_Fish\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>16. Jalea (Fried Fish)</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Where&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/places-to-visit-in-the-uk/\">Great Britain</a>&nbsp;is famous for deep-fried fish and chips, Peru is famous for jalea. Jalea is a Latino take on this<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/british-food/\">&nbsp;British staple</a>, made out of either deep-fried mixed seafood or deep-fried fish.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Unlike the British dish, Jalea is not often served with potatoes. Instead, your fish will be accompanied by a salty pile of deep-fried yuca. If you choose to order a plate of Jalea at a restaurant, make sure to ask for the accompanying creole salsa. The salsa is made out of red onion, cilantro, and lime juice that pairs perfectly with salty fish.</p>\r\n<h2 id=\"h-15-papa-a-la-huancaina-yellow-potatoes\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"15_Papa_a_La_Huancaina_Yellow_Potatoes\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>15. Papa A La Huancaina (Yellow Potatoes)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189182 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTIwOjc2OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Papa-a-La-Huancaina.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Papa-a-La-Huancaina.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Papa-a-La-Huancaina-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Papa-a-La-Huancaina-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Papa-a-La-Huancaina-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peru-Foods-Papa-a-La-Huancaina-1536x1024.jpg 1536w\" alt=\"Papa a La Huancaina Peru Foods\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Peru-Foods-Papa-a-La-Huancaina.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Many popular Peruvian recipes make use of yellow chili and papa a la Huancaina is no different. This dish uses these chilis mixed with cheese to create a smooth and spicy sauce.&nbsp;The sauce is then poured over boiled potatoes. Chefs often top the dish with hardboiled eggs and serve it with a side of ceviche.</p>\r\n<h2 id=\"h-17-tequenos-cheese-wontons\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"17_Tequenos_Cheese_Wontons\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>17. Teque&ntilde;os (Cheese Wontons)</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">One of the most popular Peruvian appetizers is teque&ntilde;os. These delicious, bite-sized treats are served in bars all over the country and are the perfect way to start any meal.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Teque&ntilde;os are made out of fried wonton wrappers that are stuffed with soft white cheese. They&rsquo;re usually served with a side of spicy salsa or guacamole for dipping.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Interestingly enough, teque&ntilde;os originated in Venezuela. The version you&rsquo;ll find in the country of origin is slightly different than the Peruvian style dish. Even if you&rsquo;ve already had Venezuelan teque&ntilde;os, it&rsquo;s worth giving the ones from Peru a try.</p>\r\n<h2 id=\"h-18-picarones-sweet-potato-donuts\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"18_Picarones_Sweet_Potato_Donuts\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>18. Picarones (Sweet Potato Donuts)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189177 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTI3OjcxMw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Picarones.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Picarones.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Picarones-760x504.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Picarones-150x99.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Picarones-768x509.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Picarones-1536x1018.jpg 1536w\" alt=\"Peruvian Dishes Picarones\" width=\"1600\" height=\"1060\" data-pin-media=\"https://theplanetd.com/images/Peruvian-Food-Picarones.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">We&rsquo;ve covered a lot of main courses on this list, but let&rsquo;s not forget dessert! Dessert is the most important part of the meal, and Peruvians really know how to come up with a good one.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Picarones are made out of a mixture of sweet potato and pumpkin. The vegetables are mashed together and used to create a smooth dough which cooks then deep fry.&nbsp;Chefs top Picarones with drizzled honey, creating a sweet dessert.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">These treats are most commonly found sold along the side of the road by street vendors. Originally sold as more affordable donuts, Picarones are now a popular Peruvian dessert.</p>\r\n<h2 id=\"h-19-alfajores-caramel-sandwich-cookies\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"19_Alfajores_Caramel_Sandwich_Cookies\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>19. Alfajores (Caramel Sandwich Cookies)</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Alfajores are one popular dessert that you can find not only in Peru but all over South America. These treats are made of thin shortbread sandwiches with sweet caramel sauce in the middle.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">There are many different toppings that you can add to alfajores. Try topping them with powdered sugar, sprinkling them with coconut, dipping them in chocolate, or even frying them!</p>\r\n<h2 id=\"h-20-mazamorra-morada-and-arroz-con-leche-purple-corn-jam-and-rice-pudding\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"20_Mazamorra_Morada_and_Arroz_Con_Leche_Purple_Corn_Jam_and_Rice_Pudding\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>20. Mazamorra Morada And Arroz Con Leche (Purple Corn Jam And Rice Pudding)</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-189187 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTM1Ojc2OA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Mazamorra-Morada.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Mazamorra-Morada.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Mazamorra-Morada-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Mazamorra-Morada-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Mazamorra-Morada-768x511.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Peruvian-Food-Mazamorra-Morada-1536x1022.jpg 1536w\" alt=\"Peruvian Food Mazamorra Morada\" width=\"1600\" height=\"1065\" data-pin-media=\"https://theplanetd.com/images/Peruvian-Food-Mazamorra-Morada.jpg\" data-pin-description=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" data-pin-title=\"Peruvian Food: 20 Traditional Peruvian Dishes You Have to Try\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Chica&nbsp;Morada is a sweet drink made out of quince, purple corn, lime, cinnamon, cloves, sugar, and apple skins. The result is a sweet purple drink that many Peruvians enjoy.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">In fact, the drink was so popular that chefs turned it into a jam! To make the jam, they simply heat the beverage and mix it with cornstarch to solidify the liquid.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Many Peruvians enjoy pairing the jelly with Arroz con Leche, a traditional rice pudding. The fruit flavor of the drink brings out the cinnamon used in the pudding and makes for a delicious combination.</p>', 16, 11, 0, 'published', '2022-03-13 08:12:47', '2022-03-13 08:12:47');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `category_id`, `user_id`, `views`, `status`, `created_at`, `updated_at`) VALUES
(22, '14 BEST EUROPEAN CAPITAL CITIES TO VISIT IN 2022', 'european-capital-cities', 'Europe has some of the most beautiful capital cities in the world. All of them offer a combination of rich history, grand architecture, sprawling parks, and exquisite artwork.', '<h2 id=\"h-1-london-england\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\">1: London, England&nbsp;</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-178334 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODYzOjg2Mw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/How-to-visit-palace-of-Westminster-London.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/How-to-visit-palace-of-Westminster-London.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/How-to-visit-palace-of-Westminster-London-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/How-to-visit-palace-of-Westminster-London-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/How-to-visit-palace-of-Westminster-London-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/How-to-visit-palace-of-Westminster-London-1536x1024.jpg 1536w\" alt=\"How to Visit the Palace of Westminster in London\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/How-to-visit-palace-of-Westminster-London.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of London, England: 8,962,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This lively European capital city plays host to millions of tourists each year and there are so many reasons to add it to your bucket list. London has a great food scene, famous museums, huge parks, and much more. It&rsquo;s got everything you would want in a European capital city.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When you visit, one of the more touristy things to do is to hop on a New Routemaster. These famous double-decker red buses allow you to experience a historic vehicle and it makes for some great photos too.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You&rsquo;ve got many sights to choose from such as Big Ben, Tower Bridge, the Tower of London and so many more. After viewing the sights, you have to head into a pub for the nightlife. London&rsquo;s strategic location makes it easy to explore the rest of Europe quickly.</p>\r\n<div class=\"gb-container gb-container-4b25d65a\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about London, England</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-london-best-areas/\">Where To Stay In London &ndash; Guide to The Best Neighborhoods</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/3-days-in-london-itinerary/\">3 Days in London &ndash; The Ultimate London Itinerary</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-london-england/\">22 of the Top Things to do in London, England</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-2-dublin-ireland\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"2_Dublin_Ireland\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>2: Dublin, Ireland</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-184867 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODczOjgyNA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-visit-for-Halloween-Dublin.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-visit-for-Halloween-Dublin.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-visit-for-Halloween-Dublin-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-visit-for-Halloween-Dublin-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-visit-for-Halloween-Dublin-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-visit-for-Halloween-Dublin-1536x1024.jpg 1536w\" alt=\"Places to visit for halloween Dublin Ireland\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Places-to-visit-for-Halloween-Dublin.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The</span>&nbsp;<span style=\"box-sizing: inherit; font-weight: bold;\">Population of Dublin: 1,173,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The city of Dublin provides vibrant nightlife. You can visit an Irish pub and watch live Irish music performances any day of the week.&nbsp;It&rsquo;s a great city to meet new friends and discover an interesting history. However, Dublin has more going for it than its nightlife and pubs.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You can also visit sites like the Spire of Dublin, which stands proudly in the heart of the city. The stainless-steel structure is something to see as it stands at over 120 meters tall. You also can&rsquo;t miss Dublin&rsquo;s Ha&rsquo;Penny Bridge which you&rsquo;ll find on the front of many postcards and you must make your way to Croke Park. Ireland&rsquo;s largest stadium and the third biggest in all of Europe!</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you want to&nbsp;travel back in time and learn more about the history of Dublin you can visit Dublin Castle. The castle served as headquarters for the English since the 13th century. Ireland claimed the castle in 1922 following their independence. It remains a major government complex to this day.&nbsp; Dublin has plenty of great things to offer. If you&rsquo;re into pub life, add this European Capital to the top of your list.&nbsp;</p>\r\n<div class=\"gb-container gb-container-21557b14\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Dublin, Ireland</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/a-step-by-step-guide-of-the-guinness-storehouse-tour/\">Guinness Storehouse in Dublin &ndash; The Ultimate Tasting Tour</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-things-to-do-in-dublin/\">33 of the Best Things to do in Dublin &ndash; 2021</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/facts-about-ireland/\">16 Fun Facts About Ireland To Prepare For Your Next Journey Abroad</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-3-amsterdam-the-netherlands\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"3_Amsterdam_the_Netherlands\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>3: Amsterdam, The Netherlands</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\"><img id=\"ODgzOjgwNQ==-1\" class=\"wp-image-174084 lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-amsterdam.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-amsterdam.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-amsterdam-760x359.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-amsterdam-150x71.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-amsterdam-768x362.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-amsterdam-1536x725.jpg 1536w\" alt=\"amsterdam is a must visit in europe\" width=\"1600\" height=\"755\" data-pin-media=\"https://theplanetd.com/images/places-to-visit-in-europe-amsterdam.jpg\" data-pin-nopin=\"true\" /></figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Amsterdam: 860,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Amsterdam provides healthy food, outdoor entertainment, and other exciting perks. You can walk or cycle around their famous canal system.&nbsp;Whichever way you choose to explore it is an unforgettable experience.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Amsterdam&rsquo;s canal system runs right through the city stretching more than 100 kilometers. The canal is a vital transportation method in the city to this day. Whether you walk, bike, or ride in a boat, keep your eyes open for monuments. The canal system features over 1,000 significant historical monuments.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Amsterdam&nbsp;comes with a low crime index, making it one of the safer European Capitals. You&rsquo;ll find plenty of cyclists and pubs in the city. The city&rsquo;s charming day life and nightlife make it one of the most famous European capital cities.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Amsterdam&rsquo;s strategic location in Europe&rsquo;s rail system makes it a great place to base yourself when exploring Europe. You can quickly reach many other European Capitals.</p>\r\n<div class=\"gb-container gb-container-a77b9663\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Amsterdam, Netherlands</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-amsterdam-accommodation-neighborhoods/\">Where To Stay In Amsterdam &ndash; The Best Areas &amp; Neighbourhoods</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/anne-frank-house-museum-amsterdam/\">Anne Frank House, Amsterdam: A Moving Tribute to an Extraordinary Girl</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/amsterdam-with-kids/\">Amsterdam with Kids &ndash; 7 Attractions You and Your Child Will Love</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-4-berlin-germany\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"4_Berlin_Germany\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>4: Berlin, Germany</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-93490 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"ODk0OjgyMA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Berlin-Sights-5-605x403.jpg\" sizes=\"(max-width: 1280px) 100vw, 1280px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Berlin-Sights-5-605x403.jpg 1280w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Berlin-Sights-5-605x403-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Berlin-Sights-5-605x403-438x292.jpg 438w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Berlin-Sights-5-605x403-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Berlin-Sights-5-605x403-941x627.jpg 941w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Berlin-Sights-5-605x403-600x400.jpg 600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Berlin-Sights-5-605x403-272x182.jpg 272w\" alt=\"\" width=\"1280\" height=\"853\" data-pin-media=\"https://theplanetd.com/images/Berlin-Sights-5.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Berlin: 3,769,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This European capital has a bohemian vibe filled with traditional artists and street art.&nbsp;It is also one of the best European cities for nightlife. Berlin is an energy-packed city attracts travelers from&nbsp;all around the world to experience its history and culture.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">History buffs can find eerie remnants of World War II history. A small piece of the Berlin Wall still stands, keeping the lessons of the past in full display and the Holocaust Memorial&nbsp;is a moving memorial dedicated to the murdered Jews of Europe. Seeing these reminders creates some stark imagery. However, seeing today&rsquo;s city rise above its past will give you inspiration for a better future.&nbsp;A good tour to take to learn of Berlin&rsquo;s past is the free&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://freewalkingtour.com/tours/berlin-communist-socialist-tour/\" target=\"_blank\" rel=\"noreferrer noopener nofollow\">communist walking tour.</a></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When you are in Berlin, make sure you visit the Brandenburg Gate, it takes inspiration from the Acropolis entrance in Athens. The neoclassical architecture&nbsp;symbolizes Berlin&rsquo;s unity and remains the only surviving city gate.&nbsp;&nbsp;</p>\r\n<div class=\"gb-container gb-container-69df8c86\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Germany</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/places-to-visit-in-berlin-germany/\">The Best Places to Visit in Berlin, Germany</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/facts-about-germany/\">Top 16 Fun Facts About Germany That You Probably Didn&rsquo;t Know</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/romantic-road-germany/\">The Essential Guide to Germany&rsquo;s Romantic Road</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-5-athens-greece\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"5_Athens_Greece\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>5: Athens, Greece</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-165483 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTA0OjEwNjU=-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins.jpg\" sizes=\"(max-width: 1920px) 100vw, 1920px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins.jpg 1920w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins-389x292.jpg 389w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins-836x627.jpg 836w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins-1536x1152.jpg 1536w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins-853x640.jpg 853w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Things-to-do-in-Greece-Athens-Ruins-600x450.jpg 600w\" alt=\"best places to visit in athens acropolis\" width=\"1920\" height=\"1440\" data-pin-media=\"https://theplanetd.com/images/Things-to-do-in-Greece-Athens-Ruins.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Athens, Greece: 664,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You can find historical sites throughout Europe but as the oldest European capital, Athens really stands out from the rest.&nbsp;The famed city provides historical landmarks as far as the eye can see. The Acropolis remains a popular tourist destination thousands of years after it was originally built.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Odeon of Herodes Atticus showcases pieces of residential lifestyle from ancient times when outdoor theatrical performances were popular throughout the city.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You&rsquo;ll find many ancient theaters like the Theatre of Dionysus and the Odeon of Herodes Atticus just to mane a few as Athens features&nbsp;more theaters than any other country.&nbsp; After walking around in the hot heat, learn more about Athens history as well as seeing more artifacts at the Acropolis Museum. Built around an ancient Athenian neighborhood, this museum provides artifacts and a refuge from the heat.</p>\r\n<div class=\"gb-container gb-container-8268325e\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Athens, Greece</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-athens/\">Where To Stay In Athens &ndash; A Guide To The Best Neighborhoods</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-athens/\">24 Amazing Things to do in Athens, Greece</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/traditional-greek-foods/\">Delicious Greek Food &ndash; 29 Traditional Greek Dishes To Try Now</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-6-lisbon-portugal\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"6_Lisbon_Portugal\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>6: Lisbon, Portugal&nbsp;</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\"><img id=\"OTE0Ojc1Mw==-1\" class=\"wp-image-144213 lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/layover-in-lisbon-miradouro-da-senhora-do-monte.jpg\" sizes=\"(max-width: 500px) 100vw, 500px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/layover-in-lisbon-miradouro-da-senhora-do-monte.jpg 500w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/layover-in-lisbon-miradouro-da-senhora-do-monte-150x240.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/layover-in-lisbon-miradouro-da-senhora-do-monte-183x292.jpg 183w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/layover-in-lisbon-miradouro-da-senhora-do-monte-392x627.jpg 392w\" alt=\"\" width=\"500\" height=\"800\" data-pin-media=\"https://theplanetd.com/images/layover-in-lisbon-miradouro-da-senhora-do-monte.jpg\" data-pin-nopin=\"true\" /></figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Lisbon: 507,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you want to party, head over to Lisbon. Europe&rsquo;s party city provides plenty of choices. After the party, head over to the Alfama district. The Miradourso das Portas do Sol provides great views of the Tagus river and red roofs that fill the district.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">If you are into shopping make sure to check out the Thieve&rsquo;s Market. Despite its name, you won&rsquo;t find any thieves here but you will find a great selection of souvenirs.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Make sure to hop on Lisbon&rsquo;s famous Tram 28 to see more of the city. You&rsquo;ll find trams across the city like cable cars in San Francisco. The Belem neighborhood offers pretty ocean views and plenty of history. You can also visit Lisbon&rsquo;s Oceanarium. This gigantic aquarium houses more than one million gallons of seawater and over 8,000 creatures that call it home.</p>\r\n<div class=\"gb-container gb-container-1de10d91\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Portugal</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/layover-in-lisbon-portugal/\">How to Spend a Layover in Lisbon, Portugal</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/lisbon-for-under-5-euros/\">Lisbon Portugal for Under 5 Euros a Day</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/douro-valley-portugal/\">Guide to the Douro Valley from Porto &ndash; Portugal&rsquo;s Most Famous Wine Region</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-7-madrid-spain\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"7_Madrid_Spain\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>7: Madrid, Spain</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-70058 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTI0OjY0Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/madrid.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/madrid.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/madrid-150x103.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/madrid-740x507.jpg 740w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/madrid-768x526.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/madrid-1558x1067.jpg 1558w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/madrid-1536x1052.jpg 1536w\" alt=\"spain cities madrid\" width=\"1600\" height=\"1096\" data-pin-media=\"https://theplanetd.com/images/madrid.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Madrid: 3,335,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Beautiful parks, historical museums, and monumental architecture fill Madrid&rsquo;s&nbsp;streets. The Parque del Retiro attracts millions of visitors&nbsp;each year. The park&rsquo;s location at the center of Madrid makes it a popular and accessible destination. The Spanish Monarchy owned this park until the 19th century. &nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You can also feast your eyes on&nbsp;the majestic&nbsp;Royal Palace of Madrid. This beautiful building has over 2,000 gilded rooms. Pretty artwork lines the palace&rsquo;s walls and ceilings. If you want to see more of Spain,&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/places-to-visit-in-barcelona/\" target=\"_blank\" rel=\"noreferrer noopener\">head over to Barcelona</a>&nbsp;as well. A train from Madrid will get you there in under three hours.</p>\r\n<div class=\"gb-container gb-container-f2a9118b\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Spain</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/facts-about-spain/\">Discover These Fun Facts about Spain</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/free-things-seville-spain/\">15 Free Things to do in Seville, Spain</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/destinations/europe/spain-travel-tips/\">Spain Travel Guide</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-8-paris-france\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"8_Paris_France\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>8: Paris, France</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-174072 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTMzOjc3Ng==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-paris.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-paris.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-paris-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-paris-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-paris-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/places-to-visit-in-europe-paris-1536x1024.jpg 1536w\" alt=\"beautiful european places paris\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/places-to-visit-in-europe-paris.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Paris, France: 2,148,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The City of Love and Lights remains a popular European tourist destination. Famous attractions&nbsp;like the Eiffel Tower and Louvre Museum help to make this one of the most visited European Capitals.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Paris offers a vibrant culture and plenty of restaurants. You can get an aerial view of the city from the observation platform at the Eiffel Tower or Arc de Triumph. The Arc plays host to an annual ceremony for the 1919 armistice that ended World War I and is not to be missed.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Don&rsquo;t leave Paris without visiting the Champs-Elysees. This renowned street lets you see the Arc de Triumph and Place de la Concorde. It&rsquo;s one of the best&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/best-places-to-visit-in-europe/\" target=\"_blank\" rel=\"noreferrer noopener\">places to visit in Europe</a>.</p>\r\n<div class=\"gb-container gb-container-61201ab9\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Paris, France</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-paris-accommodation-neighborhoods/\">Where To Stay In Paris &ndash; Best Neighborhoods and Hotels Updated for 2021</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/visit-paris-on-a-budget/\">How to Visit Paris on a Budget &ndash; 20 Tips to Save Money</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/paris-itinerary-3-days/\">3 Days in Paris: The Best Paris Itinerary for Your First Visit</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-9-rome-italy\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"9_Rome_Italy\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>9: Rome, Italy</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-183346 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTQzOjczNw==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Rome-Colosseum.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Rome-Colosseum.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Rome-Colosseum-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Rome-Colosseum-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Rome-Colosseum-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Facts-About-Rome-Colosseum-1536x1024.jpg 1536w\" alt=\"The Colosseum of Rome Facts\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Facts-About-Rome-Colosseum.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Rome, Italy: 2,873,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/interesting-facts-about-rome-italy/\">Roman Empire&rsquo;s history</a>&nbsp;showcases itself to visitors&nbsp;in this fascinating city. The Roman Coliseum remains a must-see tourist attraction. Each time you visit this city, you will encounter a new piece of history. Walking through the Roman Forum is something that you will never forget. This Forum presided as the epicenter of Rome&rsquo;s activity.&nbsp;</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">While touring the city, make a stop at the Trevi Fountain. This masterpiece receives the most praise and recognition amongst Rome&rsquo;s fountains and for good reason. It is said that if you throw one coin into the fountain you&rsquo;ll return to The Eternal City, tossing two coins means you&rsquo;ll return and fall in love, and tossing three coins means you&rsquo;ll return, find love, and marry.</p>\r\n<div class=\"gb-container gb-container-6cc507bc\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Rome, Italy</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-rome/\">Where to stay in Rome &ndash; A Guide To The Best Neighbourhoods</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/rome-on-a-budget/\">How to Visit Rome on a Budget in 2022</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/3-days-in-rome-itinerary/\">3 Days in Rome &ndash; The Ultimate Rome Itinerary</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-10-oslo-norway\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"10_Oslo_Norway\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>10: Oslo, Norway</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\"><img id=\"OTUyOjc0Mw==-1\" class=\"wp-image-73332 lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Guided-Tour-Scandinavia-viking-ships-2-1.jpg\" sizes=\"(max-width: 533px) 100vw, 533px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Guided-Tour-Scandinavia-viking-ships-2-1.jpg 533w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Guided-Tour-Scandinavia-viking-ships-2-1-150x225.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Guided-Tour-Scandinavia-viking-ships-2-1-195x292.jpg 195w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Guided-Tour-Scandinavia-viking-ships-2-1-418x627.jpg 418w\" alt=\"guided tour of viking museum oslo\" width=\"533\" height=\"800\" data-pin-media=\"https://theplanetd.com/images/Guided-Tour-Scandinavia-viking-ships-2-1.jpg\" data-pin-nopin=\"true\" />\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">Oslo Viking Museum</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Oslo, Norway: 698,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Oslo doesn&rsquo;t generate as much buzz as other European capital cities on this list. However, the capital provides scenic waterfront views and parks.&nbsp;You can take a walk in the spacious Vigeland Park. It&rsquo;s filled with green spaces and marvelous sculptures. After Vigeland Park, Frogner Park makes for another great destination.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Viking Ship Museum provides historical memorabilia of the Viking days. The ships in the museum sailed the great seas more than 1,000 years ago.</p>\r\n<div class=\"gb-container gb-container-f48fb409\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Norway</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/destinations/scandinavia/norway-travel-tips/\">Norway Travel Guide</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/what-to-do-in-bergen-norway/\">14 Things do in Bergen, Norway</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/norway-in-a-nutshell/\">Norway in a Nutshell &ndash; Scenic Train Rides to Fantastic Fjords</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-11-warsaw-poland\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"11_Warsaw_Poland\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>11: Warsaw, Poland</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\"><img id=\"OTYxOjczMA==-1\" class=\"wp-image-68150 lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-poland-warsaw-mermaid.jpg\" sizes=\"(max-width: 533px) 100vw, 533px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-poland-warsaw-mermaid.jpg 533w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-poland-warsaw-mermaid-150x225.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-poland-warsaw-mermaid-195x292.jpg 195w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/things-to-do-in-poland-warsaw-mermaid-418x627.jpg 418w\" alt=\"warsaw mermaid things to see in Poland\" width=\"533\" height=\"800\" data-pin-media=\"https://theplanetd.com/images/things-to-do-in-poland-warsaw-mermaid.jpg\" data-pin-nopin=\"true\" />\r\n<figcaption style=\"box-sizing: inherit; margin-top: 0.5em; margin-bottom: 1em; font-size: 18px; display: table-caption; caption-side: bottom;\">The Warsaw Mermaid</figcaption>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Warsaw, Poland: 1,765,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">After its near destruction in World War II, the city has come back to life. This revival inspired people to nickname Warsaw &ldquo;The Phoenix City.&rdquo; You can learn about historical events such as the Warsaw Uprising.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Warsaw&rsquo;s Royal Way contains some of the city&rsquo;s most important sights.&nbsp;The Royal Castle, Presidential Palace, and&nbsp;Pilsudski square make this a walk you don&rsquo;t want to miss.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">The Royal Way takes 30 minutes to walk. Plan a few days for the Royal Way so you can soak in the sights.</p>\r\n<div class=\"gb-container gb-container-b046e6e4\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Poland</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/polish-foods/\">Traditional Polish Foods to Try in Poland and at Home</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-poland/\">Top 10 Things to Do in Poland</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/destinations/europe/poland-travel-tips/\">Poland Travel Guide</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-12-budapest-hungary\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"12_Budapest_Hungary\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>12: Budapest, Hungary</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-164011 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTcxOjk1NQ==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest.jpg\" sizes=\"(max-width: 1920px) 100vw, 1920px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest.jpg 1920w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest-389x292.jpg 389w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest-768x576.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest-836x627.jpg 836w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest-1536x1152.jpg 1536w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest-853x640.jpg 853w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest-150x113.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Szechenyi-Spa-Baths-Budapest-600x450.jpg 600w\" alt=\"\" width=\"1920\" height=\"1440\" data-pin-media=\"https://theplanetd.com/images/Szechenyi-Spa-Baths-Budapest.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Budapest, Hungary: 1,724,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You can soak in the Szechenyi Baths and visit numerous bars in Budapest. You&rsquo;ll find attractive bridges lined up across the city. They offer spectacular views of the Danube, Europe&rsquo;s second-longest river.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">There is so much to do in Budapest and some of the things you don&rsquo;t want to miss are Buda Castle, Fisherman&rsquo;s Bastion, and more.&nbsp;The Fisherman&rsquo;s&nbsp;Bastion served as lookout&nbsp;towers in the 19th century. Many people flock over to the Fisherman&rsquo;s Bastion for a view of the city. You&rsquo;ll also find a cafe on the terrace.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">When exploring Budapest, you&rsquo;ll want to visit each half of the city. Buda and Pest each form Budapest.&nbsp;Each part comes with a different vibe. By the end of your stay, you&rsquo;ll decide for yourself if Buda or Pest provides a better experience for you.</p>\r\n<div class=\"gb-container gb-container-b9ba9d69\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Budapest, Hungary</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-budapest/\">23 of the Best Things to do in Budapest (Hungary)</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/18-pictures-of-budapest/\">18 Pictures of Budapest That Will Make You Want to Pack Your Bags</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/tour-budapest/\">Budapest Communist Tour Through History</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-13-edinburgh-scotland\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"13_Edinburgh_Scotland\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>13: Edinburgh, Scotland&nbsp;</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-177700 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTgxOjgxNg==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-Visit-UK-Scotland-Edinburgh.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-Visit-UK-Scotland-Edinburgh.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-Visit-UK-Scotland-Edinburgh-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-Visit-UK-Scotland-Edinburgh-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-Visit-UK-Scotland-Edinburgh-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Places-to-Visit-UK-Scotland-Edinburgh-1536x1024.jpg 1536w\" alt=\"City of Edinburgh Scotland UK\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Places-to-Visit-UK-Scotland-Edinburgh.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Population of Edinburgh, Scotland: 488,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This relatively small Scottish capital comes with plenty of charm. It offers something for everyone. Whether you are into outdoor activities, history, or just taking the beauty of the architecture, you won&rsquo;t be disappointed with Edinburgh.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">You can hike up Arthur&rsquo;s Seat for a beautiful view overlooking the city. You won&rsquo;t find hordes of tourists on this mountain, making it an excellent place for privacy. Arthur&rsquo;s Seat originates from a long-gone volcano.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Edinburgh also hosts many festivals throughout the year and it is a good idea to plan your visit around one of them. You won&rsquo;t find as many people in this European capital&nbsp;compared to others but Edinburgh provides nightlife and excitement with a smaller crowd.</p>\r\n<div class=\"gb-container gb-container-aa763e2a\" style=\"box-sizing: inherit; margin-bottom: 30px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px;\">\r\n<div class=\"gb-inside-container\" style=\"box-sizing: inherit; padding: 40px; max-width: 1600px; margin-left: auto; margin-right: auto;\">\r\n<p id=\"h-\" style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px;\"><span style=\"box-sizing: inherit; font-weight: bold;\">Discover more about Edinburgh, Scotland</span></p>\r\n<ul style=\"box-sizing: inherit; margin: 0px 0px 1.5em 3em; padding: 0px; border: 0px; list-style-position: initial; list-style-image: initial;\">\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/where-to-stay-in-edinburgh-best-places/\">Where to Stay in Edinburgh &ndash; A Guide To The Best Places and Neighborhoods</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/things-to-do-in-edinburgh-scotland/\">21 Best Things To Do in Edinburgh, Scotland</a></li>\r\n<li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px;\"><a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/edinburgh-itinerary-3-days/\">3 Days in Edinburgh &ndash; The Complete Edinburgh Itinerary</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<h2 id=\"h-14-florence-italy\" style=\"box-sizing: inherit; margin: 0px 0px 20px; padding: 0px; border: 0px; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 38px; line-height: 1.4em; text-transform: capitalize; color: #3c4053; background-color: #fafafa;\"><span id=\"14_Florence_Italy\" class=\"ez-toc-section\" style=\"box-sizing: inherit;\"></span>14: Florence, Italy</h2>\r\n<div class=\"wp-block-image\" style=\"box-sizing: inherit; margin-bottom: 1.2em; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">\r\n<figure class=\"aligncenter size-full\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; clear: both; max-width: 100%; height: auto;\">\r\n<div class=\"dpsp-pin-it-wrapper wp-image-186987 lazyloaded\" style=\"box-sizing: inherit; display: inline-table; line-height: 0; position: relative !important;\"><img id=\"OTkxOjc4NA==-1\" class=\" lazyloaded\" style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Italy-Florence-sunrise.jpg\" sizes=\"(max-width: 1600px) 100vw, 1600px\" srcset=\"https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Italy-Florence-sunrise.jpg 1600w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Italy-Florence-sunrise-760x507.jpg 760w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Italy-Florence-sunrise-150x100.jpg 150w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Italy-Florence-sunrise-768x512.jpg 768w, https://cdn-bmalj.nitrocdn.com/uirOOtSrYrqqUksKHkiSCjZGZlPeXsmk/assets/static/optimized/rev-0404f4f/images/Cities-in-Italy-Florence-sunrise-1536x1024.jpg 1536w\" alt=\"Piazza del Duomo in Venice Italy\" width=\"1600\" height=\"1067\" data-pin-media=\"https://theplanetd.com/images/Cities-in-Italy-Florence-sunrise.jpg\" data-pin-description=\"14 Best European Capital Cities To Visit in 2022\" data-pin-title=\"14 Best European Capital Cities To Visit in 2022\" /></div>\r\n</figure>\r\n</div>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The population of Florence: 380,000</span></p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">This historic city, which is the capital of the&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://theplanetd.com/visit-tuscany/\">Tuscan region of Italy</a>, attracts history buffs and couples alike. It offers a glimpse of history through&nbsp;<a style=\"box-sizing: inherit; transition: color 0.1s ease-in-out 0s, background-color 0.1s ease-in-out 0s; text-decoration-line: none; color: #046dad; font-weight: 600;\" href=\"https://www.britannica.com/art/Renaissance-art\" target=\"_blank\" rel=\"noreferrer noopener\">Renaissance art</a>&nbsp;and architecture as well as being one of the most romantic cities in the world.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">Florence was home to many famous artists over the years, and because of this is the place to visit if you are an art lover. As Renaissance&rsquo;s focal point, Florence provides today&rsquo;s visitors with some of the finest art in Europe. Here you will find Michelangelo&rsquo;s David, Botticelli&rsquo;s Birth of Venus, and Giorgio Vasari&rsquo;s Last Judgement, just to name a few.</p>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.2em; padding: 0px; border: 0px; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 21px; background-color: #fafafa;\">After viewing the artwork, you can take some time to experience famous sights sites like the Galleria dell-Academia and Duomo Cathedral. You&rsquo;ll also walk in the same city that has played host to several movies, including &ldquo;Inferno.&rdquo;</p>', 12, 11, 0, 'published', '2022-03-13 08:14:55', '2022-03-13 08:14:55');

